{border:0px solid #000 !important;border-radius:50px !important;box-shadow:0px 0px 10px #000 !important}/* Place,forme des images */.image{display:block !important;margin-left:auto !important;margin-right:auto !important;border-radius:20px !important}.photoset img{border-radius:10px !important}/* RETURN TO TOP */div.return_to_top_icon{background:none !important;width:0px !important;height:0px !important;margin-top:0px !important;border-bottom:solid 30px #fff !important;border-right:15px solid transparent !important;border-left:15px solid transparent !important}/*.selection.nipple - dashboard,user,inbox - "white pages"*//*.selection_nipple.white - create new blog,help,preferences*//*SELECTION ARROW begin - points to dashboard selection*/.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){ background:none !important; width:0px !important; height:0px !important; margin-top:0px !important; border-bottom:solid 4px #fff !important; border-right:11px solid transparent !important; border-left:11px solid transparent !important}.selection_nipple:not([class="selection_nipple white"]){ border-bottom:solid 11px #fff !important}/*SIDE ARROWS*/.arrow{background:none !important;width:0px !important;height:0px !important;border-right:solid 9px #fff !important;border-bottom:9px solid transparent !important;border-left:2px solid transparent !important;border-top:9px solid transparent !important;margin-top:0px !important}.nipple{background:none !important;width:0px !important;height:0px !important;border-right:solid 6px #fff !important;border-bottom:6px solid transparent !important;border-left:0px solid transparent !important;border-top:6px solid transparent !important;margin-top:0px !important}.nipple.border{background:none !important;width:0px !important;height:0px !important;border-right:solid 6px #fff !important;border-bottom:6px solid transparent !important;border-left:1px solid transparent !important;border-top:6px solid transparent !important;margin-top:1px !important}/*tags*/.posts,.tag{background-color:#e6cdb0!important;text-transform:none!important}.tags{font-size:10px!important;margin-top:0px!important;font-family:'trebuchet ms';vertical-align:middle !important;text-transform:uppercase !important}.source_url{font-size:10px!important;font-family:'trebuchet ms'!important;text-transform:uppercase;vertical-align:middle !important;margin-right:4px!important}.footer_links{margin-top:20px!important;vertical-align:middle !important}.post_info A,.post_controls A,.footer_links.with_tags SPAN A{color:#333 !important}.token{border:0px solid #444 !important;background-color:#e6cdb0!important}/*text post message body background*/.mceContentBody{ background:white !important; color:#727272 !important; /*font-family:georgia !important;*/ font-size:12px !important}/*top content bar - rounded bar with design similar to buttons*/#content_top{ width:0px !important; height:0px !important;/*uncomment the lines below if you wish to replace the top content image with your own*//*  background:url(http://assets.tumblr.com/images/content_top_edit_form.png) !important; /*replace with your top content bar image url*//*  width:900px !important;/*replace with your top content bar image width*//*  height:25px !important;/*replace with your top content bar image height*/}/*bottom content bar - rounded bar with design similar to buttons*/#content_bottom{ width:0px !important; height:0px !important;/*uncomment the lines below if you wish to replace the bottom content image with your own*//*  background:url(http://assets.tumblr.com/images/content_top_edit_form.png) !important; /*replace with your bottom bar content image url*//*  width:900px !important;/*replace with your bottom content bar image width*//*  height:25px !important;/*replace with your bottom content bar image height*/}/*large rectangular area containing text message,controls and form fields = content container frame*/#left_column, #right_column {background-color: transparent !important;}

