css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
div#watch-player.flash-player,
div#watch-player.html5-player,
div#watch-video {
    width: /*854px*/ /*970px*/ 1280px !important;
    height: /*510px*/ /*580px*/ 765px !important;
}
div#watch-sidebar {
    margin-top: 10px !important;
}
div#watch-video-container {
    background-color: #000 !important;
}
.watch-headline, #watch-headline, #watch-video, #watch-main {
    margin: 0 auto;
    width: 1280px !important;
}

#watch-panel {
    float: left;
    padding-top: 10px;
    width:940px !important;
    z-index: 0;
}
#watch-actions {
    margin-bottom: 5px;
    overflow: hidden;
    width: 940px!important;
}






#watch-description-clip {
    float: left;
    position: relative;
    width: 650px!important;
    z-index: 1;
}


.comment-list .comment .content {
    width: 940px !important;
}

#masthead, #masthead-subnav {
    width: 1280px !important;
}




#watch-description-extra-info {
    margin-left: 785px !important;
    min-height: 55px;
    padding-bottom: 0;
    position: relative;
    width: 165px;
    z-index: 1;
}

}
