css:
tree {
    font-size: 13px;
}
/* Global UI font */
* { font-size: 11pt !important;

  font-family: Verdana !important; 
} 


#folderTree treechildren {
  background-color: #379bff !important;
  font-size: 13px;
  padding-top: 10px;
}


#folderTree treechildren::-moz-tree-row(odd) {
  background-color: #379bff !important;
}

#folderTree treechildren::-moz-tree-row(even) {
  background-color: #379bff !important;
}


#folderview-cycler-next {
  list-style-image: url(chrome://messenger/skin/icons/arrow/arrow-right.png) !important;
}

#folderview-cycler-prev {
  list-style-image: url(chrome://messenger/skin/icons/arrow/arrow-left.png) !important;
}

  
 


 
 

#folderview-cycler-next {
  list-style-image: none !important;
}
#folderview-cycler-prev {
  list-style-image: none !important;
}
treechildren:-moz-tree-cell-text(unread) {
font-weight: bold !important;
 
}
 

#folderPaneHeader,
#folderTree treecol,
#folderTree
  {background-color: #ffffff !important;}

#acctCentralGrid
  {background-color: #ffffff !important;}

 

#msgHeaderView,
#attachmentList
  {background-color: #eeeeee !important;}


 
toolbox {
background-color: #eef3fa !important;
}

 toolbarbutton {
    background-color : #ffffff!important;
 
}



/* change font color of popup menus */
menupopup > menu,
menupopup > menuitem,
popup > menu,
popup > menuitem {
  font-weight: normal !important;
  color: #000000 !important;
  background-color: #ffffff !important;
}

 

 /* hide toolbar borders */
#mail-toolbar-menubar2,
#mail-bar3,
#compose-toolbar-menubar2,
#composeToolbar2,
#MsgHeadersToolbar,
#addrbook-toolbar-menubar2,
#ab-bar2,
#FormatToolbar {
  -moz-appearance: none !important;
  border-style: none !important;
  border-width: 0!important;
  background-color: #ffffff !important;
}

 
 

 .tabmail-tab { 

margin: 0px 0px !important; 
} 

.tab-background { 

border: 1px solid threedshadow !important; 
 border-top:none !important;
 border-bottom:none !important;
border-left: none !important;
 
border-top-left-radius: 0px !important; 
border-top-right-radius: 0px !important; 
-moz-margin-start: 5px !important; 
-moz-margin-end: 5px !important; 
background-color: white !important; 
background-image: -moz-linear-gradient(rgba(255,255,255,0) 100%, rgba(255,255,255,0)) !important; 
} 

.tabmail-tab:not([selected]):not(:hover) + 
.tabmail-tab:not([selected]):not(:hover)::before, 
.tabmail-tab:last-child:not([selected]):not(:hover)::after { 
background: white !important; 
} 



.tabmail-tab:last-child:not([selected]):not(:hover)::after { 
-moz-margin-start: 0px; 
-moz-margin-end: 0px; 
} 

  
tab[selected="true"]
{
 
color: black !important;
font-weight: bold !important; 
}

.tabbrowser-tabs {
  -moz-appearance: none !important;
    background-color : #FFFFFF !important;
}

toolbox,
.tabmail-tabs{ 
  -moz-appearance: none !important;
height: 40px !important;
  background:-moz-linear-gradient(top, #ffffff 0%, #ffffff 100%) !important;
}
 
toolbox
{
border: 1px solid threedshadow !important; 
}
#threadTree treecol, #threadTree .treecol-image {
 -moz-appearance: none !important;
background-color: #379bff !important;
color: white !important; 
  border-style: none !important;
  border-width: 0!important;
  height: 38px !important;

}

#searchInput {
  border-top: none !important; 
  border-bottom: none !important;
  height: 20px !important;
  width: 160px !important;
}
 treechildren::-moz-tree-cell(unread) {
    background-color: #eeeeee !important;
}
 

treechildren::-moz-tree-image(folderNameCol){
  list-style-image: none !important;
}
 
 
treechildren::-moz-tree-cell(selected) {
  background-color: white !important;
}
