css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.htcmania.com/showthread.php") {
#vbpostrow_ > td, #threadslist, 
#threadslist > tbody, #threadslist > tbody > tr , 
#threadslist td, div.page, td.alt1 {
    background-color: black !important;
    color: #E7E9E9 !important;
    
}

* {
    font-size: 1em !important;
}

td.alt2{
    background-color: #333 !important;
    color: #aaa !important;
    
}

.thead {
    background-color: #444 !important;
    background-image: none !important;
    color: white !important;
    
}

a {
    color: #09f !important;
}

#qrform {
    display: none !important;
}

div.quote div.smallfont {
    display: block !important;
}


td[nowrap] div.smallfont {
    display: none !important;
}

form[action="portal.php"] {
    display:none !important;
}

*[title^="Avatar"] {
    width: auto !important;
    height: 2em !important;
    display: inline !important;
}



.bigusername {
    color: #E7E9E9 !important;
    font-size: 1.6em !important;
    font-weight: 900 !important;
}

.bigusername + img[alt~="l√≠nea"] {
    width: 1em !important;
}

body > table {
    display: none !important;
}



#breadcrumbNavbar > tbody > tr > td:first-child {
    display: table-cell !important;
}

#breadcrumbNavbar > tbody > tr > td {
    display: none !important;
}

div.page > table.tborder:nth-child(3) {
    display: none !important;
}

div.page > div > table.tborder:nth-child(6), div.page > div > table.tborder:nth-child(1) {
    display: none !important;
}

table.tborder:nth-child(444444) {
    display: none !important;
}

body {
    font-size: 1em !important;
}

font[size="1"] {
    font-size: 1.1em !important;
}
font[size="2"] {
    font-size: 1.2em !important;
}
font[size="3"] {
    font-size: 1.3em !important;
}
font[size="4"] {
    font-size: 1.4em !important;
}
font[size="5"] {
    font-size: 1.5em !important;
}
font[size="6"] {
    font-size: 1.6em !important;
}
font[size="7"] {
    font-size: 1.7em !important;
}

.smallfont {
    font-size: 0.9em !important;
}

#posts + div > div.page > div > table.tborder {
    display: none !important;
}

.alt1, alt1Active, alt2, alt2Active, td, th, p, li , tr {
    font: 1em Tahoma !important;
    font-size: 1em !important;
}

blockquote.bq {
    font-style: oblique !important;
    font-family: Tahoma !important;
    font-size: 1em !important;
}

div[id^="post_message_"] + div:not([align]) {
    display: none !important;
}

#posts + div > div > div > table ~ * {
    display: none !important;
}

table, tr, td, tbody {
    display: inherit !important;
}

table[cellspacing="6"], table[cellspacing="6"] tr, table[cellspacing="6"] td, table[cellspacing="6"] tbody  {
    display: inline !important;
}

table[cellspacing="6"] div[id^="postmenu"]   {
    display: inline!important;
}

div[id^="post_thanks_box_"] {
    text-align: right !important;
}



.pagenav table, .pagenav tr, .pagenav td, .pagenav tbody {
    display: inline !important;
}

#vbpostrow_ {
    padding-bottom: 1em;
    padding-top: 1em;
    border: 1px solid gray !important;
    display: block !important;
}
#vbpostrow_ td {
    display: inline !important;
}

#vbpostrow_ div {
    display: inline-block !important;
}



}
