css:
@-moz-document domain('mweor.com'){ 

body { 
	background: #333 !important;
	color: #666 !important; 
}

#bodyi {
	background: #333 repeat-x !important;
}

#topbanneri {
        display: none !important;
}

#layoutuinfo
{
	color: #666 !important;
}

.menu_shadow
{
    background: #555 !important;
}

#menu_holder div.menu_dropdown
{
    background: #555 !important;
    border-radius: 5px !important;
    border-top-left-radius: 0px !important;
}

#menu_holder div.menu_dropdown div.menu_dropdown_inner
{
    background: #888 !important;
}

#menu_holder div.menu_dropdown div.menu_dropdown_divider
{
    border-right: 1px dotted #ff8400 !important;
}

#menu_holder div.menu_dropdown div.menu_subtitle
{
    color: #ccc !important;
}

#menu_holder div.menu_dropdown a
{
    color: #bbb !important;
}

#menu_holder div.menu_dropdown a:hover
{
    color: #ddd !important;
}

#gametime
{
    color: #ff8400 !important;
}

#content {
	background: #444 repeat-y !important;
	color: #888 !important;
        border-radius: 5px !important;
}

#bottombanner {
        display: none !important;
}

#footer
{
	color: #666 !important;
}

#footer a {
	color: #aaa !important;
}

#footer a:hover {
        color: #ffffff;
}

a {
	color: #aaa !important;
}


a:hover
{
	color: #ff8400 !important;
}

input[type="text"], input[type="password"], select {
	color: #555 !important;
	background: #ddd !important;
	border: 1px solid #ff8400 !important;
        border-radius: 3px !important;
}

input, select
{
	color: #555 !important;
	background: #ddd !important;
	border: 1px solid #ff8400 !important;
        border-radius: 3px !important;
}

.button
{
	color: #888 !important;
	background: #444 !important;
	border: 1px solid gray !important;
}

textarea
{
	color: #555 !important;
	background: #ddd !important;
	border: 1px solid #ff8400 !important;
        border-radius: 3px !important;
}


.formholder
{
    color: #555 !important;
    background: #444 !important;
    border: 1px solid gray !important;
    border-radius: 5px !important;
}

/*.formholder a, #eventtxt a
{
	color: #ccc !important;
}

.formholder a:hover, #eventtxt a:hover
{
	color: #ff8400 !important;
}*/

#eventtxt
{
	background: #555 !important;
}

.tborder
{
	border: 1px solid #555 !important;
        border-radius: 5px !important;
}

.theader
{
	background: #222 !important;
        border-radius: 5px !important;
	color: #ff8400 !important;
}

.trow1 table, .trow1 tr, .trow1 td
{
	background: #343434 !important;
	color: #555 !important;
}
.trow1 {
        background: #222 !important;
        color: #555 !important;
}

.trow2, .trow2 table, .trow2 tr, .trow2 td
{
	background: #3d3d3d !important;
	color: #666 !important;
}

.trow3, .trow3 table, .trow3 tr, .trow3 td
{
	background: #333 !important;
	color: #777 !important;
}

#tooltip
{
	background: #aaa !important;
	color: #444 !important;
	border: 3px solid #666 !important;
        border-radius: 5px !important;
}

.popup
{
	background: #aaa !important;
	color: #444 !important;
	border: 3px solid #ff8400 !important;
}

.popup2
{
	background: #aaa !important;
	color: #444 !important;
	border: 3px solid #ff8400 !important;
        border-radius: 5px !important;
}

.popupi
{
	width: 380px;
	position: relative;
	background: #e8f0ea;
	border: 1px solid #6d9c77;
	margin-top: 5px;
	padding: 2px;
	z-index: 7;
}

#popupclose, #bookmarkclose
{
	background: #ff8400 !important;
	color: #bbb !important;
}

#titletxt
{
	color: gray !important;
}
#titleimg {
border-bottom: 2px dotted gray !important;
}

#cattery_infobox
{
	background: #343434 !important;
        border-radius: 5px !important;
}

#cattery_descbox
{
	background: #444 !important;
}

#notice
{
	border: 1px solid #444 !important;
        border-radius: 5px !important;
	background: #555 !important;
	color: #888 !important;
}

.mweorbox
{
	border-radius: 5px !important;
        background-color: #343434 !important;
}

#cpicker
{
	border: 1px solid gray !important;
	background: #555 !important;
}

.commentbox
{
	background: #666 !important;
	border: 1px solid #aaa !important;
        border-radius: 5px !important;
}

.tabs-nav a {
    color: #aaa !important;   
}
.tabs-nav .tabs-selected a {
    color: #555 !important;
}
.tabs-container {
    border: 1px solid #333 !important;
    background: #333 !important; /* declare background color for container to avoid distorted fonts in IE while fading */
}

.itemholder {
	background: #333 !important;
        border-radius: 5px !important;
}



td.cell_rank {
        background-color: #2b2b2b !important;
}

ul.report_menu a {border: none !important}
ul.report_menu .current {background: none !important}
ul.report_menu .current a {color: #ccc !important}

.tabs-nav a, .tabs-nav .tabs-disabled a:hover, .tabs-nav .tabs-disabled a:focus, .tabs-nav .tabs-disabled a:active, .tabs-nav .tabs-selected a:link, .tabs-nav .tabs-selected a:visited, .tabs-nav .tabs-disabled a:link, .tabs-nav .tabs-disabled a:visited, .tabs-nav a, .tabs-nav a span {background-image: url(http://i44.tinypic.com/mwxlon.png) !important}



/*change people's fonts and texts*/
td.trow3 blockquote, td.trow3 center, tr.trow3 center {
         font-size: 11px !important;
         font-family: "Arial" !important;
         color: #777 !important;
}

.trow3 center, .trow3 font, .trow3 small {
         color: #777 !important;
         font-family: "Arial" !important;
         font-size: 11px !important;
}
}
