css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("foto-devushek.com") {
    DIV.search_area, UL.wp_thumbie_ul_list,DIV#header, DIV.banner1,DIV.sidebar,DIV.left_sidebar{
        display:none !important;
    }
    .post, .metki, .postarea {
        width: 1220px !important;
    }
    img {
        width:100% !important;
        height:100% !important;
    }
    body {
        background-color:black !important;
    }
}

@-moz-document domain("re-actor.net") {
    td[width="350"],TD.menu ,SPAN.news-views, TD.copyright{
        display:none !important;
    }
    td[width="640"]{
        width:1220px !important;
    }
    img {
        max-width: 1220px !important;
    }
}

@-moz-document regexp('http:\\/\\/re-actor\\.net\\/girls\\/.*\\.html') {
    img {
        /*width:120% !important;
        height:120% !important;*/
        max-width: 1024px !important;
    }
}

@-moz-document regexp('http:\\/\\/re-actor\\.net\\/girls\\/(page\\/\\d+\\/)?') {
    img {
        max-width: 640px !important;
    }
}

@-moz-document domain("swteam.info") {
    DIV#sidebar,div#header,ul#qm0,DIV.box {
        display:none !important;
    }
}

@-moz-document domain("qip.ru") {
    .in-layout {
        margin: 0px 0px 0px 0px !important;
    }
    img {
        width: 100% !important;
        height: 100% !important;
    }
    .wrap2 {
        width: 100%  !important;
    }
    .vsocial, DIV.comments, div[class^="pip"],div.left,DIV.q_teaser {
        display:none !important;
    }
    #dle-content img {
        max-width: 1220px;
    }
    DIV.useract img {
        width: 100% !important;
        max-width: 1200px !important;
    }
}

@-moz-document domain("xyya.net") {
    NOINDEX,td[width="220"],td[style="width:220px;"], DIV[id^="smile_teaser"], form{
        display:none !important;
    }
    img {
        max-width: 1024px !important;
    }
    DIV#dle-content>center {
        display:none !important;
    }
}

@-moz-document regexp("http://xyya\.net\/ero\/(page\/\d+\/)?") {
    img {
        max-width: 640px !important;
    }
}
