css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("redandwhitekop.com") {
    body {
    	font-size: 80% !important;
    	color: black !important;
    }

    img.avatar {
        display: none !important;
    }

    table.table_list td {
        border: none !important;
    }
    
    a.unreadlink {
        background-color: #A0A0A0 !important;
        color: rgb(255, 255, 255) !important;
        box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.3) !important;
        text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.4) !important;
    }

    a.unreadlink:hover {
        background: url(http://www.redandwhitekop.com/forum/Themes/rawkv3/images/rawk/nav-sec-bg.png) repeat-x top left !important;
        color: rgb(255, 255, 255) !important;
    }

	.windowbg, #preview_body, .windowbg2, .windowbg3 {
		background-color: rgb(225, 225, 225) !important;
		border-color: rgb(225, 225, 225) !important;
	}
	.stickybg, .stickybg2 {
	   background-color: rgb(232, 232, 232) !important;
	}
	.lockedbg, .lockedbg2 {
	   background-color: rgb(225, 225, 225) !important;
	}
	
    .signature, .signature blockquote.bbc_standard_quote, .signature .quoteheader {
        color: rgb(60, 60, 60) !important;
        border: 0px !important;

    }
    .quoteheader, .bbc_standard_quote {
        background-color: rgb(215, 215, 215) !important;
        margin-left: 0 !important;
    }
    
    
    .roundframe {
       background-color: rgb(225, 225, 225) !important;
    }
    
    #messageindex table.table_grid {
        background-color: rgb(225, 225, 225) !important;
    }
            
    
    #content_section .frame {
        background-color: rgb(215, 215, 215) !important;
        border-color: rgb(215, 215, 215) !important;
    }
    
    #display_jump_to {
        background-color: rgb(215, 215, 215) !important;
        border: 0 !important;
    }
    
    #topic_icons .description {
        background-color: rgb(225, 225, 225) !important;
    }    
    .catbg {
        background-color: rgb(225, 225, 225) !important;
    }
    .cat_bar {
        background-color: rgb(225, 225, 225) !important;
    }
    .poster h4 img {
        float: left !important;
    }
    .poster h4 > img:first-child {
        margin-left: -17px !important;
    }
    .post-container {
        background-color: rgb(215, 215, 215) !important;
    }
    .inner {
        border-color: rgb(215, 215, 215) !important;
    }
    .last {
        border-color: rgb(215, 215, 215) !important;
    }
    .clearfix > h2, .home-twitter.home > h2 {
        background: none !important;
        background-color: rgb(215, 215, 215) !important;
    }
    .featured.clearfix {
        background: none !important;
    }
    #main_menu {
        border: none !important;
    }
    #footer_section .frame {
        border: none !important;
    }
    
    #content_section {
        border: none !important;
    }

	div#wrapper {
        background-color: rgb(215, 215, 215) !important;
	}
	
	#messageindex table.table_grid td {
        border-top: 1px solid rgb(205, 205, 205) !important;
        padding: 8px 3px !important;
    }
}
