css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("teamswift.net") {

html, body ,

#detailsTabs > li > a

{

  margin: 0 !important;

  padding: 0 !important;

  border: none !important;

 /* pb */

  background: #000 !important;

}



/* pb */

a:link

 {

  color: #EAD6AC !important;

 }

a:visited, a:hover

 {

  color: #996633 !important;

 }

#detailsTabs > li > ul,

#detailsTabs > li > a > span,

#torrent_details, 

.ihSearch,

button, input, select, textarea 

 {

  background: #222 !important;

  border: #666 1px solid !important;

 }

.bodyline,

.highlighttext,

.pager > tbody > tr > td

 {

  background: #111 !important;

 }

a:link,

a:visited, a:hover,

.bodyline,

.pager > tbody > tr > td

 {

  border: none !important;

 }

input[type="text"],

input[type="password"],

input:not([type])

 {

  padding: .25em !important;

 }

button,

font,

form,

input,

p,

select,

textarea,

th,

tr,

td,

.bodyline,

.code,

.forumline,

.genmed,

.ihHead,

.name,

.nav,

.pager > tbody > tr > td,

.postbody,

.postdetails,

.postdetails > *,

.quote,

.releases,

.row1

 {

  color: #bbb !important;

 }

.bodyline > tbody > tr > td > table:first-child > tbody > tr > td

 {

  background:

    #333

    url(data:image/png;base64,)

    !important;

  border-bottom: 2px #333 ridge !important;

  max-height: 100px !important;

 }

#detailsTabs > li,

#nav > li,

#nav > li > ul,

.commentsBody,

.commentsDetails,

.commentsFooter,

.hlRow,

.releases

 {

  border-color: #000 !important;

 }

#nav > li > ul,

.comments,

.tabs > a:hover

 {

  border: 1px solid #000 !important;

 }

.tabs > a

 {

  border: 1px solid transparent !important;

 }

#nav > li > ul > li > a:hover

 {

 /* This stops them from flickering. */

  padding-bottom: .75em !important;

  padding-top: .75em !important;

 }

.forumline,

.forumline > tbody > tr:not(:first-child) > td,

.code,

.forumline[id="serps"] > tbody > tr > td > table > tbody > tr > td,

.quote

 {

  border-color: #000 !important;

 }

.cat,

.code,

.forumline > tbody > tr > th,

.forumline > tbody > tr:first-child > td,

.ihHead,

.quote

 {

  background:

    #393939

    url(data:image/gif;base64,R0lGODlhBgAGAIABAAAAAP///yH5BAEAAAEALAAAAAAGAAYAAAIJDGKpeNn3EGMFADs=)

    !important;

 }

.forumline > tbody > tr > .ihHead,

.forumline > tbody > tr > th

 {

  border-bottom: 2px #666 ridge !important;

 }

.cat

 {

  border-top: 2px #666 ridge !important;

 }

.releases,

.row1,

.spacerowcomment

 {

  background: #222 !important;

 }

#nav > li > ul,

.commentsBody,

.hlRow,

.row2,

.tabs > a:hover

 {

  background: #444 !important;

 }

.forumline[id="serps"] > tbody > tr > td,

.row3,

.spacerow

 {

  background: #111 !important;

 }

.tabs

 {

  margin-bottom: 5px !important;

 }



/* hide ads and other junk */

.navbkg,

.SL_zone,

.SERP_zone,

.Listing,

.gensmall,

a.finePrint,

.adHeadline,

.topBar#textAd,

a[href*="banner"],

img[src*="banner"],

img[src*="ftjcfx.com"],

[width="728"][height="90"],

img[src*="cpxinteractive"],

img[width="468"][height="60"],

a[href*="cpxinteractive.com"],

iframe:not([src*="torrent_details"]),

#ihOps,#ihRel,#ihAge,#ihSeeds,#ihLeechers,

div[style="float: right; text-align: right;"],

tr[valign="top"][align="center"]>td[class="row4"],

div[style="border: 1px solid orange; padding: 5px;"],

span[style="font-size: 9px; color: rgb(180, 180, 180);"],

td[align="left"][style="font-size: 12px; color: rgb(240, 10, 0); text-align: left;"],

table[width="90%"][cellspacing="1"][cellpadding="6"][class="forumline"],

table[border="0"][cellpadding="0"][cellspacing="0"][width="728"],

.commentsBody > hr,

 /* pb */

.ihLogo > table > tbody > tr > .ihLogo

{

  display: none !important;

}



/* too many damn br tags! */

 /* pb */

:not(.genmed) > br + br,

div + br,

table + br,

 /* pb */

td:not(.genmed) > br:first-child

{

  display: none !important;

}



/* add some spacing between tables (since I removed some br tags) */

.forumline {

  margin-bottom: 1em !important;

}



/* only links should have underlines! */

u { text-decoration: none !important; }



/* compact list of torrents a bit */

td.row3 { padding: .2em !important; }



/* useless text inside torrent details iframe */

a[href^="/download/"] + span[style="font-size: 9px; color: rgb(88, 88, 88);"],

a[href^="/download/"] + span[style="font-size: 9px; color: rgb(88, 88, 88);"] + br + a + p

{

  display: none !important;

}



/* move RSS/upload link to right side */

div[style="float: left; text-align: left;"] {

  float: right !important;

}



/* fix tooltip position */

.dirs { left: 0 !important; top: 0 !important; }



}





/* Torrent preview toggles.

 */

img[src$="/serp-toggle-down.gif"]

 {

  background:

    url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABUAAAAUCAYAAABiS3YzAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABlSURBVHja3NTBDoAwCAPQtfH/f7mKybyp3cayZD0Bh8eFAEklOywTsieqbFQAbJguGFfiwnTBuzFhuuAzNGC2gC7MVtCB2QP+wewFv2COgG8wrkE97JTEgqMWmQkU232ptegpwADmuU0V4KAVjgAAAABJRU5ErkJggg==)

    no-repeat

    !important;

  padding: 21px 21px 0 0 !important;

  height: 0 !important;

  width: 0 !important;

 }



img[src$="/serp-toggle-up.gif"]

 {

  background:

    url(data:image/png;base64,)

    no-repeat

    !important;

  padding: 21px 21px 0 0 !important;

  height: 0 !important;

  width: 0 !important;

 }

#serps

 {

  margin-left: 25px !important;

 }





/* Torrent preview height, when toggled open.

 */

tr[id^="ihtr"]:not([style*="display: none;"]),

tr[id^="ihtr"]:not([style*="display: none;"]) > td > table,

tr[id^="ihtr"]:not([style*="display: none;"]) > td > table iframe

 {

   height: 500px !important;

 }
