@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("veekun.com") {
body {
 background: #000 !important;
}
#title a {
 color: #CDBA98 !important;
}
#header {
 background: #593420 !important;
 color: #EFCDCD !important;
 border-color: #CDBA98 !important;
}
#header a {
 color: #AC8A5C !important;
}
#menu > ul > li > a {
 background: #CDBFB0 !important;
 color: #9C3F1A !important;
}
#menu > ul > li > a:visited { color: #591C0A !important; }
#menu > ul > li > a:hover {
 background: #1F0C00 !important;
 color: #FF291A !important;
}
#menu > ul > li > ul {
 background: #4C100C !important;
 border-color: #9A352A !important;
 color: #ECDADA !important;
}
#menu > ul > li > ul > li a:hover 
{
 color: #FCEAEA !important;
}
#menu > ul > li > ul > li a:link:hover,
#menu > ul > li > ul > li a:visited:hover
{
 background-color: #381504 !important;
}
#body {
 background: #120600 !important;
 border-color: #CDBA98 !important;
 color: #CDBA98 !important;
}
a:link { color: #FF2310 !important; }
a:visited { color: #BF0300 !important; }
a:hover { }
a:active { }
h1 {
 background: #251C0F !important;
 color: #CD9582 !important;
}
h2,h3 {
 color: #CD9582 !important;
 border-color: #CCC !important;
}
dt,th {
 color: #F06451 !important;
}
.disabled a:link { color: #CCC !important; }
.dex-box-link:hover
{
 background-color: #480F0C !important;
 border-color: #95302A !important;
}
code { background: #111 !important; color: #E22 !important; }
.faded { color: #FFF !important; opacity: .5 !important; }
input,select,button
{
 -moz-appearance: none !important;
}
input[type='text'],input[type='checkbox'],
input[type='radio'],select
{
 background: #36150E !important;
 border-color: #9F2412 !important;
 color: #FC3510 !important;
}
input[type='submit'],button
{
 background: #32100E !important;
 border-color: #9A2F1C !important;
 color: #CF301E !important;
 border-radius: 6px;
}

/**** FRONTPAGE ****/
.frontpage-update .header
{
 background: #321F0C !important;
 border-color: #5F402C !important;
}
.frontpage-update .content
{
 background: #2A1002 !important;
}

/**** ESSENTIALS ****/
/* Main Pokemon sprite */
#dex-pokemon-portrait-sprite img,
.dex-warrior-portrait-sprite img
{
 background: #120600 !important;
}
/* Mini Pokemon icons */
.dex-icon-link:hover
{
 background-color: #480f0c !important;
 border-color: #95302a !important;
}
/* Held items */
.striped-row-groups { border-color: #7E6742 !important; }
.striped-row-groups tbody:nth-child(even)
{
 background-color: #120600 !important;
}
.striped-row-groups tbody:nth-child(odd)
{
 background-color: #240C00 !important;
}
.striped-row-groups tbody:hover
{
 background-color: #481800 !important;
}

/**** EVOLUTION ****/
.dex-evolution-chain td {
 background-color: #2A0401 !important;
 border-color: #48120C !important;
}
.dex-evolution-chain td.selected {
 background-color: #480F0C !important;
 border-color: #95302A !important;
}

/**** STATS ****/
.color1 { background-color: #120600 !important; }
.dex-pokemon-stats-bar-container { background-color: #0C0201 !important; }
.dex-pokemon-stats-bar {
 /*background: #240c00 !important;
 color: #FFF !important;*/
 color: #000 !important;
 opacity: .40 !important;
}

/* Sortable Pokemon lists */
.js-dex-pokemon-moves-controls
{
 background: #120600 !important;
}
.js-dex-pokemon-moves-sort-link:hover,
.js-dex-pokemon-moves-filter-link:hover
{
 background: #5A2820 !important;
}
.chain-divider
{
 border-color: #8A3C29 !important;
}

.header-row { border-color: #9C5618 !important; }
.header-row th { background: #1A0E02 !important;
border-color: #9C5618 !important; }
.subheader-row th { background-color: #2C1404 !important;
border-color: #2C1404 !important; }
.striped-rows { border-color: #7E6742 !important; }
.striped-rows tr:nth-child(odd) { background: #120600 !important; }
.striped-rows tr:nth-child(even) { background: #240C00 !important; }
.striped-rows tr:hover
{
 background: #481800 !important;
}
.better-move-type:nth-child(odd) td.type,
.better-move-type:nth-child(odd) td.type2,
.better-move-stat:nth-child(odd) td.class,
.better-move-stat-special:nth-child(odd) td.stat-special-attack,
.better-move-stat-physical:nth-child(odd) td.stat-attack,
.perfect-link:nth-child(odd) td.max-link
{ background: #103600 !important; }
.better-move-type:nth-child(even) td.type,
.better-move-type:nth-child(even) td.type2,
.better-move-stat:nth-child(even) td.class,
.better-move-stat-special:nth-child(even) td.stat-special-attack,
.better-move-stat-physical:nth-child(even) td.stat-attack,
.perfect-link:nth-child(even) td.max-link
{ background: #205100 !important; }

/**** FOOTER ****/
#footer
{
 background: #260A00 !important;
 border-color: #CDBA98 !important;
 color: #CDBA98 !important;
}

}
