css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.20ju.com/content/"), url-prefix("http://www.20ju.com/content/"){
body { background: #f0eee1; }
.wrapper { width: 90% !important; }
.digg,.clear { display: none; }
.mainbody {padding-top: 10px;}
#header,#mainbg,#logo,#menu {width: 1200px !important;}
#menu { background: none !important;height:23px; position:absolute; top:44px; left:auto; right:auto;}
#menu ul li { padding: 0px;BACKGROUND:none;float:left}
#menu ul li a{color:#a8a99b; font-weight:normal;float:left;padding: 0px 8px;line-height:1.8em; }
#menu ul li a:hover{color:#47422e; font-weight:normal; background:#e6e3d4;text-decoration:none  }
 #mainbg,#logo{  background:none transparent; }
#mainbg {margin: 0px auto;}
.main { width: 1180px !important;padding: 10px; -webkit-box-shadow: 1px 1px 4px rgba(150, 150, 150, .3); -moz-box-shadow: 1px 1px 4px rgba(150, 150, 150, .3);-webkit-border-radius: 6px; -moz-border-radius: 6px; border:1px solid #aaa;MARGIN-LEFT:0px;
}
.sidebar { display: none !important; }
#logo{ background: #fff; -webkit-box-shadow: 1px 1px 4px rgba(150, 150, 150, .3); -moz-box-shadow: 1px 1px 4px rgba(150, 150, 150, .3);-webkit-border-radius: 0px 0px 6px 6px; -moz-border-radius: 0px 0px 6px 6px; border-left:1px solid #aaa;border-right:1px solid #aaa;border-bottom:1px solid #aaa;height:65px;
}
.logopic img {height:45px;}
.logotext {PADDING-TOP: 0px;color:#666}
input.input_blur {border:1px solid #ddd}
}
