css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
Author: funkydrummer
Description:
removes adverts from fastmail.fm
*/

@-moz-document domain("www.fastmail.fm") {
div.main-nav, div.three-column, div.opera, div.standard, div.tagline, h2#tagline, p#opera, ul#main-nav, div#footer, a.twitter {display: none !important;}

#splash {
height:100% !important;
border-bottom:1px solid #bbb;
background:#f2f2f2 !important;
}

div.accent-line {
position:absolute; 
top: 10em !important; 
left:20em !important; 
height: 10em !important; 
width:20em !important;
margin-top:-1px;
border:none !important;
line-height:20px;
background:#f2f2f2;
color:#777;
border-width:0;
padding:10px 0;
}

fieldset#new {
position:absolute; 
top:0 !important;  
left:0em !important; 
right:0em !important; 
width:20em !important;
box-shadow:none !important;
}
}
