css:
@-moz-document domain('tweakers.net') {

html { 
    background: #444444; 
} 
#layout { 
    background-color: #666; 
    min-width: 1714px; 
} 
#top { 
    border-bottom: 0; 
} 
a { 
    color: #000000;
    text-decoration: none;
} 
div#contentArea { 
        padding-top: 0px; 
    padding-left: 20px; 
    padding-right: 20px; 
    background: #F7F7F7; 
} 
.eenReactie, .reactie { 
    background: #F7F7F7; 
} 
#menubar { 
    width: 980px; 
} 
#searchbar { 
    margin: 34px auto 0 auto; 
    width: 1020px; 
} 
.galleryHeadingContainer, .pageTabsContainer, .notificationsContainer { 
    margin: 0 auto; 
    width: 1020px; 
} 
.pageTabsContainer { 
    border-bottom: 1px solid #C3C6C6; 
} 
table.highlights td { 
    line-height: 5px; 
    padding: 5px 5px; 
} 
#menubar { 
    width: 1024px; 
    border-radius: 6px 6px 0 0; 
} 
.articleColumn { 
    line-height: 1.4; 
} 
#socialButtons { 
    display: none !important; 
} 
div.resizewarning div { 
    display: none !important; 
} 
img.resized { 
    border: 2px #000000 !important; 
    display: block; 
    float: none; 
} 
#jobs_relations { 
    display: none; 
} 
p { 
    -webkit-margin-after: 0.5em; 
} 
#contentArea >  #fpaItems {  
    margin-bottom: 10px;  
    float: right;  
    width: 330px;  
    border: 1px solid #dddede;  
    background: linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;  
    padding: 2px;  
}  

        #contentArea >  #fpaItems .fpaItem {  
            float: left;  
            width: 161px;  
            margin: 1px !important;  
            height: 122px;  
            border: 1px solid #dddede;  
            position: relative;  
        }  

            #contentArea >  #fpaItems .fpaItem div.imageContainer {  
                width: 100%;  
            }  

                #contentArea >  #fpaItems .fpaItem img {  
                    margin-left: -42px;  
                }  

            #contentArea >  #fpaItems .fpaItem div.textContainer {  
                background-color: rgba(0,0,0, 0.5);  
                color: #fff;  
                width: 100%;  
                height: auto;  
                font-size: 12px;  
                line-height: 1.4em;  
                padding: 4px;  
                position: absolute;  
                bottom: 0;  
            }  
div.popup.trackerPopup.fixed {
    position: absolute;
    width: 336px;
    top: 0px;
    box-shadow: none;
    z-index: 5000;
}
}
