css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
div.l-header .tab .tab_notice {

background: #000000;



}


body {

background: #005050 url('http://i49.tinypic.com/2ni8kdi.png') right bottom no-repeat !important;

background-attachment: fixed !important;

}

#posts li.notification {



  background-color: #427f7f !important;



  border-top: 1px solid #64c0c0 !important;



  border-left: 1px solid #64c0c0 !important;



  border-bottom: 1px solid #284c4c !important;



  border-right: 1px solid #284c4c !important;



  color: #a5d5d0 !important;



  text-decoration: none !important;



}






#posts .notification a:not(.avatar_frame) {



color: #33c8c9 !important;





text-decoration: none !important;



border-bottom: 0px solid #000000;



-webkit-background-clip: padding-box;



}







#posts .notification blockquote {



  border-left: solid 3px #66c2c2 !important;



}







#posts .notification .avatar_frame {



background-color: #000000;



border: 1px solid #000000;



position: absolute;



top: -1px;



left: -55px;



overflow: hidden;



display: inline-block;



}




.post.is_mine.with_avatar.new_post {



  color: #ffffff;



  font-family:courier, monospace;



  background: rgba(0,0,0,0.50) !important; 



}







#new_post{

background: #ffffff !important;


  background-repeat: no-repeat !important; 



}





#posts .post.new_post .new_post_label {



-webkit-font-smoothing: antialiased;



opacity: .9;



font-size: 14px;



font-weight: normal;



color: #ffffff;



text-decoration: none;



text-transform: uppercase;



text-align: center;



width: 74px;



position: absolute;



}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  background: rgba(0, 145, 162, .8) !important; 



}















.selection_nipple:not([class="selection_nipple white"]) {



border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;



}







#content_top {



  width: 0px !important;



  height: 0px !important;









}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {



background: none !important;



width: 0px !important;



height: 0px !important;



margin-top: 0px !important;



border-bottom: solid 11px rgba(00,00,00, 0.50) !important;



border-right: 11px solid transparent !important;



border-left: 11px solid transparent !important;



}









#content_bottom {



  width: 0px !important;



  height: 0px !important;



















}











#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



  padding-top: 25px !important; 



  padding-bottom: 25px !important; 



   -moz-border-radius: 20px !important; 



}











LABEL {



  color: rgba(0,0,0, 1.0) !important; 



}









#footer {



  display: none !important; 



}







.radar {



  display: none !important; 



}





.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue,



.selected .likes  {



  color: rgba(0, 0, 0, 0.50) !important; 



  background: rgba(0, 0, 0, 0.0) !important; 



}











#new_post .arrow {



  opacity: 0.50 !important;



  margin-left: -1px !important; 



}

a {
text-decoration: none;
color: #2c4762;
}

a:hover {
text-decoration: none;
color: #1898d4;
}

#posts .post {
margin: 20px 0 0 0;
-webkit-border-radius: 10px;
-moz-border-radius: 10px;
border-radius: 10px;
background-color: #ffffff;
padding: 15px 20px 15px 20px;
position: relative;
border-bottom: solid 2px #BBB;
word-wrap: break-word;
}

#posts .post blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote blockquote blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote {
border-left: solid 2px #a8b1ba;
}

#posts .post.is_reblog .post_info a {
text-decoration: none;
}

#posts .post .post_info a, #posts .post .post_controls a {
color: #a8b1ba;
text-decoration: none;
}

#posts .post .post_info a:hover, #posts .post .post_controls a:hover {
color: #12719e;
text-decoration: none;
}

#posts .post .footer_links .tags a {
margin-right: 7px;
color: #a8b1ba;
text-decoration: none;
}

#posts .post .footer_links .tags a:hover {
color: #333333;
text-decoration: none;
}

#posts .post .footer_links .source_url a:hover {
color: #333333;
text-decoration: none;
}

#posts .post .footer_links .source_url a {
position: relative;
text-decoration: none;
color: #a8b1ba;
}
}
