css:
@namespace url(http://www.w3.org/1999/xhtml);

/* RPOL.net Lilac Style Version 2 */
/* Style and Background by: adrasteia1 */
/* Web: http://www.roleplayersarchive.net */

@-moz-document regexp("https?://((www\.?)?rpol\.net|rpol.hyphenit.com.au).*") {

    body, th, tr, td, a {
           font-family: Verdana, Geneva, sans-serif !important;
    }
    
    body {
           background-color: #8359ed !important;
           background-image: url(data:image/jpeg;base64,) !important;
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-lilac-style-sec/notepaper_bg.jpg') !important; 
    }
    
    body, th, tr, td, span.normal_text {
           color: black !important;
    }
  
    table.selection_table th {
           background-color: #80749d !important;
    }
    
    table.selection_table td {
           background-color: #dcd2f5 !important;
    }
    
    td.quote {
           background-color: #D8C9FC !important;
    }
    
    
    a, a:link, a:visited, .link-colour, .msg a:hover:before, .msg a:hover:after {
           color: #1D3B6B !important;
            
    }
    
    a:hover, a:active, .alink-colour, .msg a:before, .msg a:after, a.button_on  {
           color: #FF3333 !important;
    }
 
    .border {
           border-color: #525252 !important;
    }
    
    .info_table {		
           border: 1px solid #525252 !important;
    }
	
    table.info_box {
           background-color: #dcd2f5 !important;
    }
	
    .info_box {
           border: 1px solid #525252 !important;
    }
	
    table.info_table.round5 td tr {
           background-color: #dcd2f5 !important;
    }
	
    table.info_table td {
           background-color: #dcd2f5 !important;
    }
    
    table.info_table tr {
           background-color: #dcd2f5 !important;
    }

    table.info_table th {
           background-color: #80749d !important;    
    }

    table.message_table td {
           background-color: #dcd2f5 !important;
    }

    table.message_table tr {
           background-color: #dcd2f5 !important;
    }

    table.message_table th { 
           background-color: #80749d !important;    
    } 
     
    #header, #footer {
	   border: 2px solid #6F638D !important;
	   background-color: #dcd2f5 !important;
    }
   
    .red, .red th, .red td {
           color: #FF3333 !important;
    }
    
    .blue, .blue th, .blue td {
           color: #1D3B6B !important;
    }
    
    span.alink-colour {
           background-color: #dcd2f5 !important;
    }
    
    td.sm.left {
           background-color: transparent !important; 
    }
    
    td.sm.right {
           background-color: transparent !important; 
    }
    
    td.left.message {
           background-color: #dcd2f5 !important;
    }
    
    td.left.message_alt {
           background-color: #dcd2f5 !important;
    }
    
    td.right.sm.alink-colour {
           background-image: none !important;
    }
    
    td.sm.alink-colour { 
           background-image: none !important;
    }
    
    #composediv.niftybox {
           color: #000000 !important;
           background-color: #80749d !important;
    }

    table#composetable {
           color: #000000 !important;
           background-color: #dcd2f5 !important;
    }
 
    div.niftybox {
           color: #000000 !important;
           background-color: #dcd2f5 !important;
    }
    
    tbody.info_table tr th {
           color: #000000 !important;
           background-color: #80749d !important;
    }

    tbody.info_table tr td {
           color: #000000 !important;
           background-color: #dcd2f5 !important;
    }

    div.info_box {
           color: #000000 !important;
           background-color: #80749d !important;
    }
    
    
    table.med.center tbody tr.info_table td {
           background-color: #80749d !important;
    }
      
    fieldset legend {
           color: #ffffff !important;
    }
}
