css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tweetdeck.twitter.com") {
.column-scroller, .med-tweet, .cmp-replyto{direction:rtl;font-family:tahoma;font-size:90%;}
.tweet{padding-left:0!important;padding-right:58px!important;}
.tweet-timestamp{padding-left:0!important;padding-right:5px!important;}
.column-scroller .pull-right,.med-tweet .pull-right, .cmp-replyto .pull-right{float:left!important;}
.column-scroller .pull-left,.med-tweet .pull-left, .cmp-replyto .pull-left{float:right!important;}
.tweet-img{margin-left:0!important;margin-right:-58px!important;}
.column-scroller .obj-left,.med-tweet .obj-left, .cmp-replyto .obj-left{float:right!important;margin-left:10px!important;}
.column-scroller .obj-right,.med-tweet .obj-right,.cmp-replyto .obj-right{float:left!important;margin-left:0!important;margin-right:10px!important;}
.account-link .username{direction:rtl!important;}
.tweet-actions{float:left!important;}
.tweet-actions > li{float:right!important;}
.tweet-dogear{left:0!important;right:auto!important;-moz-transform: rotate(270deg);-webkit-transform: rotate(270deg);}
.cmp-input-container,.tweet-text{direction:rtl!important;font-family:tahoma;}
.pos-l{right:auto!important;left:50%!important;}
.pos-l .caret{left:8px!important;right:auto!important;}
.compose-text{direction:rtl;font-family:'droid arabic naskh',tahoma;}
.txt-serif{font-family:'droid arabic naskh',tahoma!important;}
}
