css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weasyl.com")
{
#detail-bar, #detail-content.content.clear, #footer, section, nav, #user-content, .stage, #messages-content, .content {margin: auto !important; max-width: 1650px !important;
}

#detail-content.content.journal { max-width: 1650px !important;}

#detail-journal { margin: 0 auto !important; max-width: 50em !important;}

#page-container {
	background: url('https://www.weasyl.com/static/images/texture-canvas.png'), #242D31 url('https://www.weasyl.com/static/images/bg.jpg') repeat-x 0% 0 !important;
    border-bottom-width: 0 !important;
    border-top-width: 0 !important;
    max-width: 100% !important;
}

#user-content.content.clear, #messages-content, .content { max-width: 1583px !important;}

.avatar {border: 1px solid rgba(51, 51, 51, 0.24) !important; border-radius: 0px !important; margin: 2px !important; }

.bar.clear, .page-title, #home-content, #user-content, #detail-content, #control-content { border: 1px !important; border-color: rgba(150, 170, 189, 0.2) !important; border-style: solid !important;}

.file-input-wrapper.stage.clear { background: #242D31 !important; }

.modal-login #login-box-container #login-box { left: 40% !important; top: 31% !important;}

.stage, #user-info, #footer {background: transparent !important;}
}
