css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace html url("http://www.w3.org/1999/xhtml");

@-moz-document url("chrome://browser/content/browser.xul") {

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .autocomplete-richlistitem { 
  -moz-box-orient: horizontal !important;
}

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .autocomplete-richlistbox > scrollbox {
  overflow: visible !important;
}

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-title-box {
  -moz-box-ordinal-group: 1 !important;
  margin: 2px 0 !important;
}

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-url-box {
  -moz-box-ordinal-group: 2 !important;
  margin: 2px 0 !important;
  -moz-box-flex: 1 !important;
}

@media only screen and (max-width: 900px) {

  #main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-title-box {
    width: 20em !important; 
    min-width: 20em !important;
    max-width: 20em !important;
  }

}

@media only screen and (min-width: 901px) and (max-width: 1200px) {

  #main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-title-box {
    width: 25em !important; 
    min-width: 25em !important;
    max-width: 25em !important;
  }

}

@media only screen and (min-width: 1201px) and (max-width: 1500px) {

  #main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-title-box {
    width: 30em !important; 
    min-width: 30em !important;
    max-width: 30em !important;
  }

}

@media only screen and (min-width: 1501px) {

  #main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-title-box {
    width: 35em !important; 
    min-width: 35em !important;
    max-width: 35em !important;
  }

}

#PopupAutoCompleteRichResult .ac-title-box .ac-type-icon.ac-result-type-bookmark {
  -moz-box-ordinal-group: 1 !important;
}

#PopupAutoCompleteRichResult .ac-title-box .ac-site-icon {
  -moz-box-ordinal-group: 2 !important;
}

#PopupAutoCompleteRichResult .ac-title-box .ac-title {
  -moz-box-ordinal-group: 3 !important;
}

#PopupAutoCompleteRichResult .ac-title-box .ac-ellipsis-after.ac-comment {
  -moz-box-ordinal-group: 4 !important;
}

#PopupAutoCompleteRichResult .ac-title-box .ac-extra {
  -moz-box-ordinal-group: 5 !important;
}

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .autocomplete-richlistitem[type^="action"] .ac-url-box > .ac-site-icon,
#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .ac-url-box > .ac-type-icon {
  visibility: collapse !important;
}

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .autocomplete-richlistitem:not([type^="action"]) .ac-url-box > .ac-site-icon {
  background: url("chrome://browser/skin/devtools/debugger-play.png") no-repeat 0 0 !important;
  width: 16px !important;
  height: 16px !important;
  margin: 0 2px !important;
}

#main-window[sizemode="maximized"] #PopupAutoCompleteRichResult .autocomplete-richlistitem[selected="true"]:not([type^="action"]) .ac-url-box > .ac-site-icon {
  background-position: -16px 0 !important;
}

#PopupAutoCompleteRichResult html|span.ac-emphasize-text {
  box-shadow: inset 0 0 1px 1px rgba(0,0,0,0.3) !important;
}

#PopupAutoCompleteRichResult .ac-url-box > .ac-url > .ac-url-text:not([selected="true"]) {
  color: rgb(0, 102, 204) !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem:-moz-any([type="bookmark"],[type="tag"]) html|span.ac-emphasize-text {
  color: green !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem[type^="action"] .ac-url-box > .ac-url > .ac-action-text,
#PopupAutoCompleteRichResult .autocomplete-richlistitem[type^="action"] html|span.ac-emphasize-text {
  color: purple !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem:not([selected="true"]) .ac-extra {
  color: tan !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem:not([selected="true"]) .ac-extra html|span.ac-emphasize-text {
  color: olivedrab !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem:not(:first-child) {
  border-top: 1px solid #ddd !important;
}

}
