css:
/* AGENT_SHEET */
@-moz-document url(chrome://browser/content/browser.xul) {

/* === Bmarks Sidebar Header === */

#sidebar-box[src$="bookmarksPanel.xul"] #sidebar-header {

-moz-appearance: none !important;
color: rgb(51, 186, 255) !important;
background-image: -moz-linear-gradient(top, #475766, #1F2933) !important;
border-color: #000 !important;
}

#sidebar-box[src$="bookmarksPanel.xul"] .tabs-closebutton {
list-style-image: url("data:image/png;base64,") !important;
}

}

@-moz-document url(chrome://browser/content/bookmarks/bookmarksPanel.xul),
               url(chrome://browser/content/places/places.xul) {

/* === Scrollbars === */

/* remove appearance */
scrollbar, scrollbar * {
-moz-appearance: none !important;
}

/* scrollbar track */
scrollbar {
background: transparent !important;
margin-right: 1px !important;
}

/* big draggable thumb */
scrollbar thumb {
max-width: 15px !important;
background: rgba(0,0,0,.45) !important; 
border: none !important;
box-shadow: inset 0 0 2px #000 !important;
}

scrollbarbutton {
height: 0 !important;
min-height: 0 !important;
max-height: 0 !important;
border: none !important;
}

/* === Bookmarks Sidebar === */

/* Background gradient and text color */
#bookmarksPanel {
color: #CDD8E4 !important;
background-image:  url("chrome://browser/skin/devtools/responsive-background.png"), -moz-linear-gradient(top, #475766, #1F2933) !important;
}

#bookmarksPanel > #bookmarks-view {
color: inherit !important;
}

/* === Library / Places ( chrome://browser/content/places/places.xul ) === */

/* Library Background Noise Image */
#placesView {
background-image: url("chrome://browser/skin/devtools/responsive-background.png"), -moz-linear-gradient(top, #475766, #1F2933) !important;
}

/* BG transparent to show #placesView background, set text color */
#downloadsRichListBox,
#placesList,
#placeContent,
#detailsDeck {

color: #CDD8E4 !important;
background: transparent !important;
}

/* search forms */
textbox {

color: #ddd !important;
-moz-appearance: none !important; 
background: rgba(31, 33, 36, .75) !important;
box-shadow: inset 0 0 2px #000;
border:none !important;
}

textbox .anonymous-div {
color: #ccc !important;
}

/* == Treechildren == */

/* indentation */
treechildren::-moz-tree-indentation {
width: 14px !important;
}

/* Affects text presentation.  Change font-family, font-size if wanted */
treechildren {
font-size: 1.1em !important;
}

/* Height of rows + remove Firefox 25 borders */
treechildren::-moz-tree-row {
height: 2.2em !important;
outline: none !important;
border: none !important;
}

/* lighter color text on hover */
treechildren::-moz-tree-cell-text(hover) {
color: #DFEBF8 !important;
}

/* highlight row on hover*/
treechildren::-moz-tree-row(hover){
background: rgba(0,0,5,.4) !important;
}

/* highlight row when selected */
treechildren::-moz-tree-row(current),
treechildren::-moz-tree-row(selected) {
background: rgba(0,0,30,.4) !important;
}

/* Selected Row (Remove Dotted Border) */
treechildren::-moz-tree-row(current, focus) {
border: none !important;
}

/* drop feedback */
treechildren::-moz-tree-row(dropOn),
treechildren::-moz-tree-drop-feedback {
background: -moz-linear-gradient(left,#42705B,#4C935F) !important;
}

treechildren::-moz-tree-drop-feedback {
background: orange !important;
}

/* Remove Folder Icons */
treechildren::-moz-tree-image(container) {

margin: 0 !important;
padding: 0 !important;
width: 4px !important;
list-style-image: none !important;
}

/* Text emphasis on selected row */
treechildren::-moz-tree-cell-text(container,selected),
treechildren::-moz-tree-cell-text(selected) {
color: #eee !important;
font-weight: bold !important;
}

/* twisty | pointer thing */
treechildren::-moz-tree-twisty(closed) {
list-style-image: url("chrome://global/skin/tree/twisty-clsd-hover.png") !important;
}

/* twisty open */
treechildren::-moz-tree-twisty(open) {
list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAJBAMAAAASvxsjAAAAMFBMVEX////3nRv3nRv3nRv4vGH54rX55cD56ccAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAABqUJZXAAAAEHRSTlMAfLL//////wAAAAAAAAAALY+WXgAAAAlwSFlzAAALEgAACxIB0t1+/AAAACJJREFUCNdjYEAFQiCC0RlEiiSDmK4gUiS9GEgqGxtDVQEAN1wC7nlNeuwAAAAASUVORK5CYII=") !important;
}

/* Section borders */
#detailsDeck,
splitter {
border-color: #28323C !important;
}

/* separator */
treechildren::-moz-tree-separator {
border-top-color: #1D2328 !important;
border-bottom-color: #63798C !important;
}

/** toolbar @ top **/

#placesToolbar {
background: #222 -moz-linear-gradient(top, #475766, #1F2933) !important;
border-bottom-color: #000 !important;
}

/* toolbar arrows */
#back-button,
#forward-button{
list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important;
}

/* top bar search bookmarks text box */
#searchFilter {

background: #52525A !important;
box-shadow: none !important;
border: 1px solid #000 !important;
}

/* Organize, Views, Import and Backup text*/
#placesMenu > menu,
#clearDownloadsButton {
color: #ddd !important;
}

#clearDownloadsButton[disabled="true"] {
color: #aaa !important;
}

/* search filter */

#searchModifiers {
color: #fff !important;
-moz-appearance: none !important;
background: #222 !important;
border: 1px solid #000 !important;
}

#organizerScopeBar > toolbarbutton {
color: #ddd !important;
}

/* Save button when searching */

#saveSearch {

-moz-appearance: none !important;
color: #eee !important;
background: -moz-linear-gradient(top, #555 20%, #222) !important;
border: 1px solid #555 !important;
}

#saveSearch:hover {
background: -moz-linear-gradient(top, #666670 20%, #33333D) !important;
border-color: #777781 !important;
}

#saveSearch:active {

color: #fff !important;
background: #000 !important;
border-color: #1B6DA0 !important;
box-shadow: inset 0 0 4px #2BADFF !important;
}

/* Search In: Bookmarks, History, Tools, Web */

#organizerScopeBar > toolbarbutton[checked="true"] {

color: #fff !important;
border-color: #000 !important;
background: #124A6D !important;
}

/* Column Sorters */
#placeContentColumns treecol {

-moz-appearance: none !important;
color: #fff !important;
background: -moz-linear-gradient(top, #555, #222) !important;
border: 1px solid #222 !important;
}

#placeContentColumns treecol:hover {
background: -moz-linear-gradient(top, #6a6a6a, #4a4a4a) !important;
border: 1px solid #666 !important;
}

/* Highlight on selected sorter column */
#placeContentColumns > treecol[id][sortDirection] {
background: -moz-linear-gradient(top,#3B5289, #112428) !important;
}

/* Bookmark count */
#editBMPanel_itemsCountText,
#itemsCountText {
font-weight: bold;
font-size: 1.2em;
}

/* Tags selector table */

#editBMPanel_tagsSelector {
-moz-appearance: none !important;
background: transparent -moz-linear-gradient(top,rgba(0,0,0,.4),rgba(0,0,0,.1)) !important;
border: 1px solid #000 !important;
}

/* Set text color and remove box shadows */
#editBMPanel_tagsSelector listitem {
box-shadow: none !important;
color: #CDD8E4 !important;
}

#editBMPanel_tagsSelector listitem[selected="true"],
#editBMPanel_tagsSelector listitem[current="true"],
#editBMPanel_tagsSelector listitem[checked="true"] {

color: #EEE !important;
background-color: rgba(0,0,5,.4) !important;
}

listbox:focus > listitem[current="true"],
listbox:focus > listitem[selected="true"][current="true"] {
outline: none !important;
}

/* autocomplete tags suggestion */
#editBMPanel_tagsField > .autocomplete-result-popupset > panel{
background: -moz-linear-gradient(#141414,#222) !important;
border: 1px solid #000 !important;
}

#editBMPanel_tagsField .autocomplete-tree.plain {
color: #ddd !important;
}

/* Edit bookmark details section ( Name, Location, Tag etc )  */
#editBMPanel_rows > row > label {
font-weight: bold !important;
}

/* expander pointers / arrows */

.expander-up,
.expander-down {
-moz-appearance: none !important;
background: none transparent !important;
border: none !important;
}

.expander-down:hover,
.expander-up:hover {
box-shadow: inset 0 0 8px #000;
}

.expander-down .button-icon,
.expander-up .button-icon  {
display: none;
}

.expander-down > hbox:after {
content: "\25BC" !important; padding: .5em !important;
}

.expander-up > hbox:after   {
content: "\25B2" !important; padding: .5em !important;
}

.expander-down > hbox:after,
.expander-up > hbox:after {

color: #1B96DB !important;
text-shadow:
#000 0 1px,
#000 0 -1px,
#000 1px 0,
#000 -1px 0;
}

.expander-down:hover > hbox:after,
.expander-up:hover > hbox:after {

color: #000 !important;
text-shadow:
#ccc 0 1px,
#ccc 0 -1px,
#ccc 1px 0,
#ccc -1px 0;
}

}
