css:
/*http://userstyles.org/styles/77553/joomla-condensed-backend?r=1351695202*/
/*http://pastebin.com/download.php?i=FqQ0q97w*/
body {
  margin-top: 0px !important;
}
#border-top {
  display: none !important;
}
#toolbar .divider {
  display: none !important;
}
#submenu-box,
#element-box,
#filter-bar {
  padding: 0px !important;
}
.smallsub {
  width: 48% !important;
  float: right !important;
  font-size: 9px !important;
}
#filter-bar {
  position: absolute !important;
  top: -27px !important;
  border-bottom: none !important;
  opacity: .25 !important;
  -moz-transition: ease-out all .25s !important;
  -webkit-transition: ease-out all .25s !important;
  -o-transition: ease-out all .25s !important;
}
#filter-bar:hover,
#filter-bar:focus {
  opacity: 1 !important;
  -moz-transition: ease-in all .25s !important;
  -webkit-transition: ease-in all .25s !important;
  -o-transition: ease-in all .25s !important;
}
#filter-bar button {
  height: 17px !important;
  font-size: 12px !important;
  line-height: 7px !important;
  padding-bottom: 4px !important;
  border-radius: 4px !important;
  font-variant: small-caps !important;
  text-transform: lowercase !important;
  font-family: arial !important;
  background: -moz-linear-gradient(top, #e1ffff 0%, #e1ffff 7%, #e1ffff 12%, #fdffff 12%, #e6f8fd 30%, #c8eefb 54%, #bee4f8 75%, #b1d8f5 100%) !important;
}
.filter-search-lbl,
#filter-bar .inputbox {
  font-variant: small-caps !important;
  text-transform: lowercase !important;
  font-family: arial !important;
}
#element-box > DIV {
  position: relative !important;
  top: 15px !important;
  padding-top: 0px !important;
}
#submenu-box {
  width: 600px !important;
  height: 0px !important;
}
#submenu-box > DIV {
  width: 50% !important;
  position: absolute !important;
  border: none !important;
  top: 58px !important;
  left: 87px !important;
}
#submenu > li {
  outline: solid thin rgba(216,216,216,1) !important;
}
#submenu > li > a {
  padding-left: 9px !important;
  padding-right: 9px !important;
  margin: 0px !important;
}
#toolbar {
  position: absolute !important;
  display: inline-block !important;
  width: 400px !important;
  top: 1px !important;
  left: 486px !important;
}
#toolbar > ul > li {
  border-right: solid thin rgba(216,216,216,1) !important;
  border-left: solid thin white !important;
  width: 32px !important;
  height: 25px !important;
  padding: 0px !important;
}
#toolbar > ul > li:last-child:before {
  position: absolute !important;
  content: "" !important;
  top: 0px !important;
  width: 2px !important;
  height: 25px !important;
  border-right: solid thin white !important;
}
#toolbar > ul > li > a > span {
  width: 19px !important;
  height: 20px !important;
  background-size: 20px !important;
}
#toolbar > ul > li > a {
  font-size: 0px !important;
  color: rgba(0,0,0,0) !important;
  border-color: rgba(0,0,0,0) !important;
}
#toolbar > ul > li > a:hover {
  border-color: rgba(0,0,0,0) !important;
  cursor: default !important;
  height: 21px !important;
  background-color: rgba(219,228,233,1) !important;
  border-left: solid thin rgba(2,90,141,1) !important;
  border-right: solid thin rgba(2,90,141,1) !important;
}
a.toolbar {
  border-color: rgba(0,0,0,0) !important;
}
/*MENU SELECTION BOX*/
#toolbar-box div.m {
  height: 0px !important;
  padding: 0px !important;
  border-radius: 16px !important;
}
#toolbar-box > DIV {
  display: block !important;
  line-height: 4px !important;
  font-size: 7px !important;
  text-indent: -8px !important;
}
#toolbar-box > DIV > DIV:last-child > H2 {
  position: relative !important;
  top: -14px !important;
  left: 11px !important;
  font-variant: small-caps !important;
  text-transform: lowercase !important;
  font-size: 16px !important;
  font-family: arial-black !important;
  color: rgba(64,128,255, .75) !important;
}
div.pagetitle.icon-48-langmanager {
  background-size: 32px 32px !important;
  background-position: 8px 8px !important;
}
/*ICON TOOLTIPS*/
li[id^="toolbar-"] a:hover:before {
  position: absolute !important;
  content: "test" !important;
  top: 32px !important;
  margin-left: -34px !important;
  width: 70px !important;
  height: 14px !important;
  display: inline !important;
  background-color: rgba(255,255,255,.75) !important;
  border: solid thin black !important;
  border-radius: 4px !important;
  font: small-caps 10px arial !important;
  color: black !important;
  text-align: center !important;
  padding-left: 7px !important;
}
li[id^="toolbar-"] a:hover:after {
  position: absolute !important;
  content: "" !important;
  top: 28px !important;
  margin-left: -5px !important;
  border-right: solid 5px transparent !important;
  border-bottom: 5px solid black !important;
  border-left: 5px solid transparent !important;
  border-top: 0px solid transparent !important;
}
#toolbar-popup-new a:hover:before,
#toolbar-new a:hover:before {
  content: "new" !important;
}
#toolbar-edit a:hover:before {
  content: "edit" !important;
}
#toolbar-copy a:hover:before {
  content: "duplicate" !important;
}
#toolbar-send a:hover:before {
  content: "send email" !important;
}
#toolbar-publish a:hover:before {
  content: "publish" !important;
}
#toolbar-publish a[onclick*="users"]:hover:before {
  content: "activate" !important;
}
#toolbar-publish a[onclick*="links"]:hover:before,
#toolbar-publish a[onclick*="plugins"]:hover:before {
  content: "enable" !important;
}
#toolbar-publish a[onclick*="notes"]:hover:before,
#toolbar-publish a[onclick*="weblinks"]:hover:before {
  content: "publish" !important;
}
#toolbar-publish a[onclick*="messages.publish"]:hover:before {
  content: "mark as read" !important;
}
#toolbar-unpublish a[onclick*="messages.unpublish"]:hover:before {
  content: "mark as unread" !important;
}
#toolbar-unpublish a[onclick*="links"]:hover:before,
#toolbar-unpublish a[onclick*="plugins"]:hover:before {
  content: "disable" !important;
}
#toolbar-unpublish a:hover:before,
#toolbar-unpublish a[onclick*="weblinks"]:hover:before {
  content: "unpublish" !important;
}
#toolbar-unblock a:hover:before {
  content: "unblock" !important;
}
#toolbar-featured a:hover:before {
  content: "featured" !important;
}
#toolbar-popup-archive a:hover:before,
#toolbar-archive a:hover:before {
  content: "archive" !important;
}
#toolbar-popup-archive a[rel*="500"]:hover:before {
  content: "index" !important;
}
#toolbar-apply a:hover:before {
  content: "save" !important;
}
#toolbar-save-copy a:hover:before {
  content: "save as copy" !important;
}
#toolbar-checkin a:hover:before {
  content: "check in" !important;
}
#toolbar-remove a:hover:before {
  content: "remove" !important;
}
#toolbar-popup-export a:hover:before {
  content: "export" !important;
}
#toolbar-save-new a:hover:before {
  content: "save & new" !important;
}
#toolbar-save a:hover:before {
  content: "save & close" !important;
}
#toolbar-save a[onclick*="message"]:hover:before {
  content: "send" !important;
}
#toolbar-delete a:hover:before {
  content: "delete" !important;
}
#toolbar-delete a[onclick*="purge"]:hover:before {
  content: "purge expired" !important;
}
#toolbar-delete a[onclick*="manage"]:hover:before {
  content: "uninstall" !important;
}
#toolbar-COM_BANNERS_DELETE_MSG a:hover:before {
  content: "delete tracks" !important;
}
#toolbar-trash a:hover:before {
  content: "trash" !important;
}
#toolbar-trash a[onclick*="index.purge"]:hover:before {
  content: "purge" !important;
}
#toolbar-purge a:hover:before {
  content: "purge cache" !important;
}
#toolbar-default a:hover:before {
  content: "home" !important;
}
#toolbar-default a[onclick*="styles"]:hover:before {
  content: "make default" !important;
}
#toolbar-default a[onclick*="installed"]:hover:before {
  content: "default" !important;
}
#toolbar-default a:hover:before {
  content: "home" !important;
}
#toolbar-extension a:hover:before {
  content: "install language" !important;
}
#toolbar-refresh a:hover:before {
  content: "rebuild" !important;
}
#toolbar-refresh a[onclick*="manage.refresh"]:hover:before {
  content: "refresh cache" !important;
}
#toolbar-refresh a[onclick*="searches"]:hover:before {
  content: "reset" !important;
}
#toolbar-refresh a[onclick*="languages"]:hover:before {
  content: "find languages" !important;
}
#toolbar-popup-options a[rel*="850"]:hover:before {
  content: "my settings" !important;
}
#toolbar-popup-options a[rel*="800"]:hover:before,
#toolbar-popup-options a[rel*="875"]:hover:before {
  content: "options" !important;
}
#toolbar-popup-stats a:hover:before {
  content: "statistics" !important;
}
#toolbar-cancel a[onclick*="application"]:hover:before,
#toolbar-cancel a[onclick*="user"]:hover:before,
#toolbar-cancel a[onclick*="group"]:hover:before,
#toolbar-cancel a[onclick*="note"]:hover:before,
#toolbar-cancel a[onclick*="category"]:hover:before,
#toolbar-cancel a[onclick*="mail"]:hover:before,
#toolbar-cancel a[onclick*="menu"]:hover:before,
#toolbar-cancel a[onclick*="item"]:hover:before,
#toolbar-cancel a[onclick*="article.cancel"]:hover:before,
#toolbar-cancel a[onclick*="source.cancel"]:hover:before,
#toolbar-cancel a[onclick*="message"]:hover:before,
#toolbar-cancel a[onclick*="level"]:hover:before {
  content: "cancel" !important;
}
#toolbar-cancel a[onclick*="profile"]:hover:before,
#toolbar-cancel a[onclick*="template"]:hover:before,
#toolbar-cancel a[onclick*="module"]:hover:before,
#toolbar-cancel a[onclick*="user"]:hover:before,
#toolbar-cancel a[onclick*="group"]:hover:before,
#toolbar-cancel a[onclick*="level"]:hover:before,
#toolbar-cancel a[onclick*="category"]:hover:before,
#toolbar-cancel a[onclick*="article"]:hover:before,
#toolbar-cancel a[onclick*="banner"]:hover:before,
#toolbar-cancel a[onclick*="client"]:hover:before,
#toolbar-cancel a[onclick*="contact"]:hover:before,
#toolbar-cancel a[onclick*="newsfeed"]:hover:before,
#toolbar-cancel a[onclick*="weblink"]:hover:before,
#toolbar-cancel a[onclick*="plugin"]:hover:before,
#toolbar-cancel a[onclick*="contact"]:hover:before,
#toolbar-cancel a[onclick*="style"]:hover:before {
  content: "close" !important;
}
#toolbar-upload a:hover:before {
  content: "install" !important;
}
#toolbar-help a:hover:before {
  content: "help" !important;
}
