css:
@-moz-document domain("secure.thelevel.com") {/**/

.pagetitle { color: #003366; }
.formtitle { color: #003366; }
.bluetext { color: #003366; }
.toptext, .toptext a { color: #000000; }
.navItemOver {color: #ffffff; background-color: #cc6600; }
.navItemOver a { color: #ffffff; }
.navItem { color: #ffffff; background-color: #996600}
.navItem a { color: #ffffff; }
#filterFormHeader, .leftFormHeader { border-bottom: 1px solid #996600; }
#filterForm .tableUnderline { border-bottom: 1px solid #996600; }

body, p, ul, ol, dl, li, td, textarea, input, select, button, option, optgroup {
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  font-size: 14px;
  line-height: 20px;
  color: #333;
}

font {
  font-size: inherit;
}

small {
  font-size: small;
}


.tableBorder {
border: 1px solid #DDD;
border-collapse: separate;
border-left: 0;
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;
}

.tableBorder table {
  border-left: 1px solid #DDD;
}

.tableBorder table tbody tr:nth-child(odd) td, .tableBorder table tbody tr:nth-child(odd) th {
  background-color: #F9F9F9;
}

.tableBorder table th, .tableBorder table td {
padding: 8px;
line-height: 20px;
text-align: left;
vertical-align: top;
border-top: 1px solid #DDD;
}

.tableBorder td, .tableBorder th {
display: table-cell;
vertical-align: inherit;
}

.tableBorder table {
border-collapse: collapse;
border-spacing: 0;
}

.tableBorder table tbody tr:hover td {
  background-color: #D9EDF7;
}



#issuetable .nav, #issuetable .nav p {
  font-size: 14px;
}

.issuekey a {
  text-decoration: none;
}

.assignee, .resolution {
  font-style: italic;
}

#issuenav .count-pagination{padding:0 16px;}
span .results-count-total{font-weight:normal;}
#issuetable{background-color:#fff;border-collapse:collapse;margin:8px 0;width:100%;}
#issuetable thead th{background-color:transparent;border-bottom:1px solid #bbb;color:#999;font-weight:400;padding:3px 7px;white-space:nowrap;}
#issuetable thead .sortable{cursor:pointer;}
#issuetable thead .sortable{-moz-border-radius-topleft:2px;-moz-border-radius-topright:2px;-webkit-border-top-left-radius:2px;-webkit-border-top-right-radius:2px;border-top-left-radius:2px;border-top-right-radius:2px;}
#issuetable thead .sortable:hover{background-color:#f7f7f7;}
#issuetable thead .active{color:#fff;}
#issuetable thead .active:hover{background-color:#bbb;}
#issuetable thead .ascending{padding-right:20px;}
#issuetable thead .descending{padding-right:20px;}
#issuetable .rowHeader,#issuetable .rowNormal,#issuetable .rowAlternate{background-color:transparent;}
#issuetable th,#issuetable td{overflow:hidden;}
#issuetable>td{color:#333;}
#issuetable img{vertical-align:middle;}
#issuetable td{line-height:1.286;padding:5px 7px;}
#issuetable .nav{border-bottom:1px solid #eee;vertical-align:top;}
#issuetable .parentIssue{color:#666;padding-right:10px;}
.gadget #issuetable .parentIssue{background-position:100% 1px;}
#issuetable td a,#issuetable td a.parentIssue{color:#000;}
#issuetable td.issuekey a,#issuetable td.summary a,#issuetable td.stsummary a{color:#326ca6;}
#issuetable .issuetype,#issuetable .issuekey,#issuetable .priority,#issuetable .status{white-space:nowrap;width:16px;}
.msie #issuetable .issuekey,.msie #issuetable .status{width:1%;}
.msie-8 #issuetable img{max-width:none;}
#issuetable .resolution,#issuetable .created,#issuetable .updated{max-width:25em;white-space:nowrap;}
#issuetable .assignee,#issuetable .reporter,#issuetable .versions,#issuetable .components,#issuetable .fixVersions{max-width:60em;min-width:80px;}
#issuetable .versions .tinylink,#issuetable .components .tinylink,#issuetable .fixVersions .tinylink{white-space:nowrap;}
#issuetable td.summary>p,#issuetable td.description>p{min-width:200px;margin:0;max-width:1400px;white-space:normal;}
.gadget #issuetable .summary>p,.gadget #issuetable .description>p{margin:0;min-width:100px;}
#issuetable .issueCount{text-align:center;}
#issuetable .stsequence{white-space:nowrap;}
#issuetable td.progress,#issuetable td.aggregateprogress{min-width:150px;max-width:150px;text-align:right;width:150px;}
#issuetable td.progress>table,#issuetable td.aggregateprogress>table{font-size:1em;margin-top:2px;width:150px;}
#issuetable td.progress td,#issuetable td.aggregateprogress td{line-height:1;padding:0;vertical-align:top;}
#issuetable td.progress td.tt_graph_percentage,#issuetable td.aggregateprogress td.tt_graph_percentage{color:#999;padding-right:3px;min-width:0;width:auto;}
#issuetable td.progress td.tt_graph_percentage p,#issuetable td.aggregateprogress td.tt_graph_percentage p{width:3em;}
#issuetable td.progress table.tt_graph,#issuetable td.aggregateprogress table.tt_graph{height:6px;}
#issuetable .streorder{width:10px;}
#issuetable .streorder div{visibility:hidden;}
#issuetable .issuerow:hover,.gadget #issuetable .issuerow.hover,#issuetable.hide-carrot>tbody>tr.issuerow:hover{background-color:#f0f0f0;}
#issuetable .issuerow.focused .streorder div,#issuetable .issuerow:hover .streorder div{visibility:visible;}
#issuetable .issue_actions{width:18px;}
#issuetable .issue_actions .aui-dd-parent{height:18px;width:32px;}
#issuetable .issue_actions a.aui-dd-link{padding:0 0 0 19px;position:absolute;left:-9000px;top:-9000px;}
#issuetable .issue_actions a.active,#issuetable .issuerow.focused .issue_actions a.aui-dd-link,#issuetable .issuerow:hover .issue_actions a.aui-dd-link,.gadget #issuetable tr.hover .issue_actions a.aui-dd-link{left:0;top:0;}
#issuetable .issue_actions a.aui-dd-link span{display:block;width:15px;height:18px;background-position:2px 7px;text-indent:-9000px;overflow:hidden;}
#issuetable .issue_actions a.aui-dd-link:hover span,#issuetable .issue_actions a.aui-dd-link.active span{}
#issuetable .issue_actions a.aui-dd-link:hover,#issuetable .issue_actions a.aui-dd-link.active{background-color:#3c78b5;color:#fff;}




input[type=submit] {
  display: inline-block;
  *display: inline;
  padding: 4px 12px;
  margin-bottom: 0;
  *margin-left: .3em;
  font-size: 14px;
  line-height: 20px;
  *line-height: 20px;
  color: #333333;
  text-align: center;
  text-shadow: 0 1px 1px rgba(255, 255, 255, 0.75);
  vertical-align: middle;
  cursor: pointer;
  background-color: #f5f5f5;
  *background-color: #e6e6e6;
  background-image: -moz-linear-gradient(top, #ffffff, #e6e6e6);
  background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#ffffff), to(#e6e6e6));
  background-image: -webkit-linear-gradient(top, #ffffff, #e6e6e6);
  background-image: -o-linear-gradient(top, #ffffff, #e6e6e6);
  background-image: linear-gradient(to bottom, #ffffff, #e6e6e6);
  background-repeat: repeat-x;
  border: 1px solid #bbbbbb;
  *border: 0;
  border-color: #e6e6e6 #e6e6e6 #bfbfbf;
  border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
  border-bottom-color: #a2a2a2;
  -webkit-border-radius: 4px;
     -moz-border-radius: 4px;
          border-radius: 4px;
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffffffff', endColorstr='#ffe6e6e6', GradientType=0);
  filter: progid:DXImageTransform.Microsoft.gradient(enabled=false);
  *zoom: 1;
  -webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
     -moz-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
          box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
}

input[type=submit]:hover,
input[type=submit]:active,
input[type=submit].active,
input[type=submit].disabled,
input[type=submit][disabled] {
  color: #333333;
  background-color: #e6e6e6;
  *background-color: #d9d9d9;
}

input[type=submit]:active,
input[type=submit].active {
  background-color: #cccccc \9;
}

input[type=submit]:first-child {
  *margin-left: 0;
}

input[type=submit]:hover {
  color: #333333;
  text-decoration: none;
  background-color: #e6e6e6;
  *background-color: #d9d9d9;
  

  background-position: 0 -15px;
  -webkit-transition: background-position 0.1s linear;
     -moz-transition: background-position 0.1s linear;
       -o-transition: background-position 0.1s linear;
          transition: background-position 0.1s linear;
}

input[type=submit]:focus {
  outline: thin dotted #333;
  outline: 5px auto -webkit-focus-ring-color;
  outline-offset: -2px;
}

input[type=submit].active,
input[type=submit]:active {
  background-color: #e6e6e6;
  background-color: #d9d9d9 \9;
  background-image: none;
  outline: 0;
  -webkit-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
     -moz-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
          box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
}

input[type=submit].disabled,
input[type=submit][disabled] {
  cursor: default;
  background-color: #e6e6e6;
  background-image: none;
  opacity: 0.65;
  filter: alpha(opacity=65);
  -webkit-box-shadow: none;
     -moz-box-shadow: none;
          box-shadow: none;
}

#searchAndHide {
  display: none;
}

#searchButton {
  content: 'Search';
}

.rowHeader {
  background: none;
}




/*Greenhopper Styles */


.Bdg[style]{max-height: none !important; padding-bottom: 0px !important;
}

.Bdg, Bdgc {
border-right-color: #D4D4D4;
border-bottom-color: #E0E0E0;
-moz-box-sizing: border-box;
-ms-box-sizing: border-box;
-o-box-sizing: border-box;
-webkit-box-sizing: border-box;
box-sizing: border-box;
color: black;
cursor: move;
display: block;
margin: 0;
position: relative;
padding: 0px 0px 0px 8px;
padding-left: 8px;
}

.Bdg table, .Bdgc table {
	background: none;
}


.Bdg > div, .Bdgc > div {
	background: white;
	background: -moz-linear-gradient(top, white 25%, #F7F7F7 75%);
	background: -ms-linear-gradient(top, white 25%, #F7F7F7 75%);
	background: -webkit-linear-gradient(top, white 25%, #F7F7F7 75%);
	background: linear-gradient(top, white 25%, #F7F7F7 75%);

	width: 100% !important;
	float: none;
	clear: both !important;
	text-align: left !important;
	padding-left: 24px;
	box-sizing: border-box;
        -moz-box-sizing: border-box;
	padding-top: 3px !important;
	height: 100%;
	overflow: hidden;	
}

.Bdg:hover > div {
	background:#f0f0f0;
}

.Bdg a, .Bdgc a {
	text-decoration: none;
}
.Bdg div .icon, .Bdgc div .icon {
	position: absolute;
	left: 12px;
	background-position-y: 1px;
	height: 24px;
}

.Bdg div[style], .Bdgc div[style] {
	font-size: 12px !important;
}

.fdt_l, .fdt {
	max-height: none !important;
	font-size: 12px !important;
	width: 95%;
	height: auto;
	padding-bottom: 10px !important;
	line-height: 14px;


}

.fdt {
	font-size: 11px !important;
	line-height: 13px;
}

.cntLbl {
	font-weight: normal;
}
.cntTxtLnk {
	text-decoration: none;
}
.boxHover {
	background: none !important;
}

.issueList:hover {
	box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
}
.issueList[style] {
	opacity: 1 !important;
	padding: 0px !important;
	width: 100% !important;
}

.stepcolheader {
	font-size: 16px;
        font-weight: normal !important;
	margin: 0;
	word-wrap: break-word;
	color: black !important;
	text-transform: lowercase;
	z-index: 2;
	height: 50px;
	background-color: white !important;
}

.stepcolheader a {
	color: #036 !important;
}

.stepcolheader #PageAnchor {
	font-size: 12px;
	float: right;
        margin-top: -20px;
	color: #036;
}

.stepcolheader .info {
	font-style: normal;
	line-height: 1.6;
	color: #666;
	display: block;
	margin-left: 24px;
}

.stepcolheader .btn {
	float: left !important;
	margin-right: 5px;
}

.stepcolheader:first-letter {
	text-transform: uppercase !important;
}

#gh_taskboard_subsof_orphan {
	background-color: #eff0ef;
}
.sb {
	border: 0px !important;
	
}
.sb > div {
	border: 0px;
	background: white;
	margin-right: 5px !important;
	border-radius: 5px;
	border: 1px solid #bbb;
	min-height: 1000px;
	height: 100% !important;
	position: relative !important;
	padding-bottom: 100px !important;
	padding-top: 10px;
	margin-top: 10px;
}

.sb > div:after {
	content: "";
	display: table;
	clear: both;
}

.navitem, #main-nav li {
  line-height: 14px;
  text-transform: none !important;
font-size: 10px;
font-family: Verdana,Sans-Serif;
font-weight: bold;
color: white;
}

