css:
@namespace url(http://www.w3.org/1999/xhtml);

/* RPOL.net Celtic Green Style Version 2.0 */
/* Style by: adrasteia1 */
/* Web: http://www.roleplayersarchive.net */
/* Background: Photoshop brushes by Obsidian Dawn (http://www.obsidiandawn.com). Theme design by: adrasteia1 */


@-moz-document regexp("https?://((www\.?)?rpol\.net|rpol.hyphenit.com.au).*") {

    body, th, tr, td, a {
               font-family: Verdana, Geneva, sans-serif !important;
    }
    
    body {
               background-color: #acfef2 !important;
               background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-aqua-green-celtic/transparentceltic.jpg') !important;
               background-repeat: repeat-x !important;
    }
        
    body, th, tr, td, span.normal_text {
               color: #000000 !important;
    }
    
    table.selection_table {
               margin-top: 15px !important;
    }
    
    table.selection_table th {
               background-color: #4daa73 !important;
    }
    
    table.selection_table td {
               background-color: #eaf9f7 !important;
    }
    
    td.quote {
               background-color: #e0edef !important;
    }
    
    a, a:link, a:visited, .link-colour, .msg a:hover:before, .msg a:hover:after {
               color: #251884 !important;
    }
    
    a:hover, a:active, .alink-colour, .msg a:before, .msg a:after, a.button_on  {
               color: #FF3333 !important;
    }
 
    .border {
	       border-color: #525252 !important;
    }
    
     table.info_box {
	       background-color: #bcf9f0 !important;
	       margin-top: 15px !important;
    }
	
    .info_box {
	       border: 1px solid #525252 !important;
	       background-color: #bcf9f0 !important;
               color: #000000 !important;
    }
	
    table.info_table.round5 td tr {
	       background-color: #bcf9f0 !important;
    }
	
    table.info_table td {
	       background-color: #eaf9f7 !important;
    }

    table.info_table tr {
	       background-color: #eaf9f7 !important;
    }

    table.info_table th {
	       background-color: #4daa73 !important;
    }

    table.message_table td {
               background-color: #eaf9f7 !important;
    }

    table.message_table tr {
               background-color: #bcf9f0 !important;
    }

    table.message_table th { 
               background-color: #3b875a !important;    
    } 
    #header {
               border: 2px solid #6F638D !important;
	       background-color: #4daa73 !important;
    }
    #footer {
               border: 2px solid #6F638D !important;
	       background-color: #eaf9f7 !important;
    }
   
    .red, .red th, .red td {
               color: #FF3333 !important;
    }
    
    .blue, .blue th, .blue td {
               color: #1D3B6B !important;
    }
    
    span.alink-colour {
               background-color: #eaf9f7 !important;
    }
    
    td.sm.left {
               background-image: none !important; 
    }
    
    td.sm.right {
               background-image: none !important; 
    }
    
    td.left.message {
               background-color: #eaf9f7 !important;
    }
    
    td.left.message_alt {
               background-color: #cefaf3 !important;
    }
    
    td.right.sm.alink-colour {
               background-image: none !important;
    }
    
    td.sm.alink-colour { 
               background-image: none !important;
    }
    
    #wrapper:after {
               content:"Style by adrasteia1. Photoshop brushes by: Obsidian Dawn. http://www.obsidiandawn.com" !important;
               font-size: 9px !important;
               float: right !important;
               width: 600px !important;
    }
    #form-menu-container.right #form-menu.message a {
               background-color: #ffffff !important;
    }
    tbody.info_table tr td {
               background-color: #4daa73 !important;
               color: #ffffff !important;
    }
    tbody.info_table th {
               background-color: #4daa73 !important;
               color: #ffffff !important;
    }
    center form.form {
               color: #000000 !important;
    }
    table.selection_table {
               margin-top: 15px !important;
               box-shadow: 5px 10px 15px #49c9b6;
    }
    fieldset legend {
               color: #000000 !important;
    }
    table.med.center td {
               background-color: #4daa73 !important;
    }
}
