css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www1.cs-manager.com") {

#container, #footer_container,.post,#content, #header {

margin: 0 auto !important;



}

#container, #header, #footer_container{

border-left:1px solid black;

}

body {

font-family: tahoma !important;

background-color:#000000; 

background-image:url("http://users.telenet.be/tim.daminet/csm/patt.png") !important;

background-repeat:repeat !important;

} 

.dev tr td , .inline tr td, .inline input{

font-family: tahoma !important;

}

#header{

background-image:url("http://users.telenet.be/tim.daminet/csm/banner.png") !important;



}

#search{

background-color: #a2a2a2 !important;

}

.full, input{

background-color: #c9c9c9 !important;

}

td.foot {

font-family: tahoma !important;

background-color: #ebebeb !important;

}

.button , .buttonsmall{

background-image:url("http://users.telenet.be/tim.daminet/csm/title.jpg") !important;

border: 0px !important;

}

.buttonsmall{

padding:1px !important;

border: 1px solid #171717 !important;

}

h3, h2{

background-image:url("http://users.telenet.be/tim.daminet/csm/title.jpg") !important;

background-repeat:repeat-x !important;

border:0 !important;

color: white !important;

}

h3 a{

color: white !important;

text-decoration: underline !important;

}

table.info, td.player a{

background-color: #fafafa !important;

}

td.position{

background-color: #f2f2f2 !important;

}



table.info p,p a, td.position, td.player a, select{

font-family: tahoma !important;

}

td.head {

background-image:url("http://users.telenet.be/tim.daminet/csm/title.jpg") !important;

background-repeat:repeat-x !important;

}

.head {

background-image:url("http://users.telenet.be/tim.daminet/csm/title.jpg") !important;

background-repeat:repeat-x !important;

color: white !important;

}

td.info, .head td{

font-family: tahoma !important;

background-color:#f2f2f2 !important;

}



td.error, td.value {

font-family: tahoma !important;

background-color: #fafafa !important;

}



ul.menu li a:hover, ul.gameinfo li a:hover, ul#friend li a:hover, ul#tabtop li a:hover, ul#friend span:hover {

background-color: #fafafa  !important;

border-color:#5c5c5c !important;

color: #5c5c5c !important;

}

ul#friend li a:hover span{

color: #fafafa  !important;

}

ul.gameinfo li a, ul#friend li a, ul.menu li a {

background-color: #7c7c7c!important;

border-color:#5c5c5c !important;

color:#fafafa !important;

}



ul#friend span{

background-color: transparent !important;

background-image:url("http://users.telenet.be/tim.daminet/csm/online.png") !important;

background-repeat: no-repeat !important;

background-position: top right !important;

color: #7c7c7c !important;

padding-right: 12px !important;

padding-top: 0px !important;

}

ul#tabtop li a{

color: white !important;

text-decoration: underline !important;

background-color:#a2a2a2 !important;

}

.mail tr td{

font-family: tahoma !important;

}

/** LanCamp Training **/

tr.lan td.info {

	background: #CCFFCC !important;

}

tr.lan td.value {

	background: #CCFFCC !important;

}

}
