css:
@-moz-document domain("fanfiction.net"), domain("fictionpress.com") {

/*
STYLE AUTHOR: based on style by NHansen, modified by Speedy
*/

/************************************* 
BODY
*************************************/

body {
	background: #141414 !important;
 	color: #cacaca !important;
	}

body div {
	background-color: transparent!important;
	color: #cacaca  !important;
	}

div.sep {
	margin: 5px 0 5px 0 !important;
	border-bottom: 1px solid #33c5f2   !important;
	}

hr {
	background: -moz-linear-gradient(left,  rgba(83,203,241,0) 0%, 
rgba(73,201,241,0.5) 15%, rgba(69,200,241,0.75) 22%, rgba(60,199,242,1) 
36%, rgba(51,197,242,1) 50%, rgba(41,191,238,1) 61%, 
rgba(32,186,234,0.75) 71%, rgba(25,182,231,0.5) 79%, rgba(5,171,224,0) 
100%) !important;
    opacity: .7 !important;
	}

b {
 	color: #fff !important;
	}
	
div.modal-header {
    background: #086784 !important;
    opacity: 0.9 !important;
    border: 1px solid #cacaca !important;
    border-radius: 3px;
}

div.modal-body, div.modal-footer {
    background: #181818 !important;
    opacity: 0.9 !important;
    border: 1px solid #cacaca !important;
    border-radius: 3px;   
}

/************************************* 
WRAPPERS
*************************************/

div#content_wrapper_inner {
    background-color: #181818 !important;
    border-right: 1px solid #33c5f2  !important;
    border-left: 1px solid #33c5f2 !important;
    border-bottom: 1px solid #33c5f2 !important;
    }

/************************************* 
BODY LINKS 
*************************************/

a:link, span.fake_link {
	color: #109cff !important;
	text-decoration: none !important;
	border-bottom: 1px solid #109cff !important;
	}

a:visited {
	color: #33c5f2  !important;
	text-decoration: none !important;
	border-bottom: 1px solid #33c5f2 !important;
	}

a:hover, a:visited:hover, span.fake_link:hover { 
	color: #fff !important;
	text-decoration: none !important;
	border-bottom: 1px solid #fff !important;
    text-shadow: #fff 2px -2px 0.7em !important;  
	}

a:active { 
	color: #33c5f2 !important;
	text-decoration:none !important;
	}

a[href='http://m.fanfiction.net/m/yes_mobile.php'] { 
	border: 0 !important;
	}

/*************************************
TOP MENU LINKS
*************************************/


.zui a:link {
	color: #109cff  !important;
	background: transparent !important;
	text-decoration: none !important;
	border: none  !important;
    border-radius: 4px !important;
	padding: 5px 10px 5px 10px !important;
	}

.zui a:visited {
	color: #33c5f2 !important;
	background: transparent !important;
	text-decoration: none !important;
	border: none !important;
	}


.zui a:hover, .zui a:visited:hover { 
	color: #fff !important;
	background: transparent !important;
	text-shadow: #fff 1px -1px 0.6em !important;
	text-decoration:underline !important;
	}

.zui a:active { 
	color: #fff !important;
	background: transparent !important;
	text-shadow: #fff 1px -1px 0.6em !important;
	text-decoration:none !important;
	}
	
li.divider {
	background: #33c5f2 !important;
	border: none !important;
	}
	
.dropdown-menu li {
	margin: 1px !important;
	border: 0;
	}
	
ul.dropdown-menu {
    background: -moz-linear-gradient(left,  rgba(19,19,19,1) 0%, rgba(45,45,45,1) 100%); /* FF3.6+ */
    opacity: 0.95 !important;
    border: 1px solid #33c5f2 !important;
	}
	
	
.btn {
	background: #181818!important;
	color: #fff !important;
	text-shadow: #fff 2px -2px 0.6em !important;
	border: 1px solid #33c5f2 !important;
	box-shadow: 0px 0px 0px !important;
    }
    
.btn:hover {
     border-color: #fff !important;
     background-color: #2d2d2d !important;
     }



/*************************************
TOP MENU/NAVIGATION BARS
*************************************/

.xxui, .zui {
	background-color: transparent  !important;
	height: 30px !important;
	}

.zmenu {
	background: -moz-linear-gradient(top,  rgba(149,149,149,1) 0%, 
rgba(13,13,13,0.85) 46%, rgba(1,1,1,0.84) 50%, rgba(4,4,4,0.81) 58%, 
rgba(10,10,10,0.87) 72%, rgba(56,56,56,0.95) 90%, rgba(27,27,27,1) 
100%); /* FF3.6+ */
	border-bottom: 1px solid #33c5f2  !important;
	border-top: 1px solid #33c5f2 !important;
	padding: 0px !important;
	}

.menu-child {
    background: -moz-linear-gradient(top,  rgba(149,149,149,0.46) 0%, 
rgba(13,13,13,0.6) 23%, rgba(1,1,1,0.65) 31%, rgba(6,6,6,0.81) 58%, 
rgba(10,10,10,0.68) 80%, rgba(56,56,56,0.6) 94%, rgba(27,27,27,0.57) 
100%); /* FF3.6+ */
	border-bottom: 1px solid #33c5f2 !important;
	}

.zhide {
	display:none;
	}
	
div[style='width:100%;border-bottom:1px solid #CDCDCD;background-image:url("http://ffcdn2012.fictionpressllc.netdna-cdn.com/static/images/adbg.gif");'] {
    display:none !important;
    }

/* for fictionpress.com */
table.xxmenu {
	background-color: #070707 !important;
	border-bottom: 1px solid #33c5f2  !important;
	}
	
/*************************************
INPUTS (DROP-DOWNS, SELECTION, TEXTAREAS, ETC.)
*************************************/

select, input {
	background: #181818 !important;
	color: #cacaca !important;
	border: 1px solid #33c5f2  !important;
	-moz-appearance: none !important;
   	margin-right: 5px!important;
	margin-bottom: 4px !important;
	width: auto !important;
	}

input.searchfield {
	border: 1px solid #33c5f2 !important;
	}

textarea {
	background: #202020  !important;
	color: #cacaca !important;
	border: 1px solid #cacaca !important;
	-moz-appearance: none !important;
	margin-bottom: 5px !important;
	}

select:hover, select:focus, input:hover, input:focus, textarea:hover, textarea:focus {
	border-color: #fff  !important;
	}

/*************************************
TABLE COLORING/FIXES
*************************************/

.tborder, .trowh {
	background: #202020  !important;
	border: 1px solid #cacaca  !important;
	}

.tcat {
	background: #086784  !important;
	border: 0px solid #cacaca  !important;
	border-bottom: 6px solid #086784   !important;
	border-radius: 4px !important;
	}

.tcat a:link, .tcat a:visited {
	color: #33c5f2  !important;
	}

.tcat a:hover, .tcat a:active {
	color: #fff !important;
	text-shadow: #fff 1px -1px 0.6em;
	}

.thead {
	background: #054154  !important;
	color: #cacaca !important;
	border: 0px solid #cacaca !important;
	}

.myborder {
	background: #141414   !important;
	border: 1px solid #cacaca !important;
	padding-bottom: 10px !important;
	}

.myborder.menulinks {
	background: #141414  !important;
	border: 1px solid #cacaca  !important;
	padding-bottom: 2px !important;
	}

iframe {
	border: 1px solid #cacaca   !important;
	border-radius: 4px !important;
	margin-left: 4px !important;
	margin-top: 2px !important;
	}

#list_output table tr td {
    border-style: solid;
 	border-color: #cacaca   !important;
	border-radius: 4px !important;
	border-width: 1px 1px 1px 1px !important;
	padding-left: 7px !important;
	background: #202020   !important;
	}
	
td[style="width:260px;vertical-align:top;padding:5px;border: solid 1px #6581C1;-moz-box-shadow: -1px 0px 2px #cdcdcd;-webkit-box-shadow: -1px 0px 2px #cdcdcd;box-shadow: -1px 0px 2px #cdcdcd;"] {
    border: solid 1px #cacaca  !important;
    -webkit-box-shadow: 0px 0px 0px !important;
    box-shadow: 0px 0px 0px !important;
    background-color: #202020  !important;
}

div#top {
    background: -moz-linear-gradient(top,  rgba(149,149,149,1) 0%, 
rgba(13,13,13,0.85) 46%, rgba(1,1,1,0.84) 50%, rgba(4,4,4,0.81) 58%, 
rgba(10,10,10,0.87) 72%, rgba(56,56,56,0.95) 90%, rgba(27,27,27,1) 
100%); /* FF3.6+ */
    }
    
.table-bordered {
    border: 2px solid #cacaca !important;
    border-radius: 5px !important;
    }

#gui_table1i td {
    border: 0 !important;
    }
    
 th {
    border-top: 0 !important;
    }   

#gui_table3i td {
    border: 1px solid #2d2d2d !important;
    }
    
.table-bordered td {
    border: 0 !important;
    background-color: #181818 !important;
    }
    
.table-hover tbody tr:hover > td, .table-hover tbody tr:hover > th {
    background-color: #2d2d2d !important;
    }
    
span.mceIcon.mce_bold, span.mceIcon.mce_italic, span.mceIcon.mce_underline, span.mceIcon.mce_justifyleft, span.mceIcon.mce_justifycenter, span.mceIcon.mce_hr, span.mceIcon.mce_removeformat, span.mceIcon.mce_search, span.mceIcon.mce_fullscreen, span.mceIcon.mce_pasteword, span.mceIcon.mce_undo, span.mceIcon.mce_redo, span.mceIcon.mce_preview, span.mceIcon.mce_code, img.mceAction.mce_spellsplitbutton, a#bio_spellsplitbutton_open.mceOpen.mce_spellsplitbutton, span.mceIcon.mce_strikethrough, span.mceIcon.mce_blockquote, span.mceIcon.mce_bullist, span.mceIcon.mce_numlist, span.mceIcon.mce_link, a#xbody_spellsplitbutton_open.mce_spellsplitbutton, a#xbody_styleselect_text.mceText.mceTitle, a#xbody_styleselect_open.mceOpen {
    background-color: #33c5f2 !important;
    border-color: #fff !important;
    }
    
span.mceIcon.mce_bold:hover, span.mceIcon.mce_italic:hover, span.mceIcon.mce_underline:hover, span.mceIcon.mce_justifyleft:hover, span.mceIcon.mce_justifycenter:hover, span.mceIcon.mce_hr:hover, span.mceIcon.mce_removeformat:hover, span.mceIcon.mce_search:hover, span.mceIcon.mce_fullscreen:hover, span.mceIcon.mce_pasteword:hover, span.mceIcon.mce_undo:hover, span.mceIcon.mce_redo:hover, span.mceIcon.mce_preview:hover, span.mceIcon.mce_code:hover, img.mceAction.mce_spellsplitbutton:hover, a#bio_spellsplitbutton_open.mceOpen.mce_spellsplitbutton:hover, span.mceIcon.mce_strikethrough:hover, span.mceIcon.mce_blockquote:hover, span.mceIcon.mce_bullist:hover, span.mceIcon.mce_numlist:hover, span.mceIcon.mce_link:hover, a#xbody_spellsplitbutton_open.mce_spellsplitbutton:hover, a#xbody_styleselect_text.mceText.mceTitle:hover, a#xbody_styleselect_open.mceOpen:hover  {
    background-color: #109cff !important;
    }

a#bio_resize.mceResize, a#xbody_resize.mceResize {
   border: 0 !important;
   margin-right: -5px !important;
   margin-top: -8px !important;
   }
   
.mceIframContainer.mceFirst.mceLast {
   border: 0 !important;
   }
  
table#xbody_tbl.mceLayout {
   border: 0 !important;
   }
   
 a#l_st.current, a#l_fs, a#l_fa, a#l_cc {
     background: #181818 !important;
     border-color: #33c5f2 !important;
     }

   
 a#l_st:hover,  a#l_fs:hover,  a#l_fa:hover,  a#l_cc:hover {
     background: #181818 !important;
     border-color: #109cff !important;
     }

#mytab.nav.nav-tabs {
    border-bottom: 1px solid #33c5f2 !important;
    }

/*************************************
BORDERS
*************************************/

a[name="top"] + table.myborder tr td {
	background: transparent !important;
	}

.myborder_no_bottom, .myborder_only_bottom, .myborder_only_leftbottom {
	border-style: solid !important;
	border-color: transparent !important;
	}

.myborder_only_bottom.grayback {
	background-color: #141414 !important;
	}

a[name="top"] + table.myborder td:first-child {
	color: #33c5f2 !important;
	}

a[name="top"] + .myborder a, a[name="top"] + table.myborder td:first-child a {
	color: #cacaca !important;
	}

a[name="top"] + .myborder a:hover, a[name="top"] + table.myborder td:first-child a:hover {
	color: #33c5f2  !important;
	}
	
img.cimage {
    border: 2px solid #2d2d2d  !important;
    }
 
#xtab ul#primary a {
    border: 1px solid #cacaca  !important;
    border-bottom: 1px solid #cacaca  !important;
    border-radius: 3px;
    background: -moz-linear-gradient(top,  rgba(255,255,255,0.45) 0%, 
rgba(255,255,255,0.25) 7%, rgba(255,255,255,0) 26%, rgba(255,255,255,0) 
44%, rgba(255,255,255,0) 100%); /* FF3.6+ */
    }

#xtab ul#primary a.current {
    border-bottom: 1px solid #202020  !important;
    background-color: #202020 !important;
    }

#xtab ul#primary a:hover {
    border-bottom: 1px solid #202020  !important;
    background-color: #202020
    }
    
div[style='border:1px solid #666;padding-top:5px;'] {
    background-color: #202020 !important;
    border: 1px solid #cacaca  !important;
    border-radius: 2px;
    }
    
table#bio_tbl.mceLayout {
    border: 0 !important;
    }

/*************************************
REVIEW/LOGIN AREA + ALTERNATING BACKGROUNDS
*************************************/

.topnav {
	border: 1px #33c5f2  solid !important;
	background-color: #131313 !important;
	}

ul.topnav li a {
	border-bottom: 1px solid #33c5f2 !important;
	color: #33c5f2 !important;
	}

ul.topnav li a:hover {
background: -moz-linear-gradient(top,  rgba(0,166,226,1) 0%, 
rgba(0,38,51,0.78) 17%, rgba(0,15,20,0.79) 20%, rgba(0,8,11,0.86) 50%, 
rgba(0,1,2,0.95) 82%, rgba(0,63,86,0.98) 94%, rgba(0,87,118,1) 100%); /*
 FF3.6+ */
    border-bottom: 1px solid #33c5f2 !important;
    border-top: 0;
	color: #fff !important;
	text-shadow: #fff 1px -1px 0.6em;
	text-decoration:underline !important;
	}

ul.topnav ul li a {
	border-bottom: 1px solid #cacaca;
	color: #33c5f2!important;
	}

ul.topnav ul li ul li a {
	border-bottom: 1px solid #33c5f2 !important;
	color: #33c5f2 !important;
	}

ul.topnav ul li a:hover, ul.topnav ul li.active, ul.topnav ul ul li a:hover {
	background: -moz-linear-gradient(top,  rgba(0,166,226,0.7) 0%, 
rgba(0,38,51,0.78) 17%, rgba(0,15,20,0.79) 20%, rgba(0,8,11,0.86) 50%, 
rgba(0,1,2,0.73) 82%, rgba(0,63,86,0.68) 94%, rgba(0,87,118,0.65) 100%);
 /* FF3.6+ */

	}

.alt0,.alt0Active,.alt2,.alt2Active {
	background: #141414 !important;
	color: #cacaca  !important;
	}

.alt1,.alt1Active,.alt3,.alt3Active {
	background: #181818 !important;
	color: #cacaca !important;
	border-left: 0px solid #cacaca !important;
	border-top: 0px solid #cacaca !important;
	}
	
.table-striped tbody>tr:nth-child(odd)>td,.table-striped tbody>tr:nth-child(odd)>th{
    background-color: #141414 !important;
    }

/* MUST *ALWAYS* BE AFTER THE ABOVE TWO CLASSES */
.fhigh {
	background: #333 !important;
	}

/*************************************
TABLE SPACING/HOVER COLORS
*************************************/

table#gui_table15i,table#gui_table14i,table#gui_table13i,table#gui_table12i,
table#gui_table11i,table#gui_table10i,table#gui_table9i,table#gui_table8i,
table#gui_table7i,table#gui_table6i,table#gui_table5i,table#gui_table4i,
table#gui_table3i,table#gui_table2i,table#gui_table1i {
