	-moz-appearance: none !important;
	border-style: solid !important;
	}

.x-high, .z-high {
	background: #111 !important;
	}

/*************************************
LOGIN AREA COLORIZATION
*************************************/

.xtabc, .admintabs a{
	border-color: #cacaca !important;
	}

.admintabs a:hover, .admintabs a.selected, td[bgcolor="#f0f0f0"] {
	background: #2d2d2d !important;
	}

a.positive {
	border: 1px solid #cacaca !important;
	}

div.panel_normal, div.panel_warning, div.panel_success {
	border: 1px solid #cacaca !important;
	background-color: #202020 !important;
	color: #cacaca  !important;
	}

span.gui_warning {
	color: #ff9900  !important;
	}
	
span.gui_success {
	color: #00ff00  !important;
	}

div.panel_normal hr, div.panel_warning hr, div.panel_success hr {
	background-color: #202020  !important;
	color: #cacaca !important;
	}
	
/*************************************
REVIEW BUTTON CUSTOMIZATION
*************************************/

.xinput_style {
	color: #cacaca !important;
	border: solid 1px #cacaca !important;
	background-color: #2d2d2d !important;
	background: -moz-linear-gradient(top, #111, #111 1px, #111 30px) !important;
	}

.share_link a {
	color: #33c5f2 !important;
	}

div.toast {
    background: #141414 !important;
    border-color: #cacaca !important;
    color: #00ff00 !important;
    }
    
/************************************* 
TWITTER PLUG-IN, FAKE LINKS FIX 
*************************************/

#twitter_div {
    background-color: #181818 !important;
    }

#twitter_div h4, #twitter_div h3, #twitter_div h4 a  {
    background-color: transparent !important;
    color: #33c5f2 !important;
    }

div.twtr-bd {
    background-color: #181818 !important;
    }

div.twtr-bd p {
    color: #33c5f2 !important;
    }
 
div.simplemodal-container {
    background-color: #0F0F0F !important;
    border: 1px solid #2d2d2d !important;
    }
    
div.simplemodal-overlay {
    background-color: #202020 !important;
    }
    
div#review_success {
    color: #00ff00 !important;
    }

