.gray, .xgray {
	color: #555 !important;
	font-style: italic !important;
	}

.bm {
	border-bottom: 1px solid #33c5f2  !important;
	}

.drop_m {
	border-bottom: 1px solid #33c5f2  !important;
	border-top: 1px solid #33c5f2  !important;
	}

div[style="height:10px;border-top:1px solid #cdcdcd;"] {
	border-top: 1px solid #33c5f2 !important;
	margin-top: 5px !important;
	margin-bottom: 5px !important;
	}

span.sort-sort {
    color: #33c5f2 !important;
    border-color: #33c5f2 !important;
    }

span.sort-sort:hover {
    color: #fff !important;
    border-color: #fff !important;
    text-shadow: #fff 2px -2px 0.7em !important; 
    }
  
span.lc {
    background: #181818 !important;
    }

span.lc:hover {
    text-shadow: #fff 2px -2px 0.7em !important; 
    }
    
span.lc-left {
    background: #131313 !important;
    }
    
div.lc {
    background: #181818 !important;
    }

.storytextp {
    margin-top: 30px !important;
    }
    
span.badge {
   background-color: #086784 !important;
   opacity: .85 !important;
   }

span.badge.badge-active {
   background-color: #33c5f2 !important;
   }

.xicon-section-arrow, .icon-reply, .icon-cog {
   color: #cacaca !important;
   }

.modal-body td {
color: #ffffff !important;
}


/***********************************
PAIRING MODE BUTTON (LOGIN AREA)
************************************/

.btn.btn-primary {
     color: #33c5f2 !important;
     text-shadow: #33c5f2 2px -2px 0.7em !important;
     }
     
.btn.btn-success {
     color: #00ff00 !important;
     text-shadow: #00ff00 2px -2px 0.7em !important;
     }


/*************************************
COPY STORY TEXT
**********************************/

.nocopy {
-moz-user-select: text ! important;
}
    

