css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

.post_avatar
{
background-color:rgb(134, 0, 134);
}

#posts .post .post_question::after
{
bottom:-7px;
border-top:7px solid #b001b8;
}

#right_column::after
{
background:transparent;
}

body
{
color:#A200A2;
background:magenta;
}

a
{
color:rgb(255, 0, 255);
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:#DB00E4;
}

div.l-header .selection_nipple
{
border-bottom:12px solid #DB00E4;
}

#posts .post blockquote
{
border-left:solid 4px magenta;
}

#posts .post blockquote blockquote
{
border-left:solid 4px #E600E6;
}

#posts .post blockquote blockquote blockquote
{
border-left:solid 4px #CA00CA
}

#posts .post blockquote blockquote blockquote blockquote
{
border-left:solid 4px #9C009C;
}

#posts .post blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #7C007C;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #5C005C;
}

div.l-header #search_query
{
color:#FFC2FF;
}

#posts .notification
{
background-color:purple;
}

#posts .notification.single_notification
{
border:solid 1px purple;
}

#posts .notification .nipple.border
{
border-right:7px solid purple;
}

#posts .notification .nipple
{
border-right:6px solid purple;
}

#posts .notification a:not(.avatar_frame)
{
border-bottom:1px solid magenta;
}

#posts .notification.alt
{
background-color:#520052
}

#posts .notification.alt .nipple
{
border-right:6px solid #520052;
}

#posts .notification blockquote
{
border-left:solid 3px magenta;
}

#posts .post .post_info a
{
color:#B800FF;
}

#return_to_top .return_to_top_icon
{
background:-600px -190px url('http://i.imgur.com/YRU0F.png') no-repeat transparent;
}

#posts .post .post_question
{
background:#B000B8;
color:#FF6EE8;
}

#posts .post .post_question_nipple
{
width:0px;
height:0px;
border-left:10px solid transparent;
border-right:10px solid transparent;
border-top:10px solid #B000B8;
}

#tab_switching .tab_notice, div.l-header .tab .tab_notice, #popover_blogs .tab_notice, #dashboard_controls_open_blog .tab_notice
{
background-color:#B300D5;
}

.tab_notice_nipple
{
height:0px;
width:0px;
border-left:7px solid transparent;
border-right:0px solid transparent;
border-top:8px solid #B300D5;
}

#posts .post .post_question_asker
{
color:#909;
}

}
