css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("games.on.net") {
    .topiclist.forums .row {
        height:50px !important;
        overflow:hidden !important;
    }

    .topiclist.forums .row .icon {
        background-position:10px 10px !important;
    }

    .topiclist.forums .row DT[title='Unread posts'],
    .topiclist.forums .row DT[title='No unread posts']{
        margin-top:12px !important;
        height:20px !important;
        overflow:hidden !important;
    }    

    .topiclist.forums .row:hover {
        height:auto !important;
        overflow:auto !important;
    }

    .topiclist.forums .row:hover .icon {
        background-position:auto !important;
    }

    .topiclist.forums .row:hover DT[title='Unread posts'],
    .topiclist.forums .row:hover DT[title='No unread posts']{
        margin-top:auto !important;
        height:auto !important;
        overflow:auto !important;
    }

}
