css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.librarything.com") {

/* GENERAL */
body {
    background-color: #DDD !important;
}
body, div, h1, h2, h3, h4, p, span, td, li {
    color: #141 !important;
}
a {
    color: #636 !important;
}
body, td, h1, select, textarea, .subnav, input, .ltbtn, span {
    font-family: Palatino Linotype, Georgia, Times New Roman !important;
}
textarea, input[type=text], input[type=file] {
    font-size: 12px !important;
}
/* Navigation tabs. */
.userpad, #maintabs li {
    background-color: #DED !important;
}
.topnav {
    border-bottom: 1px solid #141 !important;
}
#maintabs li {
    border: 1px solid #141 !important;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
li#thisone {
    background-color: #EEE !important;
    border-bottom: 1px solid #C5C5C5 !important;
}
/* Subnavigation. */
.subnav {
    margin-top: 0px !important;
    border: 0 !important;
    background-color: #F8F8F8 !important;
    padding-top: 10px !important;
    padding-bottom: 10px !important;
}
.subnav .right a {
    font-weight: normal !important;
}
/* The login/out stuff. */
.userpad {
    border-bottom: 1px solid #141 !important;
    border-left: 1px solid #141 !important;
    border-bottom-left-radius: 5px !important;
}
.userpad .navicon12 {
    vertical-align: bottom !important;
}
/* Certain side boxen. */
.greenbox, .yellowbox {
    border: 1px solid #141 !important;
}
.greenbox {
    background-color: #DED !important;
    background-image: none !important;
}
.yellowbox {
    background-color: #F8F8F8 !important;
    background-image: none !important;
}
/* Search box. */
.sbar {
    background-image: none !important;
}
#sbarform {
    background-color: #FFF !important;
    border: 1px solid #353 !important;
    border-radius: 5px !important;
    padding: 5px !important;
}
#stxt {
    width: 130px !important;
    padding-left: 0 !important;
    margin: 0 !important;
}
/* Logo bar. */
.topnav {
    background-color: #898 !important;
    background-image: none !important;
}
/* The LT logo itself.  Font: Cochin. */
img#mainlogo {
    background-image: url('data:image/png;base64,') !important;
    margin-top: 5px !important;
    margin-bottom: -5px !important;
}
/* Top/bottom pagination rows and such. */
#topBarThing, #topBarThing table, .pageBar {
    background-color: #898 !important;
    color: #C5C5C5 !important;
}
/* Pseudo-button, normal. */
.ltbtn:hover .ltbtn-top-shadow, .ltbtn.ltbtn-activated .ltbtn-top-shadow {
    background: none repeat scroll 0 0 #AFF7A6 !important;
    border-bottom-color: #9CE792 !important;
    border-top-color: #C6EFC3 !important;
}
.ltbtn:hover .ltbtn-inner-box, .ltbtn.ltbtn-activated .ltbtn-inner-box {
    background: none repeat scroll 0 0 #88DE7D !important;
    border-bottom-color: #7AC572 !important;
    border-top: medium none #FFFFFF !important;
}
.ltbtn.ltbtn-collapse-left:hover .ltbtn-inner-box, .ltbtn.ltbtn-collapse-left.ltbtn-activated .ltbtn-inner-box {
    border-left-color: #9CE792 !important;
}
/* Pseudo-button, selected. */
.ltbtn-selected .ltbtn-top-shadow, .ltbtn-selected:hover .ltbtn-top-shadow {
    background: none repeat scroll 0 0 #D8B8F5 !important;
    border-bottom-color: #C6A5E4 !important;
    border-top-color: #E4D2F3 !important;
}
.ltbtn-selected .ltbtn-inner-box, .ltbtn-selected:hover .ltbtn-inner-box {
    background: none repeat scroll 0 0 #BD8AEA !important;
    border-bottom-color: #AA74DC !important;
    border-left-color: #9474AF !important;
}
.ltbtn-collapse-left.ltbtn-selected .ltbtn-inner-box, .ltbtn-collapse-left.ltbtn-selected:hover .ltbtn-inner-box {
    border-left-color: #D8B8F5 !important;
}
/* Selecting a collection. */
.menuBuilderMenuItemSelected {
    background-color: #BCB !important;
}
.menuBuilderMenuItem:hover {
    background-color: #454 !important;
    color: #EEE !important;
}

/* HOME */
/* Home page main box. */
#you {
    background-color: #BCB !important;
    border: 1px solid #676 !important;
    border-radius: 5px !important;
}
#you .bull {
    color: #676 !important;
}
/* Right sidebar. */
.right.smaller {
    border: 1px solid #120 !important;
    padding: 0 2px !important;
}
.right h1 {
    margin: 0px -2px 6px !important;
    background-color: #141 !important;
    color: #C5C5C5 !important;
}

/* ADD BOOKS */
/* Search area. */
.search {
    background-color: #BCB !important;
    border: 1px solid #676 !important;
    border-radius: 5px !important;
}
/* Adding books -> recently added. */
#bookframe h1 {
    background-color: #141 !important;
    color: #C5C5C5 !important;
}
/* The quick edit link. */
.show {
    color: #00C !important;
}
/* When you show quick edit for a book. */
.miniedit, .miniedit td, .miniedit .left {
    background-color: #C5C5C5 !important;
}
/* The book line hover. */
.addbooks .book:hover {
    background-color: #EFE !important;
}

/* COLLECTION */
/* Social stuff. */
.WorkStats { display: none !important; }
/* The next page/show all stuff. */
#pages .show, #pages2 .show, .pbStepper a, .pbNav a {
    color: #C5C5C5 !important;
    padding: 0 2px !important;
}
.pbStepper a:hover, .pbNav a:hover {
    color: #636 !important;
    background-color: #C5C5C5 !important;
}
/* Top nav thing. */
#topBarThing .displayBar {
    background-color: #F8F8F8 !important;
}
#topBarGrad {
    background-color: #D2D2D2 !important;
}
/* Search box is off by a pixel. */
#catalog_quicksearch_button, #catalog_quicksearch_menubutton {
  margin-top: -1px !important;
}
/* Book actions. */
.tools {
    background-color: #DED !important;
    border: 1px solid #9A9 !important;
    padding: 0px !important;
}
/* Hovering over a cell to change it. */
.ipe:hover, .ipeHover {
    background-color: #DED !important;
}
/* Remove the "double click" bit. */
.ipe:hover, .ipeHover {
    background-image: none !important;
}

/* INDIVIDUAL BOOK */
/* The book cover. */
img#mainCover {
    border: 1px solid #120 !Important;
    background-color: #F8F8F8 !important;
    padding: 5px !important;
    box-shadow: 1px 5px 3px #666 !important;
}
.workCoverImage {
    outline: none !important;
}
/* Tables. */
tr.wslheader td {
    background-color: #ABA !important;
    border: 1px solid #898 !important;
}
tr.wslcontent td {
    border: 1px solid #898 !important;
    background-color: #F8F8F8 !important;
}
/* Section titles. */
div[id^="workpagesort"] .nav {
    border: 1px solid #898 !important;
}
div[id^="workpagesort"] .nav, div[id^="workpagesort"] .navInfoTitle {
    background-color: #ABA !important;
}
div[id^="workpagesort"] .nav div span {
    margin-top: -5px !important;
}
/* Section bodies. */
div[id^="workpagesort"] .qelcontent {
    margin-top: -1px !important;
    margin-bottom: 15px !important;
    border: 1px solid #898 !important;
    background-color: #F8F8F8 !important;
}
div[id^="workpagesort"] .qelcontent .fwikiContainerTable {
    border: 0 !important;
}
/* References fix. */
div[id^="workpagesort"] .references {
    margin-left: 0px !important;
    margin-right: 0px !important;
}
/* "Wiki" buttons. */
.fwikiButton {
    background-color: #DDD !important;
}
.fwikiButton:hover {
    background-color: #ABA !important;
}
/* Editing book div. */
#book_bookInformationSection {
    margin-right: 1px !important;
}
#book_bookInformationSection .book_bit {
    border: 1px solid #898 !important;
}
/* Align certain elements. */
#book_bookInformationSection .bookNonEditField {
    padding-top: 2px !important;
}
#book_bookInformationSection .bookNonEditField img {
    margin-top: -8px !important;
}

/* INDIVIDUAL AUTHOR */
/* Left box. */
.authorContentTable .leftnav {
    border: 1px solid #141 !important;
    background-color: #DED !important;
}
/* Works. */
.work_statline, .workSimpleHead {
    border: 1px solid #898 !important;
}
.work_statline_header td, .workSimpleHead td {
    background-color: #DED !important;
}
.work_statline_td, .worklist {
    background-color: #F8F8F8 !important;
}
.worklist {
    padding-top: 1px !important;
    margin: -1px 0 20px 0 !important;
    border: 1px solid #898 !important;
}
/* Members, events. */
.memberLists, .venueListing {
    border: 1px solid #898 !important;
    background-color: #F8F8F8 !important;
    margin: -11px -3px 20px -3px !important;
    padding: 5px !important;
}
/* Common Knowledge */
.fwikiContainerTable {
    border: 1px solid #898 !important;
    background-color: #F8F8F8 !important;
    margin-top: -1px !important;
}

/* SERIES */
/* Extra stuff in the sub nav bar. */
.fwikiSubnavTableRight {
    margin-top: -11px !important;
}
/* Works. */
.worksinseries {
    border: 1px solid #898 !important;
    background-color: #F8F8F8 !important;
}
.worksinseriestop td {
    background-color: #DED !important;
}
.worksinseries .checkmark {
    padding-left: 10px !important;
    width: 40px !important;
}
/* Tags and recs. */
#relatedtagsarea, #recommendations {
    border: 1px solid #898 !important;
    background-color: #F8F8F8 !important;
    padding-left: 5px !important;
}
/* Why, why. */
.content {
    margin-bottom: 0px !important;
}

/* WIKI HELP */
/* Subnavbar. */
#wtSubnav {
    background-image: none !important;
    height: 28px !important;
    padding-bottom: 0px !important;
    margin-top: 0 !important;
    background-color: #F8F8F8 !important;
}
/* Main content. */
#content {
    margin-top: -5px !important;
}
/* Left sidebar. */
#navContainer {
    background-color: #DED !important;
    margin-top: -10px !important;
    border-right: 1px solid #898 !important;
    border-bottom: 1px solid #898 !important;
}
#navContainer .navTitle {
    border-bottom: 1px solid #676 !important;
}
/* Right sidebar. */
.rightcolumn {
    background-color: #DED !important;
    border: 0 !important;
    border-left: 1px solid #898 !important;
    border-bottom: 1px solid #898 !important;
    margin-right: -15px !important;
    margin-top: -60px !important;
    padding-top: 15px !important;
}
#toc {
    background-color: #DED !important;
}
/* Category links. */
#catlinks {
    border: 1px solid #898 !important;
    background-color: #DED !important;
}

/* PROFILE */
/* Seriously. */
p {
    margin-bottom: 0 !important;
}
/* The comments box. */
.comments {
    margin-top: 20px !important;
}
.comments a {
    font-size: 11px !important;
}

/* STATISTICS */
/* Left nav column. */
div.leftnav {
    background-color: #E8FFE8 !important;
    border: 1px solid #9C9 !important;
}
li.slect {
    background-color: #FFF !important;
    border: 1px solid #363 !important;
}

}
