@-moz-document domain("craigslist.org") {
/* Site Settings */

* {
	text-decoration: none!important;
}

.cl-component.location-picker-homepage-link a,
.homepage a,
.searchgroup .searchfieldlabel
{
	text-transform: capitalize!important;
}

.alert-info,
.buttongroup,
.buttongroup .button,
.gallery .arrow.slider-back,
.gallery .arrow.slider-forward,
.grid .result-image .result-price,
.grid .result-row,
.jsonform-select.segmented button,
.jsonform-select.segmented,
.searchbtn,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

li {
	letter-spacing: 1px;
}

/*___ Fonts ________________________________________________________________________*/

.cl-component.location-picker-homepage-link a {
    font-size: 45px!important;
}

.grid .result-image .result-price
 {
    font-size: 31px!important;
}

.homepage a,
.jsonform-select.segmented button
{
	font-size: 27px!important;
}

.homepage #rightbar h5,
.result-date
{
	font-size: 19px!important;
}

.alert.alert-sm
{
	font-size: 17px!important;
}

selector
{
	font-size: 15px!important;
}

.homepage a
{
	line-height: 33px!important;
}

.result-row .result-heading
{
	line-height: 27px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.content,
.querybox,
.search-legend
{
    margin-left: 20%!important;
}

.grid .result-info {
	bottom: 0!important;
}

.form-tab {
    top: 13px;
}

/*___ Margins ______________________________________________________________________*/

.grid .result-row
{
	margin: 12px!important;
}

selector
{
	margin: 3px 6px!important;
}

x.grid .result-row .result-image img {
    margin: 13% 9%;
}


/*___ Paddings _____________________________________________________________________*/

.cal,
.grid .result-row,
.searchgroup .searchfieldlabel
{
	padding: 12px!important;
}

.homepage #postlks #post
{
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.grid .result-row
{
	max-height: none!important;
}

.jsonform-select.segmented
{
	height: auto!important;
}

.grid.has-map-view-button .open-map-view-button {
    height: 24rem;
}

.grid .result-row {
    height: 23rem!important;
}

.grid .result-row .result-image {
    height: 80%!important;
}

/*___ Widths _______________________________________________________________________*/

.grid .result-row .result-image img,
.grid .result-row,
.homepage .col ul.left, .homepage .col ul.mc
{
	max-width: none!important;
}

.actions-combo
{
	width: auto!important;
}

.homepage .col ul,
.homepage .community ul,
.homepage .jobs ul,
.w1024.homepage .page-container,

ul#hhh0,
ul#jjj0,ul#jjj1
{
	width: 98%!important;
}

.grid.has-map-view-button .open-map-view-button {
    width: 69%;
}

.homepage .col ul {
    width: 48%!important;
}

ul#bbb0,ul#bbb1,
ul#ccc0,ul#ccc1,
ul#ggg0,ul#ggg1,
ul#sss0,ul#sss1
{
    width: 46%!important;
}

ul#forums0,ul#forums1,ul#forums2
{
    width: 30%!important;
}

.grid .result-row {
    width: 21%!important;
}

.search-options-container {
    width: 18%!important;
}

}

