.alert-info,
.searchgroup .searchfieldlabel,
h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

.threadpaginator .threadnumbers,
time
 {
	color: #07beff!important;
}

/*___ Green ________________________________________________________________________*/

.grid .result-image .result-price
 {
	color: #07ff24!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*__________________________________________________________________________________*/

.dropdown-list,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #641a1a!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 8px;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

::placeholder
{
	color: #f48f8f;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/

.actions-combo .action.flag-action .flag .flagIcon,
.cl-component.share-button .container .share-icon,
.gallery .arrow
{
    color: #0000;
}

.form-tab {
    color: #0000;
}

.hnd {
    color: #81ff81;
}

}

@-moz-document domain("craigslist.org") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.gallery .arrow,
.gallery .slider-info,
.global-header,
.homepage .cats,
.querybox,
.search-options-container,

footer
{
	background: #060505!important;
	background-color: #060505!important;
}

selector
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#topban,

.ban .bantext,
.buttongroup,
.cal td,
.form-tab,
.grid .result-image .result-price,
.hasPic,
.homepage .ban a:hover,
.homepage .col ul,
.jsonform-select.segmented button,
.result-title:focus,
.search-options,
.searchbtn,

[bgcolor="#dddddd"],
body.dayTheme .forumlistcolumns .col li
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.actions-combo .reply-button
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.actions-combo .reply-button::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.actions-combo .reply-button:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.cl-component.location-picker-by-map .apply-button
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
