.threadpaginator .threadnumbers,
.cl-component.location-picker-homepage-link a,
.gallery .arrow,
.threadline time
 {
	opacity: 1!important;
}

}

@-moz-document domain("craigslist.org") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Arrows _______________________________________________________________________*/

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/direction-arrow-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

.gallery .arrow.slider-back,
.search-open
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.gallery .arrow.slider-forward,
.search-closed
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.gallery .arrow.slider-back,
.gallery .arrow.slider-forward
{
	background-size: 29px!important;
	height: 33px!important;
	padding-left: 39px!important;
	width: 1px!important;
}

.search-closed,
.search-open
{
	background-size: 29px!important;
	height: 33px!important;
	padding-left: 39px!important;
	width: 1px!important;
}

/* Checkmark _______________________________________________________________________________*/

/* Big Check ___________________________*/

selector
{
	background: url(https://i.ibb.co/ZMNmHwc/Mcrw-IRxnnrq.png) no-repeat!important;
	box-sizing: border-box;
}

selector {
    background-size: 41px!important;
    height: 42px!important;
    margin-top: -6px!important;
    padding-left: 42px!important;
    width: 1px!important;
}

/* Checked ___________________________*/

input[type="checkbox"] {
    appearance: none!important;
}

input[type="checkbox"]:checked
{
	background: url(https://i.ibb.co/G76xcfP/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

input[type="checkbox"]
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

input[type="checkbox"]:checked, input[type="checkbox"] {
    background-size: 30px!important;
    height: 39px!important;
	margin-top: 12px;
    padding-left: 22px!important;
    width: 1px!important;
}

/* Eyes _______________________________________________________________*/

/* Open ____________________________*/

.icon-trash.red {
	background: url(https://i.ibb.co/VSLvZzD/eye-clip-art-symbol-eye-makeup-brown.png) no-repeat!important;
	box-sizing: border-box;
}

/* Close ____________________________*/

.icon-trash {
	background: url(https://i.ibb.co/v3qK5Rf/no-see.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-trash.red,
.icon-trash {
    background-size: 24px!important;
    height: 26px!important;
    padding-left: 25px!important;
    width: 1px!important;
}

/* Flag______________________________________________________________________*/

.white-flag
{
	background: url(https://i.ibb.co/42D6V8g/flag-yellow.png) no-repeat!important;
	box-sizing: border-box;
}

.black-flag
{
	background: url(https://i.ibb.co/yqZK8xW/flag-icon-512.png) no-repeat!important;
	box-sizing: border-box;
}

.black-flag, .white-flag {
    background-size: 24px!important;
    height: 26px!important;
    padding-left: 25px!important;
    width: 1px!important;
}

/* No Photo _______________________________________________________________________*/

.grid .result-row .result-image.empty
{
	background: url(https://i.ibb.co/SQrdmvK/no-image-style-30x30.png) no-repeat!important;
	box-sizing: border-box;
}

.grid .result-row .result-image.empty {
    background-size: 10rem!important;
    height: 16rem!important;
    padding-left: 11rem!important;
    width: 1px!important;
}

/* Search ________________________________________________________________________*/

.icon-search
{
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-search {
    background-size: 21px!important;
    height: 22px!important;
    padding-left: 22px!important;
    width: 1px!important;
}

/* Share _________________________________________________________________________*/

.cl-component.share-button .container .share-icon
{
	background: url(https://i.ibb.co/27rmYdq/share-36x36.png) no-repeat!important;
	box-sizing: border-box;
}

.cl-component.share-button .container .share-icon
{
    background-size: 24px!important;
    height: 26px!important;
    padding-left: 25px!important;
    width: 1px!important;
}

/* Star ___________________________________________________________________________*/

/* On ___________________*/

.icon-star.fav
{
	background: url(https://i.ibb.co/dPs9Fwm/star-26x25.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off __________________*/

.icon-star
{
	background: url(https://i.ibb.co/VBcDjnC/star-empty-glow.png) no-repeat!important;
	box-sizing: border-box;
}

.icon-star.fav,
.icon-star
{
	background-size: 20px!important;
	height: 21px!important;
	padding-left: 26px!important;
}

}

@-moz-document domain("craigslist.org") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
