/*--------------------------------*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org"),
domain("wikimedia.org"),
domain("wiktionary.org"),
domain("wikiquote.org"),
domain("wikisource.org"),
domain("wikiversity.org"),
domain("wikinews.org"),
domain("wikidata.org") {
/******************* TEXT, BACKGROUND-COLOR****/
body {
   font-family: Georgia, serif !important;
   font-size: 16px !important;
   color: #111;
}
div#p-namespaces{ margin-left:20px;}
#mp-left{ background-color: #f6f6f6 !important;}
#mp-tfp-h2,#mp-tfa-h2,#mp-itn-h2,#mp-dyk-h2,#mp-otd-h2{
  background-color: #f6f6f6 !important;
  border: 0 !important;
}
/******************* COLORS ****/
div#content, .homonymie, #mp-right {
   color: #000;
   background-color: #f6f6f6 !important;
}
div#mw-head, div#mw-page-base, div#mw-head-base{
background-color: #bbb !important;}
/*************** LINE-HEIGHT & COLUMN WIDTH ****/
div#content p, div#content li, div#content dl,
.NavFrame, pre, h1,h2,h3,h4,h5, .mw-content-ltr div{
   max-width: 700px;
   line-height:1.6em !important;
   list-style: circle !important;
}

/******************* INFOBOX, BOXs ****/
.infobox_v2 tbody{width: 300px !important;float: right;}
.infobox_v2 p{ width: 170px !important; }
.metadata, .bandeau{
margin-left:0px !important;
max-width:700px!important;}
/******************* TOC ****/
#toc { font-size:12px !important; max-width:700px;}
#toc li, #toc ul { display: inline; }
#toc li:after { content: ' | '; }
#toc li:last-child:after { content: ''; }
#toctitle { display: none; }
#mp-left img{visibility:visible !important;}
/******************* LINKS *****/
a, a:visited,
div#mw-panel div.portal div.body li a,
div#mw-panel div.portal div.body li a:visited {
   color: #777 !important;
}
.new {color:#994444 !important;}
/**************** TABS ********/
div#left-navigation, div#right-navigation #p-views{
    height:20px !important;
}
.vectorTabs, div#content, div#footer, div#mw-head-base,li{
   background-image: none !important;
}
.vectorTabs{
   background-color: #bbb !important;
   height:10px;
}
#mw-js-message{background-color:#ddd !important;}
.vectorTabs .selected a, .vectorTabs a:hover{
   background-color: #f6f6f6 !important;
}
.vectorTabs a{
   color: #333 !important;
   background-color: #bbb !important;
}
#mw-head, mw-head-base{
   background-color: #FFF !important;
   position:fixed !important;
   height:80px !important;
}
#p-personal{
  font-size:95%;
}
#p-personal a{color:#555 !important;}
/******************* LOGO ****/
#p-logo{display:none;}
#mw-panel {margin-top:-30px;}
#mw-panel:before {
   font-family:/*[[fonts]]*/;
   content:'w';
   font-weight:normal;
   font-size:130px;  
   margin-left:20px;
   line-height:1em;
   color:/*[[logocolor]]*/;
   position:fixed !important;
   margin-top:-172px !important;
}
/******************* IMAGES ****/
img, .thumbinner, .bandeau,
.mw-js-message-interprojets {
   border-radius:2px;
}
/******************* HOME ****/
.mw-content-ltr table p img,hr{visibility:hidden !important;}
#mp-right div{background:none !important;}
#accueil_titre{max-width:1200px !important;}
#mw-content-text table td, .vectorMenu a{
   background:none !important;
   border:0 !important;
}
/******************* TITLES ****/
.modifiedSectionTitle, .firstHeading,h1,h2,h3,h4,h5{
   padding:10px !important;
   margin-left:10px !important;
   font-family:/*[[fonts]]*/ !important;
   font-style:italic !important;
   line-height:1em !important;
   letter-spacing:0.05em;
   border-bottom:0 !important;
   color:#555 !important;
}

h3 .mw-headline,h4 .mw-headline,h5 .mw-headline{
font-weight:normal !important;}

h1{  font-size:2.4em !important;  }
h2 .mw-headline{  font-size:1.5em !important;  }
h3 .mw-headline{  font-size:1.7em !important;  }
h4 .mw-headline{  font-size:1.5em !important;  }
h5 .mw-headline{  font-size:1.3em !important;  }

::-moz-selection {background: #9B3;}
}
