border-right-color: #006699 !important;
border-left-width : 2px !important;
border-right-width : 2px !important;
border-bottom-style : none !important;
border-top-style : none !important;
font-family: "Arial", "Verdana", "Helvetica", sans-serif !important; 
font-size: 1.1em !important;
font-weight :bold !important;
color:#FFA34F !important; }


.forum-row-first td, 
.forum-row-last td, 
.forum-row-odd td, 
.forum-row-even td {
border-top-color:#001C31 !important; 
border-left-color: #006699 !important;
border-right-color: #006699 !important;
border-left-width : 2px !important;
border-right-width : 2px !important;
border-bottom-color:#001C31 !important; }

.forum-column-footer {
border: 2px solid #006699 !important;
background: #FFFFFF !important;
font-family: "Arial", "Verdana", "Helvetica", sans-serif !important; 
font-size: 1.1em !important; 
font-weight :bold  !important; 
text-align : center !important; 
color:#FFA34F !important;
border-bottom : none !important;
border-left : none !important;
border-right : none !important;
}
.forum-navigation-box {
border-top-style : none !important;
border-right-style : none !important;
border-left-style : none !important;
border-bottom-style : none !important; }

.forum-page-navigation {
float:right !important; }

.forum-block-container {
background-color:#003E63 !important;
color: White !important; }

.forum-cell-contact {
