height:1px !important;
border-top:none !important;
border-left-color:#006699 !important;
border-right-color:#006699 !important;
border-left-width : 2px !important; }

.forum-cell-user {
background-color:#003E63 !important;
border-left-color:#006699 !important;
border-left-width:2px !important;
border-right-color:#006699 !important; }

.forum-cell-post {
background-color:#003E63 !important;
border-right-color: #006699 !important;
border-right-width : 2px !important; }

.forum-contact-links {
height:1px !important;
}


.forum-cell-actions {
background-color:#003E63 !important;
height: 1px !important;
border-top : none !important; 
border-right-color: #006699 !important;
border-right-width : 2px !important; }

.forum-user-info {
background-color:#003E63 !important;
color:#cccccc !important; }

.forum-user-signature {
background-color:#003E63 !important;
color : #bbbbbb !important;
border-top-color: #bbbbbb; }

.forum-post-date {
background-color:#003E63 !important;
border-bottom: none !important;
color : #bbbbbb !important; }

.forum-post-text {
background-color:#003E63 !important;
color:#e0e0e0  !important; }

.forum-post-table {
background-color:#003E63 !important;
border-bottom-color: #006699  !important;
border-top-color: #006699 !important; }

div.forum-note-box { background: none #000040 !important; }
div.forum-note-success {
border-style: none !important; ;
border-width: 0 !important; ;
margin: 0 !important; ;
padding: 0 !important;
background-color: #006699 !important;
}

.forum-quote td, 
.forum-spoiler td, 
.forum-code td, 
.forum-code-box ol, 
.forum-quote th, 
.forum-spoiler th, 
.forum-code th, 
.forum-code-box {
background : #001C31 !important;
color: #BCBCBC !important;
font-family: "Arial", "Verdana", "Helvetica", sans-serif !important; 
font-size: 1em !important;
line-height: 125% !important; }

.forum-quote, 
.forum-spoiler, 
.forum-code, 
.forum-code-box {
border-bottom-color : #003E63 !important;
border-left-color : #003E63 !important;
border-right-color : #003E63 !important;
border-top-color : #003E63 !important; }

div#botmenustr a.selected, span.forum-page-current {
text-decoration: underline !important;
color : Gray !important;
}

blockquote.bx-quote {
