css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



* { text-shadow: none !important; }



div.l-content,#left_column #posts .post .permalink {

background-color:none !important

}



#dashboard_controls_suggested_blogs {

background:#aaa !important;

border:none !important

}



#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#main_tag_beta div.l-content, #dashboard_inbox div.l-content,#dashboard_tumblelog_settings div.l-content,#dashboard_followers div.l-content,#tagged_display #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background:rgba(0,0,0,0.3) !important

}



#dashboard_index,#dashboard_edit_post,#dashboard_submissions,#dashboard_drafts,#dashboard_post_queue,#dashboard_likes,#dashboard_followers,#dashboard_following,#dashboard_members,#main_tag,#main_tag_beta,#dashboard_inbox,#dashboard_tumblelog_settings,#dashboard_directory,#tagged_display {

background:#000000 url(http://oi47.tinypic.com/11awjde.jpg) no-repeat right bottom fixed !important

}



#dashboard_switch_blog_menu_current,#dashboard_switch_blog_arrow_and_divider,#dashboard_switch_blog_arrow_current {

background-image:none !important

}







#footer,.dashboard_options_form_divider {

display:none

}



#left_column #dashboard_controls a, #left_column #dashboard_controls span {

color: #555 !important;

}



#left_column #posts .notification {

background-color:#eee !important;

border-bottom:none !important;

color:#aaa !important

}



#left_column #posts .notification .avatar {

background-color:#fff !important;

border-color:transparent !important;

opacity:0.8 !important

}



#left_column #posts .notification .avatar:hover {

opacity:1 !important

}



#left_column #posts .notification .block,.post_info,.post_info a {

color:#aaa !important

}



#left_column #posts .notification a {

border:none !important;

color:#888 !important

}



#left_column #posts .notification blockquote {

border-left-color:#bbb !important

}



#left_column #posts .notification.alt,#dashboard_switch_blog_menu_footer,#dashboard_switch_blog_menu_wrapper {

background-color:#f7f7f7 !important

}



#left_column #posts .post .post_avatar,#left_column #posts .post .sub_avatar .sub_avatar_inner {

background-color:#b4b78d !important;

border-bottom:none !important

}



#left_column #posts .post,.dashboard_options_form {

border-bottom:none !important

}



#nav .nav_item .nav_menu a {

border-top-color:#eee !important;

color:#888 !important

}



#nav .nav_item .nav_menu a:hover,.no_posts_found,h1.dashboard_header,h1.dashboard_header a,h1.dashboard_header span,#posts .post .footer_links a:hover,#posts .post .post_controls a:hover,#right_column,#right_column a,.dashboard_nav_title,.dashboard_subpages,.dashboard_controls_suggested_blog_title,.dashboard_controls_suggested_blog_description {

color:#fff;

font-style: italic;

}



#nav .nav_item .nav_menu,#dashboard_switch_blog_menu_header {

background-color:#f7f7f7 !important;

border:none !important

}



#nav .nav_item.active,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel,#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

background:#704f3e !important

}



#pagination a {

background: #888 !important;

color: #eee !important

}



#pagination a:hover {

background: #666 !important

}



#posts .post .footer_links a,#posts .post .post_controls a {

color:#bbb !important

}



#right_column .dashboard_nav_item {

background:none !important;

border-bottom:1px solid #aaa !important

}



#right_column a:hover,.new_post_label:hover {

color:#b4b78d !important

}



#show_radar,#hide_radar,#hide_recommended_blogs,#show_recommended_blogs {

background:none !important

}



#auto_pagination_loader {

background: transparent!important;

border: 0px!important;

font-weight: normal;

font-family: Georgia, serif;

font-style: italic;

color: #b4b78d;

}



.dashboard_controls_radar_media {

background:#aaa !important

}



.dashboard_controls_suggested_blog {

border:1px solid #999 !important;

border-width:0 0 1px !important

}



.dashboard_options_form, .dashboard_options_form_disclaimer {

background:#fff !important;

color: #555 !important

}



.dashboard_switch_blog_menu_item {

background-color:#f7f7f7 !important;

border:0 !important

}



.nav_item:hover {

color:#eee !important

}



.new_post_label {

-webkit-text-stroke:0 !important;

color:#aaa !important;

opacity:1 !important;

top: 0px !important;

padding-top: 80px

}



.notification_type_icon {

display:none !important

}



.sub_avatar {

background-color:transparent !important;

height:32px !important;

width:32px !important

}



h1,h2,h3,h4,h5,h6 {

color:#666 !important

}



ol#posts li.post.new_post:hover {

background-position: center bottom

}









.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}



.post.photo_post .photo, .post.photo_post .video_thumbnail {

  -moz-box-shadow: 2px 2px 2px rgba(0,0,0, 0.50) !important;

  border-radius: 12px !important;

}



.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}







.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(255,255,255, .65) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}



.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(255,255,255, 1.0)  !important;

}







.post.conversation.same_user_as_last.is_mine:hover,.post.conversation.same_user_as_last.is_reblog.is_mine:hover,.post.conversation.not_mine:hover,.post.conversation.is_mine:hover,.post.conversation.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.note.same_user_as_last.is_mine:hover,.post.note.same_user_as_last.is_reblog.is_mine:hover,.post.note.not_mine:hover,.post.note.is_mine:hover,.post.note.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.regular.same_user_as_last.is_mine:hover,.post.regular.same_user_as_last.is_reblog.is_mine:hover,.post.regular.not_mine:hover,.post.regular.is_mine:hover,.post.regular.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.link.same_user_as_last.is_mine:hover,.post.link.same_user_as_last.is_reblog.is_mine:hover,.post.link.not_mine:hover,.post.link.is_mine:hover,.post.link.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.quote.same_user_as_last.is_mine:hover,.post.quote.same_user_as_last.is_reblog.is_mine:hover,.post.quote.not_mine:hover,.post.quote.is_mine:hover,.post.quote.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.audio.same_user_as_last.is_mine:hover,.post.audio.same_user_as_last.is_reblog.is_mine:hover,.post.audio.not_mine:hover,.post.audio.is_mine:hover,.post.audio.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.video.same_user_as_last.is_mine:hover,.post.video.same_user_as_last.is_reblog.is_mine:hover,.post.video.not_mine:hover,.post.video.is_mine:hover,.post.video.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}





.post.photo.same_user_as_last.is_mine:hover,.post.photo.same_user_as_last.is_reblog.is_mine:hover,.post.photo.not_mine:hover,.post.photo.is_mine:hover,.post.photo.is_reblog.is_mine:hover {

  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;

}







.permalink {

  background: none !important;

  width: 16px !important;

  height: 16px !important;

  background: rgba(0,0,0, .85) !important;

  border-bottom-left-radius: 18px !important;

  border-top-right-radius: 24px !important;

  margin-top: 6px !important;

  margin-right: 6px !important;

}



.permalink:hover {background: rgba(0,0,0, 1.0 ) !important;}







.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, .selected .tag, #post_two, #categories A:last-child {

   border-bottom-left-radius: 6px !important;

   border-bottom-right-radius: 6px !important;

}



.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, #categories A:first-child, .selected .following, .following, .selected .posts, #left_column DIV:nth-child(9) {

   border-top-left-radius: 6px !important; 

   border-top-right-radius: 6px !important;

}





.nipple {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  border-right: solid 6px rgba(255,255,255, 1.00) !important;

  border-bottom: 6px solid transparent !important;

  border-left: 0px solid transparent !important;

  border-top: 6px solid transparent !important;

  margin-top: 0px !important;

}



.nipple.border {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  border-right: solid 6px rgba(205,123,184, .50) !important;

  border-bottom: 6px solid transparent !important;

  border-left: 1px solid transparent !important;

  border-top: 6px solid transparent !important;

  margin-top: 1px !important;

}



#posts .avatar_frame, .notification.single_notification {

  border: 1px rgba(205,123,184, 1.0) solid !important;

}







#right_column .controls_section li .tag, #right_column .controls_section li .count, #right_column .controls_section li .likes, #right_column .controls_section li .following, #right_column .controls_section li .posts, #right_column .controls_section li .followers, #right_column .controls_section li .messages, #right_column .controls_section li .drafts, #right_column .controls_section li .queue, #right_column .controls_section li .settings {color:#dedede !important}

 

li.selected, li.recessed, a.active, div.post_filter_tabs a {background: none !important}







}









@-moz-document url-prefix('http://www.tumblr.com/explore'), url-prefix('https://www.tumblr.com/explore')   {

   #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {    padding: 40px 0;  }

}





