css:
@-moz-document domain(tumblr.com) {
body {
background: url('http://i.imgur.com/861pn.png') repeat fixed !important;
}

body {
font: normal 14px/1.4 "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
color: #8B8A8A;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(21, 21, 22, 0.02);
padding: 20px;
-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;
}

#posts .post {
clear: both;
position: relative;
margin: 20px 0 0 0;
padding: 15px 20px 15px 20px;
background-color: rgba(255, 255, 255, 0.03);
border-bottom: solid 2px rgba(187, 187, 187, 0);
word-wrap: break-word;
-webkit-border-radius: 10px;
-moz-border-radius: 10px;
border-radius: 10px;
}

#posts .post.new_post .new_post_label {
font-weight: normal;
text-align: center;
color: rgba(245, 227, 135, 0.72);
font-size: 15px;
}

#posts .notification.alt {
background-color: rgba(87, 86, 85, 0.41);
}

#posts .notification {
width: 464px;
color: rgba(255, 255, 255, .45);
font-family: "Lucida Grande","Helvetica Neue",Helvetica,Arial,sans-serif;
font-size: 11px;
line-height: 19px;
background-color: rgba(65, 66, 66, 0.78);
border-left: solid 1px rgba(8, 11, 14, 0);
border-right: solid 1px rgba(42, 63, 86, 0);
border-bottom: solid 1px rgba(238, 232, 86, 0.49);
-webkit-background-clip: padding-box;
margin: 0 0 0 -1px;
padding: 7px 65px 7px 11px;
position: relative;
}

#posts .notification.single_notification {
clear: both;
border: solid 1px rgba(99, 99, 99, 0);
margin-top: 20px;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
border-radius: 6px;
}

#posts .notification.first_notification {
clear: both;
border-top: solid 1px rgba(130, 136, 143, 0);
margin-top: 20px;
-webkit-border-top-left-radius: 6px;
-webkit-border-top-right-radius: 6px;
-moz-border-radius-topleft: 6px;
-moz-border-radius-topright: 6px;
border-top-left-radius: 6px;
border-top-right-radius: 6px;
}

#posts .notification .nipple.border {
border-top: 7px solid transparent;
border-bottom: 7px solid transparent;
border-right: 7px solid rgba(42, 63, 86, 0);
position: absolute;
left: -7px;
top: 9px;
}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid transparent;
border-right: 6px solid #4D4E50;
position: absolute;
left: -5px;
top: 10px;
}

#posts .notification.alt .nipple {
border-right: 6px solid #4D4E50;
}

#posts .post .post_info, #posts .post .post_controls {
font-size: 13px;
font-weight: bold;
color: #949494;
line-height: 20px;
}

#posts .post .post_info a {
color: rgba(214, 0, 0, 0.69);
text-decoration: none;
cursor: pointer;
}

#posts .post .permalink {
width: 23px;
height: 23px;
background: url('http://i.imgur.com/aKpM7.png');
position: absolute;
top: -4px;
right: -5px;
display: none;
}

div.l-header .selected .selection_nipple {
display: none;
}

#posts .post .arrow {
width: 12px;
height: 23px;
background: -396px -247px rgba(99, 73, 73, 0);
position: absolute;
left: -11px;
top: 22px;
}

.source_url .source_url_gradient {
background: top left repeat-y transparent!important;
position: absolute;
top: 0;
bottom: 0;
left: 145px;
width: 15px;
}

img.content_top, img#content_top, img#content_bottom {
display: none;
margin: 0 auto;
min-height: 25px;
width: 900px;
}

#posts .post .post_controls .photo_reply_icon .photo_reply_icon_mask {
position: absolute;
top: 0;
left: 0;
width: 20px;
height: 17px;
background: url('http://i.imgur.com/M06L3.png') top left no-repeat transparent;
}

#posts .post .post_controls .reblog_count:hover {
background-color: rgba(221, 221, 217, 0.89);
}

#dashboard_controls a:hover {
color: #ACB3B9;
}

#dashboard_controls a, #dashboard_controls .control {
color: #999;
text-decoration: none;
text-shadow: #313F4E 1px 1px 1px;
}

#footer {
text-align: center;
color: #7F7F80;
font-size: 13px;
}

#footer a {
margin: 0 6px;
color: #5D5F61;
white-space: nowrap;
text-decoration: none;
}

#pagination a:hover {
color: #6E7174;
}

#pagination a {
color: #D0D0D0;
text-decoration: none;
background-color: rgba(92, 116, 141, 0);
padding: 5px 8px;
margin-left: 10px;
white-space: nowrap;
}

#right_column::after {
position: absolute;
top: 100%;
right: 0;
left: 0;
height: 35px;
content: ' ';
background: -moz-linear-gradient(top,rgba(44, 71, 98, 1) 0,rgba(44, 71, 98, 0) 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(44, 71, 98, 1)),color-stop(100%,rgba(44, 71, 98, 0)));
background: -webkit-linear-gradient(top,rgba(44, 71, 98, 0) 0,rgba(44, 71, 98, 0) 100%);
background: -o-linear-gradient(top,rgba(44, 71, 98, 1) 0,rgba(44, 71, 98, 0) 100%);
background: -ms-linear-gradient(top,rgba(44, 71, 98, 1) 0,rgba(44, 71, 98, 0) 100%);
background: linear-gradient(top,rgba(44, 71, 98, 1) 0,rgba(44, 71, 98, 0) 100%);
}

#posts .post .conversation_lines .chat_line:nth-child(even) {
background: #242424;
}

#posts .post .conversation_lines .chat_line {
border-right: 1px solid rgba(226, 228, 231, 0);
border-bottom: 1px solid rgba(226, 228, 231, 0);
border-left: 1px solid rgba(226, 228, 231, 0);
list-style-type: none;
padding: 9px 14px;
}

#logo {
width: 196px;
height: 45px;
background-image: url('http://i.imgur.com/Myv2k.png');
background-repeat: no-repeat;
}
}
