css:
@namespace url(http://www.w3.org/1999/xhtml);

/* RPOL.net Steampunk'd Style */
/* Style by: adrasteia1 */
/* Design by: adrasteia1 using Photoshop brushes under license from Obsidian Dawn (http://www.obsidiandawn.com). */
/* Web: http://www.roleplayersarchive.net */

@-moz-document regexp("https?://((www\.?)?rpol\.net|rpol.hyphenit.com.au).*") {

    body, th, tr, td, a {
           font-family: Verdana, Geneva, sans-serif !important;
    }
    
    body {
           background-color: #f5c884 !important;
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-steampunk/steampunkback5.jpg') !important; 
    }
    
    body, th, tr, td, span.normal_text {
           color: black !important;
    }
  
    table.selection_table th {
           background-color: #d89973 !important;
    }
    
    table.selection_table td {
           background-color: #ffffff !important;
           color: #000000 !important;
    }
    
    td.quote {
           background-color: #ffffe9 !important;
    }
    
    
    a, a:link, a:visited, .link-colour, .msg a:hover:before, .msg a:hover:after {
           color: #913802 !important;
            
    }
    
    a:hover, a:active, .alink-colour, .msg a:before, .msg a:after, a.button_on  {
           color: #FF3333 !important;
    }
 
    .border {
	   border-color: #d89973 !important;
    }
    
    .info_table {		
	   border: 1px solid #d89973 !important;
    }
	
    .info_table th {
	   background-color: #d89973 !important;
	   color: #000000 !important;
    }
	
    .info_table td {
	   background-color: #ffffe9 !important;
	   color: #000000 !important;
    }
	
    table.info_box {
	   background-color: #fdfec5 !important;
	   color: #000000 !important;
    }
	
    .info_box {
	   border: 1px solid #d89973 !important;
	   background-color: #fdfec5 !important;
	   color: #000000 !important;
    }
	
    table.info_table.round5 td tr {
	   background-color: #fdfec5 !important;
    }
    
    table.info_table td {
           background-color: #fdfec5 !important;
    }
    
    table.info_table tr {
	   background-color: #fdfec5 !important;
    }

    table.info_table th {
           background-color: #d89973 !important;    
    }

    table.message_table td {
           background-color: #fdfec5 !important;
    }

    table.message_table tr {
           background-color: #fdfec5 !important;
    }

    table.message_table th { 
           background-color: #d89973 !important;    
    } 
     
    #header {
           border: 2px solid #d89973 !important;
           background-color: #eac689 !important;
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-steampunk/swirlpng.png') !important;
           background-repeat: no-repeat !important;
           background-position: center !important;
    }
     
    #footer {
           border: 2px solid #d89973 !important;
           background-color: #eac689 !important;
    }
   
    .red, .red th, .red td {
           color: #FF3333 !important;
    }
    
    .blue, .blue th, .blue td {
           color: #26a0c3 !important;
    }
    
    span.alink-colour {
           background: none !important;
    }
    
    td.sm.left {
           background-color: transparent !important;
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-steampunk/coatofa.png') !important;
           background-repeat: no-repeat !important;
           background-position: center !important;
    }
    
    td.sm.right {
           background-color: transparent !important;
    }
    
    td.left.message {
           background-color: #ffffff !important;
           color: #000000 !important;
    }
    
    td.left.message_alt {
           background-color: #ffffff !important;
           color: #000000 !important;
    }
    
    table.info_table td.left {
           background-color: #ffffff !important;
           color: #000000 !important;
    }
    
    table.info_table {
           border: none !important;
    }
    
    td.right.sm.alink-colour {
           background: none !important;
    }
    
    td.sm.alink-colour { 
           background-image: none !important;
    }
      
    #header a:hover, #header a:active, a.button_on {
           background-color: #f7f4a1 !important;
           border-color: #f5f187 !important;
    }

    #footer a:hover, #footer a:active {
           background-color: #f7f4a1 !important;
           border-color: #f5f187 !important;
    }
	
    #wrapper:after {
           content:"Style by adrasteia1. Using Photoshop brushes under license from Obsidian Dawn - http://www.obsidiandawn.com" !important;
           font-size: 9px !important;
           float: right !important;
           width: 600px !important;
    }   
    
    .stickyblock {
           background-color: #fdfec5 !important;
           color: #000000 !important;
    }
    
    td#stickylist.stickylist {
           background-color: #ffffff !important;
    }
      
    table#composetable {
           background-color: #fdfec5 !important;
           color: #000000 !important;
    }

    div#composediv.niftybox {
           background-color: #ffffff !important;
           color: #000000 !important;
    }   
    
    .niftybox {
           background-color: #ffffff !important;
           color: #000000 !important;
    } 

    .niftybox td.smc.sm.left.label_nowrap {
           background-image: none !important;
    }
    
    #form-menu-container.right #form-menu.message a {
           background-color: #ffffff !important;
    }
    
    fieldset legend {
           color: #000000 !important;
    }
}
