css:
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

.blog.has_follow_button:before,
#right_column:after {display: none}
	#dashboard_index,#dashboard_inbox,#dashboard_edit_post,#dashboard_submissions,#dashboard_drafts,#dashboard_post_queue,#dashboard_likes,#dashboard_followers,#dashboard_following,#dashboard_directory,#dashboard_lookup_contacts,#main_doc_page,#dashboard_settings,#dashboard_create_tumblelog,#dashboard_members,#main_tag,#main_meetups,#main_about,#main_explore,#dashboard_goodies,#main_developers,#main_themes,#dashboard_manage_themes,#main_edit_theme,#main_jobs,#main_policy,#dashboard_tumblelog_settings {
		background:#f9e8eb url(http://codylongomusic.com/wp-content/gallery/cody/dsc7101.jpg) no-repeat bottom left fixed! important;
		background-size: cover !important;
	}
	#dashboard_index div.l-content,#dashboard_inbox div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_followers div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
		background:rgba(0,0,0,0.1) !important
	}
	.selection_nipple {
		border-bottom-color:rgba(0,0,0,0.1) !important
	}
	.permalink {
		background:none !important
	}
	.avatar_frame {
		background-color:rgba(255,255,255,0.1) !important;
		border-color:rgba(255,255,255,0.1) !important
	}
	.notification {
		background:rgba(0,0,0,0.1) !important;
		border:rgba(255,255,255,0.1) !important;
		background-image:none !important;
		margin-bottom:1px !important
	}
	.notification a {
		border-bottom:1px solid rgba(0,0,0,0.1) !important
	}
	.notification a:hover {
		border-bottom:1px solid rgba(0,0,0,0.3) !important
	}
	.notification_type_icon {
		display:none !important
	}
	.nipple {
		border-right-color:rgba(0,0,0,0.07) !important
	}
	.controls_section li {
		background:rgba(0,0,0,0.1) !important
	}
	.with_subtitle small {
		color:rgba(0,0,0,0.5) !important
	}
	.recessed a {
		color:rgba(255,255,255,0.7) !important
	}
	.radar {
		display:none !important
	}
	* {
		text-shadow:none !important
	}
	h1,.title {
		color:rgba(0,0,0,0.6) !important
	}
	.no_posts_found,h1.dashboard_header {
		color:rgba(255,255,255,0.9) !important
	}
	.dashboard_options_form {
		background:rgba(0,0,0,0.1) !important;
		border:none !important;
		color:rgba(255,255,255,0.7) !important
	}
	.dashboard_options_form_disclaimer,.dashboard_options_form_disclaimer a {
		color:rgba(255,255,255,0.9) !important
	}
	.blue_note,.blue_note a {
		background:rgba(0,0,0,0.2) !important;
		color:rgba(0,0,0,0.6) !important
	}
	#footer,#footer a {
		border:none !important;
		color:rgba(0,0,0,0.5) !important
	}
	img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"] {
		opacity:0;
		visibility:hidden
	}
	.new_post {
    	background: #fff url(http://25.media.tumblr.com/tumblr_m5xz80sQGg1rvzsmuo1_500.jpg) repeat-x center 15% !important;
	}
	.new_post_label {
		-webkit-text-stroke:0 !important;
		color:rgba(0,0,0,0.4) !important;
		opacity:1 !important;
		top:0px !important;
		padding-top:80px
	}
	.new_post_label:hover {
		color:rgba(0,0,0,0.7) !important
	}
	#pagination a {
		background:rgba(0,0,0,0.4) !important;
		color:rgba(255,255,255,0.7) !important
	}
	#pagination a:hover {
		color:rgba(255,255,255,0.9) !important
	}
	#dashboard_controls a,#dashboard_controls .control {
		color:rgba(255,255,255,0.7) !important
	}
	.dashboard_controls_arrow_first_page,.dashboard_controls_arrow_left,.dashboard_controls_arrow_right {
		display: none !important
	}

	#logo {width: 0 !important; height: 0 !important}
	.logo_anchor {text-decoration: none !important;}
	.logo_anchor:before {
		content: "oh,cody.";
		font-family: "Bookman", "Bookman Old Style", "Georgia", "Times New Roman", serif; 
		font-size: 42pt;
		font-weight: bold;
		line-height: 48pt;
		color: white;
		font-size-adjust: 0.45;
	}
}
