css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  {

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    opacity: 0;
    visibility: hidden; }


.new_post_label_icon { display: none !important; }

.new_post_label { padding-top: 80px !important; color: rgba(255,255,255,0.7) !important; font: italic 19px georgia, serif !important; text-transform: lowercase !important;
letter-spacing:1px !important; text-shadow: #fff 1px 0px 3px !important; }

.new_post_label:hover { color: rgba(255,255,255,1) !important; }

#new_post {
background: #fff url(http://i46.tinypic.com/jf9co2.png) no-repeat center bottom; padding: 10px important!; height: 100px !important; }

#new_post .arrow {
display: none }
}
