/*
 * Engadget CleanLook
 *
 * Author      : Amitava K. Saha
 * Date        : June 06, 2008
 * Description : A clean look engadget.com. Removes ads, center aligned
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("engadget.com") {
#page {
background-color: transparent !important;
}
body {
background-color: #4b4b4b !important;
font-family:"Trebuchet MS", Arial, Helvetica, sans-serif !important;
}

#logo {
background: url("data:image/png;base64,") no-repeat !important;
top: 0px !important;
}

#header {
margin: auto !important;
width: 798px !important;
background-color: transparent !important;
}

.searchform {
background: none !important;
}
#hat, .cobrand, #subcontent, #footer, #grid, .footer-cobrand, .instructions, .topleader, .bottomleader, #slice, .inpostad, #FLASH_AD, .quigoads {
display: none !important;
width: 0px !important;
height: 0px !important;
}

#content_wrap {
text-align: center !important;
}

#content {
margin: auto !important;
text-align: left !important;
width: 778px !important;
padding: 10px !important;
color: #666666 !important;
font-size:11px !important;
}

}
