css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("plus.google.com"){

/* created by cordalace@gmail.com */
/* https://bitbucket.org/cordalace/wide_kroogle */
/* http://userstyles.org/styles/77895/wide-kroogle */

/* Left panel */
.LPb {width: 100px !important;}
.J9, .I9 {margin-left: 100px !important;}

/* wide width */
/* basic */
.J9, .K9 {margin-right: 0px !important;}
.bR, .FE, .l8, .Sb, .kI, .Fm, .Je, .Vra, .ez, .oX, .Rm {width: auto !important;}
/* wide inactive "share" field */
.Ff {width: 100% !important;}

/* profile picture and info */
.qga {margin-bottom: 210px !important;}
.CZb .qga .Vka {margin-top: -195px !important;}
.Oka, .Vka .l-ul {position: absolute !important; left: 280px !important;}
.Oka {top: 48px !important;
      border-radius: 3px !important;
      border-top: 1px solid #CCCCCC !important;}
.Vka .l-ul {top: 168px !important; min-width: 225px !important;}
.Vka .l-ul div {margin-bottom: 0px !important; margin-top: 2px !important;}
.yta {display: none !important;}

/* hide header shadow */
.Uda, .wl {display:none !important;}

/* youtube video: centered and fixed size */
.bva .dv iframe {margin-left: auto !important;
                 margin-right: auto !important;
                 width: 497px !important;
                 height: 372px !important;}

.hxa {display:none !important;}
.lp {border-bottom:1px dotted #ccc !important;
     padding-top:2px !important;}

/* black (not grey) comments text color */
.gE {color: #000 !important;}
.Mi {color: #000 !important;}

/* justified comments text */
.Mi, .wm, .yF {text-align: justify !important;}

.Ae {border-bottom: 1px dotted #CCC;}
.Ag {background-color: #fff !important;}

/* Hide chat (right panel)*/
.Hoa {display:none !important;}

.Yj, .Fg, .FB {max-width: none !important;}

/* content description: centered */
.ru, .dg {width: 671px !important;
          margin-left: auto !important;
          margin-right: auto !important;}

.Mm {text-align: center !important;}
.Yj, .Fg {max-width: none !important;}
.Ws {width: auto !important;}
}
