css:
/*
 * IMDB New Style & Clean Version V 1.01
 *
 * Author      : Amitava K. Saha
 * Date        : June 07, 2008
 * Description : A beautiful clean version of IMDB.com. Remove ads and centered layout
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {
#wrapper > div[align="center"],  table[width="115"], iframe, #swf_990x90,  #footer, .boxSides, #tn15bot, #swf_superrectangle, #swf_990x90_noCR_V, .tvgrid, .dvd_buckets_rhs_ad, .boxbottom, #tn15adrhs, #floatingunit, #swf_supertab_uservideo, #swf_navbarbamf, td[width="171"]{
display: none !important;
}

#nb15supertab {
top: -110px !important;
position: relative !important;
height: 40px !important;
}

#root {
position: relative !important;
text-align: left !important;
margin-right: 10px !important;
}

#wrapper {
margin: auto !important;
text-align: center !important;
width: 1000px !important;
background-color: #fcf8ec !important;
}

.outer_body > tbody > tr >td {
background-color: #fcf8ec !important;
}

body {
background-color: #a58b5c !important;
margin: 0 !important;
background-image: none !important;
}

* {
font-family:"Trebuchet MS", Arial, Helvetica, sans-serif !important;
}

table, p, .info {
font-size: 12px !important;
color: #333333 !important;
border-color: #d6d3d3 !important;
}

a{
color: #f85702 !important;
}

h2, .dvd_bucket_summary {
font-size: 12px !important;
}

div.hpLeftNav,  .lhscol, #tn15lhs {
background-image: url("data:image/jpeg;base64,") !important;
background-repeat: repeat !important;
}

#nb15sub {
width: 550px !important;
font-size: 12px !important;
}

p > a > img, form > a > img {
padding: 5px 5px 0 0 !important;
}

td[bgcolor="#dfdfdf"]{
background-color: #f8f7f6 !important;
}

.hpLeftNav h2 {
background-image: none !important;
background-repeat: no-repeat !important;
background-color: #cdb68b !important;
}

.lhscol {
border-top: 0px !important;
}

td[width="120"], div[align="right"], td[bgcolor="#999999"], hr, #prflsh9994BBCBDAA94E97896D761D62DBAEA2, table[width="300"], table[width="300px"], div[z-index="999998"], table[id="prt9994BBCBDAA94E97896D761D62DBAEA2"], #prflsh8D49180ED9B24D0BBF3C0A72EE4DC8FB, #swf_SummerBlockbuster_300x250_Temp, #prtrans {
display: none !important;
width: 0px !important;
}

#root {
padding-left: 10px !important;
}

.hpLeftNav {
margin-left: -10px !important;
}

#nb15botbg {
margin-left: -10px !important;
width: 1000px !important;
}

td[bgcolor="#dfdfdf"]{
background-color: #fcf8ec !important;
}

#tn15 {
margin-left: 0px !important;
margin-right: 0px !important;
}

.cast {
width: 700px !important;
left: -100px !important;
position: relative !important;
}

#tn15lhs a.link.selected {
background-image: none !important;
color: #003333 !important;
}

.dvd_bucket, .dvd_bucket_content {
width: 400px !important;
}

#moviemeter {
width: 400px !important;
}

tbody > tr > td {

}
}
