css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("promodj.com") {
/*Копирайт*/
.dj_menu_rest:after 
 { content: "PDJ MOD"!important; font-weight:bold!important; text-align:right!important; color: #999999!important; font-size: 11px !important}

/*Блокировка рекламы*/
#atlas_240x400,.adv240x400 object, .adv240x400 embed, .adv800x200, #featured_afisha_3, .market_offer_featured, .adv240x400 iframe {display:none !important }
.adv240x400 {min-height: 0px !important}
#flashbranding,#branding_click,#feat_dj_content_block {display:none !important }

/* "Премиумы двоятся" */
div.who_was_there, .comment_header_best, dj_universal p, .comment_header, a.premium:after, a.premium {line-height: normal !important;}

/*Цвет "ИНКОГНИТО"*/
.incognito { color: #5d5d5d!important; font-weight:bold!important;}


/*Шапка*/

div.mainmenu {
    background-color: #C6190E!important;
    height: 29px!important;
    left: 0!important;
    overflow: hidden!important;
    padding: 4px 4px 0!important;
    position: fixed!important;
    top: 0!important;
    width: 100%!important;
    z-index: 201!important;
    border-radius: 0px 0px 0px 0px!important;
    box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.5)!important;
}
div.escho_container {
    background-color: #FFFFFF!important;
    border: 1px solid #C6190E!important;
    min-width: 80px!important;
    right: 0!important;
    top: 29px!important;
    position: fixed!important;
    z-index: 501!important;
}
.content_twice_inner {
background: none repeat scroll 0 0 transparent!important;
padding-top: 29px!important;
}
#header_pre_container {padding-top: 30px!important;}



}
