css:
@-moz-document domain("boards.foolz.us"), domain("archive.moe"), domain("archive.thedarkcave.org"), domain("beta.foolz.us") {
 ::-moz-selection { background-color:#757575 !important; }
 ::selection { background-color:#757575 !important; }

 .letters {
    background: black !important;
    color: #C5C8C6 !important;
}

body {
    color: #C5C8C6 !important;
}

 .letters a {
    color: #C5C8C6 !important;
}

 .letters a:visited {
    color: #C5C8C6 !important;
}

 .search_box {
    color: black;
}
 .navbar .nav > li > a {
    color: #C5C8C6 !important;
}

 .container {
    background: black !important;
    color: #C5C8C6 !important;
}

html {
    background: black !important;
}

 .thread_form_wrap {
    background: #282A2E !important;
    border-color: black !important;
}

 .rules {
    color: #C5C8C6 !important;
}

 a,a:visited {
    color: #81A2BE !important;
}
 a:hover {
    color: #5F89AC !important;
}


 .post_author, .post_tripcode {
    color: #C5C8C6 !important;
}

 .post_poster_data a, .post_poster_data a * {
    color: #81A2BE !important;
}

 .post_poster_data:hover a, .post_poster_data:hover a * {
    color: #5F89AC !important;
}

 #reply_ajax_notices.error {
    color: #364041 !important;
}

 .container-fluid {
    background: #1D1F21 !important;
}

 .post_wrapper {
    background: #282A2E !important;
    border-bottom: 1px solid #282A2E !important;
    border-radius: 5px !important;
}

 .text {
    color: #C5C8C6 !important;
}

 .omitted_text {
    color: #C5C8C6 !important;
}

 article.thread > .thread_image_box {
    color: #C5C8C6 !important;
    text-shadow: none !important;
}

 .post_data {
    color: #C5C8C6 !important;
}

 h2.post_title {
    color: #B294BB !important;
}

 .post_file_controls a {
    color: #C5C8C6 !important;
}
 .greentext {
    color: #B5BD68 !important;
}

 .post.post_ghost .post_wrapper {
    border-left: 5px solid black !important;
    border-right: 1px solid black !important;
    border-top: 1px solid black !important;
    border-bottom: 1px solid black !important;
}

 .js_hook_realtimethread {
    color: #C5C8C6 !important;
    border-color: black !important;
}

 #footer {
    background: black !important;
    color: #C5C8C6 !important;
}

 .btn-group {
    color: black !important;
}

 .btn-inverse {
	background: #4C5057 !important;
}

 #footer a, a.visited {
    color: #C5C8C6 !important;
}

 
.post.highlight .post_wrapper {
	background: #1D1D21 !important;
    border: 1px solid #111111 !important;
}

 #reply_ajax_notices.success {
    background: #484A4E !important;
    border-color: #121315 !important;
    color: #C5C8C6 !important;
}

 .paginate {
    background: #282A2E !important;
}

 article.thread .spoiler .greentext {
    color: black !important;
}

 article.thread .spoiler .greentext:hover {
    color: #B5BD68 !important;
}
 .paginate a {
    color: black !important;
}

 .dropdown-menu a {
    color: black !important;
}

 h2,
 h3 a,
 h3 a:visited {
    color: #C5C8C6 !important;
}

 #main h3.section_title {
    color: #C5C8C6 !important;
}

 .table-striped tbody tr:nth-child(2n+1) td, 
.table-striped tbody tr:nth-child(2n+1) th {
    background-color: #282A2E !important;
}

img {
    background-color: #C5C8C6 !important;
}

 #footer ul a, 
a.visited {
    color: black !important;
}

.thread_image_box img {
    background-color: transparent !important;
}

 #thread_o_matic > article.thread {
    border-color: black !important;
}

 #main .section_title {
    border-color: black !important;
}
}
