@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(tumblr.com) {


#logo {height: 0 !important;

        width: 0 !important;

	padding-left: 240px !important;

	padding-top: 70px !important;

	background: url('http://media.tumblr.com/tumblr_md4pteSguR1r36ohm.png') no-repeat !important;}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, #dashboard_members{background-color:#000000;background-image:url('http://25.media.tumblr.com/tumblr_md4fmlGXzN1r6ksv2o1_1280.jpg') !important;background-attachment:fixed !important;background-repeat:no-repeat !important;-webkit-background-size: cover;
	-moz-background-size: cover;
	-o-background-size: cover;
	background-size: cover;}#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content,#dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(161,0,0,0.2) !important;}#dashboard_edit_post{background-color: #000000 !important}#right_column .dashboard_nav_item {border-bottom:0px solid #eee !important;background-image:none  !important}#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a,#right_column .dashboard_nav_item a,#right_column .dashboard_nav_item ul.dashboard_subpages,#right_column .dashboard_nav_item ul.dashboard_subpages li a {color:#FFF !important;}#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {color:#888 !important ;}#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {background: none !important;}#left_column 


/*bunch of posts*/

#posts .notification .avatar {border-color:#000000 !important;background-color:#a10000 !important;opacity:0.9 !important;-o-transition: opacity 1s linear; -webkit-transition: opacity .8s linear; -moz-transition: opacity 1.5s linear;}#left_column 

#posts .notification .avatar_frame {
/*BACKGROUND AND BORDER FOR THE ICONS NEXT TO NOTIFICATIONS
NOT REALLY THAT NOTICEABLE UNLESS THE PAGE LAGS A BIT*/

  background-color:#a10000 !important;

  border: 1px solid #000000 !important;
}

#posts .notification .nipple {

/*THE LITTLE ARROW TO THE LEFT OF NOTIFICATIONS*/

  border-top: 6px solid transparent !important;

  border-bottom: 6px solid transparent !important;

  border-right: 6px solid rgba(110, 0, 0, 0.2) !important;
}

div.l-header .selection_nipple {

/*THE LITTLE TRIANGLE AT THE TOP OF THE CONTENT BLOCK*/

  border-bottom: 12px solid rgba(110, 0, 0, .5) !important;
}


#posts .notification {

/*NOTIFICATIONS*/

  background-color: #a10000 !important;

  border: 1px solid rgba(161, 0, 0, .5) !important;
}

#posts .notification a:not(.avatar_frame) {

/*THE TEXT ITSELF IN NOTIFICATIONS*/

  color: rgba(255, 255, 255, .56) !important;
  font-weight: bold;
  text-decoration: none;
  border-bottom: 1px solid rgba(220, 103, 139, 0.9) !important;  /*THIS IS THE UNDERLINE*/
}


#posts .notification .avatar:hover{opacity:100 !important;}.dashboard_switch_blog_menu_header{background-color:#000000 !important;color:#000 !important;}.dashboard_switch_blog_menu_item

{background-color:#FFFFFF !important;color:#000 !important;border:0px !important}.dashboard_switch_blog_menu_footer{background-color:#ffffff !important;color:#000 !important;}.dashboard_switch_blog_menu_wrapper{background-color:#000000 !important;}#new_post_notice_container{color:#fff!important}#new_post_notice_container a

#logo {height: 0 !important;
	width: 0 !important;
	padding-left: 210px !important;
	padding-top: 49px !important;
	background: url(http://i1105.photobucket.com/albums/h359/Nathanthenerd2/BADWOLF-2.png) no-repeat !important;}

.new_post_label{
 color: rgba(0, 0, 0, .9) !important;
}

.new_post_label_icon{background-image:none !important}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
#new_post{
background: url(http://25.media.tumblr.com/tumblr_md4kmxUMwz1r6ksv2o1_500.png) center no-repeat !important;
background-color:#fff !important;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 1;
}




#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://media.tumblr.com/tumblr_md4jf03QMW1r36ohm.png') !important;}


}
