/* Created by AxyDavid*/

@namespace url(http://www.w3.org/1999/xhtml);
#yt-masthead-container {
background: transparent !important;
border-bottom: 0px solid rgb(232, 232, 232)!important;
}
guide-module-toggle, .guide-module-toggle{
visibility:hidden !important;
}
#yt-masthead-user-displayname, #yt-masthead-dropdown {
position:absolute !important;
visibility:hidden !important;
}

#watch7-action-buttons {
    padding: 0px 18px;
    border-width: 1px 1px 1px !important;
    border-style: solid;
    border-image: none;
}
#watch7-secondary-actions .yt-uix-button.yt-uix-button-toggled.yt-uix-tooltip {
    border-bottom-color: white !important;
 border-color: white !important;
 border-width: 0px 0px;


}
.yt-uixs-button {

    padding: 0px 0.0em !important;


}
#watch7-headline{
	 position: relative; 
	top: -437px !important;
	left: -20px;
	height: 0px !important;
background: transparent !important;
    border-width: 0px !important;
 }
 #player, .site-left-aligned.guide-enabled #watch7-main-container {
    padding-left: 10%!important;}
    #player { 	 
    position: relative; 
	top: 20px; 
	height: 410px !important;}
#watch7-user-header, #watch7-action-buttons, #watch7-action-panels{
	position: relative; 
	top: -16px;
	}
	#watch-discussion{
	position: relative; 
	top: -44px;
	#comments-view h4 {
    line-height: 2em;
}
	}
	#yt-masthead-user {
	border: 1px !important;

	position: relative; 
    left: 0px !important;
	border-width: 0px !important;
	border-color: rgb(232, 232, 232) !important;
	border-style: solid !important;
	}

	.yt-masthead-user-icon { 	 
    position: relative; 
    height=90%;
	top: 0px; !important;
	border-style: solid !important;}
#yt-masthead-dropdown, #sb-button-notify {
position:absolute !important;
visibility:hidden !important;
}
.yt-uix-button-default {
    text-shadow:none !important;
    border-color: transparent !important;

    background-color: transparent !important;
    background-image: none !important;
}
.yt-uix-button-text{
    text-shadow: 0px 1px 0px rgba(255, 255, 255, 0.5) !important;
    border-color: white !important;
    background-color: white !important;
    background-image: none !important;
    box-shadow: none !important;
}
#search-btn{
position:absolute !important;
left: 480px !important;
top: -1px !important;
z-index: 30;
}
#masthead-search-term{
position:absolute !important;
}
.yt-uix-button:focus, yt-uix-button, .yt-uix-button:focus:hover, .yt-uix-button-focused, .yt-uix-button-focused:hover {
    box-shadow: 0px 0px 0px rgb(221, 221, 221) !important;
}
.yt-uix-button:focus, .yt-uix-button:focus:hover, .yt-uix-button-focused, .yt-uix-button-focused:hover {
    box-shadow: 0px 0px 0px 0px rgba(27, 127, 204, 0.4);
}
.yt-uix-button {
    box-shadow: none !important;
}
.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
    border-color: rgb(211, 211, 211);
    background: none repeat scroll 0% 0% rgb(248, 248, 248);
    color: rgb(6,69,173) !important;
    
}
#watch7-headline h1 a {
    color: rgb(6,69,173) !important;
}
#footer-container{
	position: relative;  
}
#watch7-main {
    position: relative;
    z-index: 2;
    min-width: 955px;
    margin-top: 0px;
    margin-bottom: 0px !important;
.yt-uix-button {
    border-radius: 0px 0px 0px 0px !important;
    }

.yt-uix-shelfslider-list{
	height:60px;
	width:43px
}
