css:
@-moz-document domain("forums.vogue.com.au") {
* {font-family: inherit !important; font-size: 100% !important; font-style: inherit !important; font-weight: inherit !important;}
body {background-color:#fafafa !important; font-family: 'lucida grande','Lucida Sans Unicode', tahoma, sans-serif! important; font-size: 0.75em !important;} 
h1 {font-family: 'Times New Roman', Garamond, Times, serif !important; font-size:3.5em !important; font-style: italic !important; letter-spacing: -4px !important; text-transform: lowercase !important;}
hr {color:#ccc !important;}
#content-wrapper {margin:0 auto !important; padding:0px !important; width:904px !important;}
#content-1 {background-color:#eaeaea !important; margin:0 !important; padding:0 !important; width:100% !important;}
#content-2, #footer, #fb_tweet_buttons, #header, .heading-2, .module.news-ticker, .shopping {display:none !important;}
#collapseobj_forumhome_activeusers div.large-title div {padding-top:10px !important;}
#collapseobj_forumhome_activeusers div.large-title {margin-bottom:-10px !important;}
#collapseobj_forumhome_stats .smallfont {padding-top:10px !important;}
#collapseobj_similarthreads .alt1, #collapseobj_similarthreads .alt2 {padding:20px 5px 10px !important;}
#forumtools {padding: 0 !important;}
#inlinemodform .pagenav {width:900px !important;}
#nav {display: block !important; margin: 0 auto !important; padding-top:3px !important; width:904px !important;}
#nav-wrap {width:747px !important;}
#nav #nav-level1, #nav #nav-level2, #nav #nav-level3 {font-size:0.8em !important; width:747px !important;}
#network-bar {margin:0 auto !important; width:904px !important;}
#page {margin:0 auto !important;} 
#threadslist .tcat .smallfont {padding-right:10px !important;}
#welcomeMember {padding-bottom:10px !important;}
.forum-content .thread-tools .pagenav {margin-top:15px !important;}
.forum-home .alt1, .forum-home .alt1Active, .forum-home .alt2, .forum-home .alt2Active, #threadslist .alt1, #threadslist .alt1Active, #threadslist .alt2, #threadslist .alt2Active {padding:10px 10px 10px 5px !important;}
.forum-home a {padding-right:10px !important;}
.forum-home-body .headerTitle h1 {background-color:#fff !important; background-image: url('http://www.vogue.com.au/images/logo/logo_vogue.gif') !important; background-position: center 10px !important; margin:0px !important;}
.forum-home-body .date {font-size:0.85em !important;}
.forum-jump {margin: 0 !important; padding-right:235px !important;}
.gridInScrollbar {background-color:#eaeaea !important;}
.info-bar-search {margin:0 !important; padding:0 !important;}
.large-title {color: #333 !important;}
.h5 {margin-left:10px !important;}
.h5 font, .post-body .alt2 a {color:#e12830 !important;}
.headerTitle h1 {padding:10px 10px 10px 20px !important;}
.navWrapper {margin:0; padding-bottom:1px !important;}
.post-body .alt2 {background-color:#fff !important; padding:10px !important;}
.post-container {margin:0 !important;}
.post-container .alt1 {background-color:#fafafa !important;}
.post-controls-top .pagenav {margin:0 !important;padding:0 0 3px !important;width:246px !important;}
.searchforum {margin:0 !important; padding:0 0 10px !important;width:378px !important;}
.section-page-heading {padding-top:10px !important; padding-left:10px !important;}
.sectiontitle {letter-spacing: 1px !important; text-transform: uppercase !important;}
.tcat, .vbmenu_control, .tfoot {background-image:none !important; background-color:#999 !important;}
.tborder, .threadpost, threadpost {background-image:none !important; background-color:#fff !important;}
.thead {background-image:none !important; background-color:#ccc !important; }
.threaduser {background-image:none !important; background-color:#eee !important;}
.tborder, .alt1, .alt1Active, .alt2, .alt2Active, .threaduser {background-color:#fafafa !important;}
.tborder .title {width:300px !important;}
.threaduser {background-color:#eee !important;}
.vbmenu_control {text-transform:uppercase !important;}
}

@-moz-document url-prefix("http://m.forums.vogue.com.au") {
body {background-color: #f2f2f2 !important; font-size:1.1em !important;}
h1 {font-size:1.8em !important; letter-spacing:-0.1em !important;}
.adbanner {display:none !important;}
.forumhome, .forumdisplay, .newthread, .search_forums, .search_results, .showthread, .standard_error {width:750px !important;}
.alt1 {background-color: #fcfcfc !important; width:700px !important;}
.forumhome .page, .forumdisplay .page, .showthread .thread {margin:0 !important;}
.forumhome .page li {background-color: #fcfcfc !important;}
.last-post, .reply-and-pages {font-size:0.75em !important;}
.page-margin .page ul li {background-repeat:repeat-x !important;}
.post-body {font-size:0.9em !important; width:100% !important;}
.thread {margin:0 !important;}
.thread .alt1, .thread .alt2 {background-color: #fcfcfc !important;}
.reply-quote, .report-post, .thread .alt2 .smallfont {font-size:0.8em !important;}
.threadbit, .user-info  {background-color: #fcfcfc !important;}
.threadnav a {color:#e12830 !important;}
}

@-moz-document domain("forums.vogue.com.au") {
@media only screen and (min-resolution: 140dpi) {
body {background-color: #f2f2f2 !important; font-size:1.1em !important;}
h1 {font-size:1.8em !important; letter-spacing:-0.1em !important;}
.adbanner {display:none !important;}
.forumhome, .forumdisplay, .newthread, .search_forums, .search_results, .showthread, .standard_error {width:750px !important;}
.alt1 {background-color: #fcfcfc !important; width:700px !important;}
.forumhome .page, .forumdisplay .page, .showthread .thread {margin:0 !important;}
.forumhome .page li {background-color: #fcfcfc !important;}
.last-post, .reply-and-pages {font-size:0.75em !important;}
.page-margin .page ul li {background-repeat:repeat-x !important;}
.post-body {font-size:0.9em !important; width:100% !important;}
.thread {margin:0 !important;}
.thread .alt1, .thread .alt2 {background-color: #fcfcfc !important;}
.reply-quote, .report-post, .thread .alt2 .smallfont {font-size:0.8em !important;}
.threadbit, .user-info  {background-color: #fcfcfc !important;}
.threadnav a {color:#e12830 !important;}
}
}
