css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

	body, html {
		background: #FCFCFC !important;
		color: #eee !important;
	}
	/*Remove comment to remove Home and Settings options
	#navbotright a[href="//www.4chan.org/"], #navtopright a[href="//www.4chan.org/"], a#settingsWindowLink, a#settingsWindowLinkBot {
	   display: none !important;
    }*/
	/*Remove comment to remove the Next and Previous on the pagelist
	.pagelist > div input, .pagelist > div:first-child, .pagelist > div.prev, .pagelist > div.next {
		display: none !important;
	}*/
	.postingMode, .bf, blockquote embed, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], #recaptcha_logo, #recaptcha_tagline {
		display: none !important;
	}
	.postarea form div {
		border: none !important;
	}
	.postContainer.inline {
        background: none !important;
        border: none !important;
	}
	div.postarea, div.pagelist div.pages, div.pagelist {
		padding: 0 !important;
	}
	pre.prettyprint {
	   background: #F7F7F7 !important;
	}
	span.pln {
	   color: snow !important;
	}
	div.widget-content a {
	   color: 8B0000 !important;
	}
	h1.title, .home-link {
	   color: limegreen !important;
	}
	#Blog1_blog-pager-older-link {
	   color: crimson !important;
	}

	span.atn {
	   color: purple !important;
	}
	span.com {
	   color: #F2D9B5 !important;
	}
	span.kwd, span.tag {
	   color: #99C2F2 !important;
	}
	span.typ {
	   color: plum !important;
	}
	span.pun {
	   color: tan !important;
	}
	span#imgControls > label {
	   color: #C0C0C0 !important;
	}
	.inline {
	   background: none !important;
	   padding: 0 !important;
	   border: none !important;
	}
	span.str {
	   color: #5B9E09 !important;
	}
	span.lit {
	   color: #87EAEB !important;
	}
	span.abbr {
	   color: white !important;
	}
	span.abbr > a {
	   color: #7FBBED !important;
	}
	a.summary.desktop {
	   color: #EFFFFF !important;
	}
	a.hide_thread_button.hidden_thread {
	   color: #808080 !important;
	}
	strong.capcode.capcodeAdmin {
	   color: red !important;
	}
	a.quotelink, a.backlink {
		color: #0094FF !important;
		text-decoration: underline !important;
	}
	#IYE_Options {
		background: #2C2C2C !important;
		border-radius: 8px;
	}
	.menu_button {
		color: #666 !important;
	}
	.focused {
		color: #000 !important;
	}
	#filter-button {
		padding-right: 1px !important;
		color: #ccc !important;
		text-decoration: underline !important;
	}
	span.quote {
		color: #0094FF !important;
	}
	span.name {
		color: #0094FF !important;
	}
	span.postertrip {
		color: lightblue !important;
	}
	sup {
		color: red !important;
		font-size: 7pt !important;
	}
	a.linkification-ext {
		color: steelblue !important;
		text-decoration: underline !important;   
	}
	.fourchan_x div a[href*="//www.4chan.org/news?all#109"] {
	   display: none !important;
	}
	#passlink a, #passlink {
	   color: #666 !important;
    }
	a.replylink {
		color: #6B8E93 !important;
	}
	a.report_button {
		color: #A9A9A9 !important;
	}
	a {
		color: #efefef !important;
	}
	hr {
		display: none !important;
	}
	#recent-images li {
		background-color: transparent !important;
	}
	form {
		margin: 1em;
	}
	.inputtext, textarea {
		width: 295px;
	}
	.rules {
		display: none;
	}
	table.postForm > tbody > tr > td:first-child, .qrHeader {
		background: #444 !important;
		border: 1px solid #666 !important;
		color: #aaa !important;
		font-size: 6pt !important;
	}
	h1, h2, h3, h4 {
		color: black !important;
	}	
	h2 {
	   text-align: center !important;
	}
	dt, .boxcontent, #updater {
		color: #fff !important;
	}
	.menubutton {
		background-image: none !important;
	}
 	#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, .reply, #backwash_tooltip {
		background-color: #CCCCCC !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		border-radius: 8px;
	}
	.post.reply.highlight {
		background-color:#F7F7F7 !important;
		border: 1px solid #E9E6E6 !important;
		color: #eee !important;
	}
	.warning, .capcodeMod .name {
		color: gray !important;
	}
	.nameBlock .capcode, .postertrip, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, span.subject, .nameblock .name, .omittedposts, .abbr {
		font-size: 8pt;
	}
	.capcodeAdmin .name {
		color: #a55 !important;
	}
	span.subject, .warning {
		color: #D92200 !important;
	}
	#hd, small, font[size="1"], font[size="2"], sideArrows, div.navLinks, #navlinks, #option-button, #ft, .boardBanner, img.rotating {
		display: none !important;
	}
	div.fileInfo > a[href*="http://www.google.com"], div.fileInfo > a[href*="http://www.Google.com"] {
	   color: #C0C090 !important;
	}
	div.fileInfo > a[href*="http://www.tineye.com"], div.fileInfo > a[href*="http://Tineye.com"] {
	   color: #C0C0C0 !important;
	}
	div.fileInfo > a[href*="http://iqdb.org"], div.fileInfo > a[href*="http://IQDB.org"] {
	   color: #C0C0D0 !important;
	}
	div.fileInfo {
	   color: #000000 !important;
	}
	.pagelist {
		background: #444 !important;
		border-right: 2px solid #555 !important;
		border-left: 2px solid #555 !important;
		border-bottom: 1px solid #555 !important;
	}
	.pagelist > div {
		font-size: 8pt;
	}
	.pagelist > div a, .pagelist > div strong, .content > p {
		color: #eee !important;
	}
	.pagelist > div strong {
		background-color: #111;
		padding-left: 1px !important;
		padding-right: 1px !important;
	}
	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt ! important;
		color: #fff !important;
		background: #CCCCCC !important;
		border-color: white !important;
		border: 1px solid;
	}
	#filter-container .yuimenuitem-selected {
		border-color: white !important;
	}
	.boxbar, #preview-tooltip-nws h2, #preview-tooltip-ws h2, #disclaimer-dialog h2, .hd {
		background-color: transparent !important;
	}
	dd {
		color: #fff !important;
		border-bottom: 1px white solid !important;
	}
	input[type="submit"], input[type="button"], select {
		background: #444 !important;
		color: #aaa !important;
		border: 1px solid white;
		-moz-appearance: none;
		-webkit-appearance: none;
		font-size: 9pt !important;
	}
	input[type="checkbox"] {
		-moz-appearance: none !important;
		-webkit-appearance: none !important;
		background: rgba(0,0,0,.4) !important;
		border: 1px solid rgba(255,255,255,.3) !important;
		height: 10px !important;
		width: 10px !important;
	}
	.reply input[type="checkbox"] {
		border: 1px solid rgba(255,255,255,.3) !important;
	}
	input[type="checkbox"]:checked {
		background: rgba(255,255,255,.4) !important;
	}
	div[style="width: 100%; font-size: 4px; background-color: rgba(224,64,0,1);"] {
		display: none !important;
	}
	th {
		background: #222 !important;
	}
	th font {
		font-size: 7pt;
	}
	td.deletebuttons {
		font-size: 8pt !important;
		color:#eee !important;
	}
	img#recaptcha_reload,
	img.refreshBtn {
		height: 0px !important;
		padding-top: 18px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
	    opacity: 0.8;
	}
	img#recaptcha_switch_audio {
		height: 0px !important;
		padding-top: 15px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
		opacity: 0.8;
	}
	img#recaptcha_whatsthis {
		height: 0px !important;
		padding-top: 16px !important;
		width: 25px !important;
		background-image: url(data:image/png;base64,) !important;
		opacity: 0.8;
	}
	.pagelist .pages {
		padding: 1px 1px;
	}	
	.new {
		background: rgba(255,255,255,.4) !important;
		border-radius: 5px;
	}
	.postarea, td.recaptcha_image_cell, #quickrep td.recaptcha_image_cell,  td[style="padding-left: 7px;"] {
		padding: 0 !important;
	}
	.recaptchatable #recaptcha_response_field, #quick_recaptcha_response_field {
		margin-top: 3px !important;
	}
	.recaptcha_input_area {
		padding: 3px 0 0 0 !important;
	}
	tr[height="73"], tr[height="73"] td {
		height: 30px !important;
	}
}
@-moz-document domain("dis.4chan.org") {
	.thread, .newthread, .head {
		background-color:#CCCCCC !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		border-radius: 8px;
	}
	.border, .hborder {
		background: none !important;
		border: none !important;
	}
}
