css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("digidragon.net")

{

/******************* MAIN ********************/

body { background-image: url("http://theedmontonian.com/wp-content/uploads/2010/08/DSC_3825.jpg") !important;
background-position: center center !important;
background-repeat: repeat !important;
margin-bottom: 1em !important;
#800000 !important;
color: #fff !important;
}

strong { background-color: #000000 !important; color: #00FFFF !important; opacity:0.90;
filter:alpha(opacity=75); } 

.smalltext { background-color: #000000 !important; color: ##00FFFF !important; opacity:0.90;
filter:alpha(opacity=75); }

.trow1 { background-color: #000000 !important; color: #00FFFF !important; opacity:0.90;
filter:alpha(opacity=75); }

.trow2 { background-color: #000000 !important; color: #00FFFF !important; opacity:0.90;
filter:alpha(opacity=75); }

.tcat { background-color: #000000 !important; color: #00FFFF !important; opacity:0.90;
filter:alpha(opacity=75); }

.thead { background-color: #000000 !important; color: #00FFFF !important;  opacity:0.90;
filter:alpha(opacity=75); }

#panel { background-color: #000000 !important; color: #00FFFF !important; opacity:0.99;
filter:alpha(opacity=99); }

#content { background-color: #000000 !important; color: #00FFFF !important; opacity:0.75;
filter:alpha(opacity=75); }

#container { background-color: #000000 !important; color: #00FFFF !important; opacity:0.75;
filter:alpha(opacity=75); }

script { background-color: #000000 !important; color: #00FFFF !important; opacity:0.75;
filter:alpha(opacity=75); } 

a:link, a:active {
color: #fff !important;
}

a:visited {
color: #fff !important;
}

a:hover {
color: lightgrey !important;
}

td { background-color: #000000 !important; color: #00FFFF !important; }

span { background-color: #000000 !important; color: #00FFFF !important; opacity:1.0;
filter:alpha(opacity=100); }

table { background-color: #000000 !important; color: #00FFFF !important; }

