css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("telegrafist.org") {

    @media screen{

        div.entry_content:not(div#touchcarousel){
        column-count: auto;
        column-width: 270px;
        column-gap: 2em;
        column-rule: 1px dashed black;
            -moz-column-count: auto;
            -moz-column-width: 270px;
            -moz-column-gap: 2em;
            -moz-column-rule: 1px dashed black;
            -webkit-column-count: auto;
            -webkit-column-width:270px;
            -webkit-column-gap: 2em;
            -webkit-column-rule: 1px dashed black;
        text-align: justify;
        }

        div.mcolumn-pad{
        padding: 15px !important;
        }
     
        img.image-main-style, iframe{
        width: 100% !important;
        height: auto !important;
        margin-left: 0px !important;
        padding-bottom: 0px !important;
        padding-right: 0px !important;
        }
    }

    @media print{
        @page{
            margin: 1cm;
        }
    
        img.image-main-style{
        width: 100% !important;
        margin-left: 0px !important;
        padding-bottom: 0px !important;
        padding-right: 0px !important;
        }
    
        *:not(img){
            background-color: rgb(255, 255, 255) !important;
            color: black !important;
            font-family: serif !important;
            width: 100% !important;
            vertical-align: middle;
            font-size: 12px;
        }
         
        .ms{
            font-weight: 600 !important;;
        }
          
        div.entry_content{
        column-count: auto;
        column-width: 270px;
        column-gap: 2em;
        column-rule: 1px dashed black;
            -moz-column-count: auto;
            -moz-column-width: 270px;
            -moz-column-gap: 2em;
            -moz-column-rule: 1px dashed black;
            -webkit-column-count: auto;
            -webkit-column-width:270px;
            -webkit-column-gap: 2em;
            -webkit-column-rule: 1px dashed black;
        text-align: justify;
        }

        body{
        line-height: 0px !important;
        padding-top: 0px !important;
        }

        .one-sidebar-right #column-main .mcolumn-pad, 
        .one-sidebar-right #container #content, 
        .two-sidebar-right #column-main .mcolumn-pad, 
        .two-sidebar-right #container #content {
            padding: 0px !important;
            margin-right: 1cm !important;
        }

        #page-main .content {
            border:  0px !important;
        }

        p.ptext, 
        div.clear, 
        div.tags, 
        div#pagination, 
        div#content_sidebar, 
        div#pl_comments, 
        div#content_sidebar, 
        div#sharebar, 
        div#highlight, 
        div#breadcrumb, 
        div#sidebar-wrap, 
        div#footer, 
        div#header, 
        div#morefoot_area, 
        div#fullwidth_sidebar {
            display: none !important;
        }
        
        .post-thumb{
        height: 30px !important;
        }
        
        img.attachment-thumbnail{
        float: left !important;
        }
        
        #column-main, 
        .mcolumn-pad, 
        .copy-pad, 
        column-main{
        margin-right: 1cm  !important;
        padding-right: 1cm !important;
        }
        
        #site {
        min-width: 100% !important;
        margin: -1px !important;
        }

    }

}
